/**
 * Load test for publish-upgrade route.
 * Usage:
 *   API_BASE_URL=http://localhost:8788 UPGRADE_TOKEN=token k6 run publish-upgrade.k6.js
 */
import http from 'k6/http';
import { check, sleep } from 'k6';
import { SharedArray } from 'k6/data';

export const options = {
  scenarios: {
    upgrade: {
      executor: 'constant-vus',
      vus: 5,
      duration: '30s',
    },
  },
  thresholds: {
    http_req_duration: ['p(95)<5000'],
    http_req_failed: ['rate<0.01'],
  },
};

const shops = new SharedArray('shops', () => [
  'alpha',
  'beta',
  'gamma',
  'delta',
  'epsilon',
]);

export default function () {
  const id = shops[(__VU - 1) % shops.length];
  const url = `${__ENV.API_BASE_URL}/shop/${id}/publish-upgrade`;
  const payload = JSON.stringify({ components: [] });
  const params = {
    headers: {
      Authorization: `Bearer ${__ENV.UPGRADE_TOKEN}`,
      'Content-Type': 'application/json',
    },
  };

  const res = http.post(url, payload, params);
  check(res, {
    'status 200': (r) => r.status === 200,
    'ok true': (r) => r.json('ok') === true,
  });

  const fileRes = http.get(`${__ENV.API_BASE_URL}/data/shops/${id}/shop.json`);
  check(fileRes, {
    'shop.json valid': (r) => r.status === 200 && r.json('lastUpgrade') !== undefined,
  });

  sleep(1);
}

/// <reference types="@cloudflare/workers-types" />
// apps/api/src/routes/components/[shopId].ts

import { existsSync, readFileSync, readdirSync } from "fs";
import path from "path";
import jwt from "jsonwebtoken";
import { validateShopName } from "@acme/lib";
import { logger } from "@acme/shared-utils";

interface ComponentChange {
  name: string;
  from: string | null;
  to: string;
  summary?: string;
  changelog?: string;
}

function readJson<T = unknown>(file: string): T {
  return JSON.parse(readFileSync(file, "utf8")) as T;
}

function extractSummary(log: string): string {
  for (const line of log.split("\n")) {
    const trimmed = line.trim();
    if (!trimmed || trimmed.startsWith("#")) continue;
    return trimmed;
  }
  return "";
}

function gatherChanges(shopId: string, root: string): ComponentChange[] {
  const shopJson = path.join(root, "data", "shops", shopId, "shop.json");
  let stored: Record<string, string> = {};
  if (existsSync(shopJson)) {
    try {
      const shopData = readJson<{
        componentVersions?: Record<string, string>;
      }>(shopJson);
      stored = shopData.componentVersions ?? {};
    } catch {
      stored = {};
    }
  }
  const packagesDir = path.join(root, "packages");
  const changes: ComponentChange[] = [];
  for (const [pkgName, oldVersion] of Object.entries(stored)) {
    const dir = path.join(packagesDir, pkgName.replace(/^@[^/]+\//, ""));
    const pkgJson = path.join(dir, "package.json");
    if (!existsSync(pkgJson)) continue;
    const pkg = readJson<{ name?: string; version?: string }>(pkgJson);
    const latest = pkg.version;
    if (!latest || latest === oldVersion) continue;
    const changelogPath = path.join(dir, "CHANGELOG.md");
    let summary = "";
    let changelog = "";
    if (existsSync(changelogPath)) {
      summary = extractSummary(readFileSync(changelogPath, "utf8"));
      changelog = path.relative(root, changelogPath);
    }
    changes.push({
      name: pkg.name || pkgName,
      from: oldVersion ?? null,
      to: latest,
      summary,
      changelog,
    });
  }
  return changes;
}

function listFiles(dir: string, base = ""): string[] {
  if (!existsSync(dir)) return [];
  const files: string[] = [];
  for (const entry of readdirSync(dir, { withFileTypes: true })) {
    const rel = path.join(base, entry.name);
    if (entry.isDirectory()) {
      files.push(...listFiles(path.join(dir, entry.name), rel));
    } else {
      files.push(rel);
    }
  }
  return files;
}

function diffDirectories(a: string, b: string): string[] {
  const files = new Set([...listFiles(a), ...listFiles(b)]);
  const changed: string[] = [];
  for (const file of files) {
    const fileA = path.join(a, file);
    const fileB = path.join(b, file);
    const hasA = existsSync(fileA);
    const hasB = existsSync(fileB);
    if (!hasA || !hasB) {
      changed.push(file);
      continue;
    }
    const bufA = readFileSync(fileA);
    const bufB = readFileSync(fileB);
    if (!bufA.equals(bufB)) changed.push(file);
  }
  return changed;
}

export const onRequest = async ({
  params,
  request,
}: {
  params: Record<string, string>;
  request: Request;
}) => {
  let shopId: string;
  try {
    shopId = validateShopName(params.shopId);
  } catch {
    logger.warn("invalid shop id", { id: params.shopId });
    return new Response(JSON.stringify({ error: "Invalid shop id" }), {
      status: 400,
      headers: { "Content-Type": "application/json" },
    });
  }

  const authHeader = request.headers.get("authorization") || "";
  if (!authHeader.startsWith("Bearer ")) {
    logger.warn("missing bearer token", { shopId });
    return new Response(JSON.stringify({ error: "Forbidden" }), {
      status: 403,
      headers: { "Content-Type": "application/json" },
    });
  }

  const token = authHeader.slice("Bearer ".length);
  const secret = process.env.UPGRADE_PREVIEW_TOKEN_SECRET;
  if (!secret) {
    logger.warn("invalid token", { shopId });
    return new Response(JSON.stringify({ error: "Forbidden" }), {
      status: 403,
      headers: { "Content-Type": "application/json" },
    });
  }
  try {
    const payload = jwt.verify(
      token,
      secret,
      {
        algorithms: ["HS256"],
        audience: "upgrade-preview",
        issuer: "acme",
        subject: `shop:${shopId}:upgrade-preview`,
      },
    ) as jwt.JwtPayload;
    if (typeof payload.exp !== "number") {
      throw new Error("missing exp");
    }
  } catch {
    logger.warn("invalid token", { shopId });
    return new Response(JSON.stringify({ error: "Forbidden" }), {
      status: 403,
      headers: { "Content-Type": "application/json" },
    });
  }

  const root = path.resolve(__dirname, "../../../../../..");
  const components = gatherChanges(shopId, root);

  const includeDiff = new URL(request.url).searchParams.has("diff");
  if (!includeDiff) {
    return new Response(JSON.stringify({ components }), {
      headers: { "Content-Type": "application/json" },
    });
  }

  const appDir = path.join(root, "apps", `shop-${shopId}`);
  const templateDir = path.join(root, "packages", "template-app");
  const configDiff = {
    templates: diffDirectories(
      path.join(appDir, "src", "templates"),
      path.join(templateDir, "src", "templates"),
    ),
    translations: diffDirectories(
      path.join(appDir, "src", "translations"),
      path.join(templateDir, "src", "translations"),
    ),
  };
  return new Response(JSON.stringify({ components, configDiff }), {
    headers: { "Content-Type": "application/json" },
  });
};

export { extractSummary, gatherChanges, diffDirectories, listFiles };

/* istanbul ignore file */
/* eslint-disable @typescript-eslint/no-var-requires */
jest.mock('fs', () => require('memfs').fs);
jest.mock('jsonwebtoken', () => ({ verify: jest.fn() }));
jest.mock('@acme/lib', () => ({ validateShopName: jest.fn((s: string) => s) }));

import path from 'path';
import { vol } from 'memfs';
import jwt from 'jsonwebtoken';
import { validateShopName } from '@acme/lib';
import { onRequest } from '../[shopId]';
import { logger } from '@acme/shared-utils';

export const verify = jwt.verify as jest.Mock;
export const validate = validateShopName as jest.Mock;
export { onRequest, vol };

export const root = path.resolve(__dirname, '../../../../../../..');

export function setup() {
  vol.reset();
  verify.mockReset();
  validate.mockReset().mockImplementation((s: string) => s);
  delete process.env.UPGRADE_PREVIEW_TOKEN_SECRET;
  return jest.spyOn(logger, 'warn').mockImplementation(() => {});
}

export function createRequest({
  shopId,
  token,
  url = 'http://localhost',
  headers = {},
}: {
  shopId?: string;
  token?: string;
  url?: string;
  headers?: Record<string, string>;
} = {}) {
  const params = shopId === undefined ? ({} as any) : { shopId };
  if (token === '') {
    return {
      params,
      request: {
        headers: { get: () => 'Bearer ' } as any,
        url,
      } as any,
    };
  }
  const allHeaders: Record<string, string> = { ...headers };
  if (token !== undefined) {
    allHeaders.authorization = `Bearer ${token}`;
  }
  return {
    params,
    request: new Request(url, { headers: allHeaders }),
  } as any;
}

import { readFileSync, writeFileSync } from "fs";
import path from "path";
import { spawn } from "child_process";
import jwt from "jsonwebtoken";
import { logger } from "@acme/shared-utils";

export function run(cmd: string, args: string[], cwd: string): Promise<void> {
  return new Promise((resolve, reject) => {
    const proc = spawn(cmd, args, { cwd, stdio: "inherit" });
    // Register the close handler before the error handler. Some tests stub the
    // `on` method to immediately invoke the provided callback regardless of the
    // event name. If the error handler runs first in that scenario the promise
    // would reject even though the process "succeeded". By attaching the close
    // handler first we ensure the promise resolves on a successful exit and any
    // subsequent calls to reject are ignored.
    proc.on("close", (code) => {
      if (code === 0) resolve();
      else reject(new Error(`${cmd} ${args.join(" ")} failed with status ${code}`));
    });
    proc.on("error", (err) => reject(err));
  });
}

export const onRequestPost = async ({
  params,
  request,
}: {
  params: Record<string, string>;
  request: Request;
}) => {
  try {
    const id = params.id;
    if (!id || !/^[a-z0-9_-]+$/.test(id)) {
      logger.warn("invalid shop id", { id });
      return new Response(JSON.stringify({ error: "Invalid shop id" }), {
        status: 400,
        headers: { "Content-Type": "application/json" },
      });
    }

    const authHeader = request.headers.get("authorization") || "";
    if (!authHeader.startsWith("Bearer ")) {
      logger.warn("missing bearer token", { id });
      return new Response(JSON.stringify({ error: "Unauthorized" }), {
        status: 401,
        headers: { "Content-Type": "application/json" },
      });
    }

    const token = authHeader.slice("Bearer ".length);
    const secret = process.env.UPGRADE_PREVIEW_TOKEN_SECRET;
    if (!secret) {
      logger.warn("invalid token", { id });
      return new Response(JSON.stringify({ error: "Forbidden" }), {
        status: 403,
        headers: { "Content-Type": "application/json" },
      });
    }
    try {
      jwt.verify(token, secret);
    } catch {
      logger.warn("invalid token", { id });
      return new Response(JSON.stringify({ error: "Forbidden" }), {
        status: 403,
        headers: { "Content-Type": "application/json" },
      });
    }

    const root = path.resolve(__dirname, "../../../../../..");
    const appDir = path.join(root, "apps", `shop-${id}`);
    const shopFile = path.join(root, "data", "shops", id, "shop.json");
    const pkgFile = path.join(appDir, "package.json");

    const body = (await request.json().catch(() => ({}))) as {
      components?: unknown;
    };
    const selected: string[] = Array.isArray(body.components)
      ? (body.components as string[])
      : [];

    const deps = JSON.parse(readFileSync(pkgFile, "utf8")).dependencies ?? {};
    const shop = JSON.parse(readFileSync(shopFile, "utf8"));
    shop.componentVersions = shop.componentVersions || {};

    const toLock = selected.length > 0 ? selected : Object.keys(deps);
    for (const name of toLock) {
      if (deps[name]) {
        shop.componentVersions[name] = deps[name];
      }
    }
    shop.lastUpgrade = new Date().toISOString();
    writeFileSync(shopFile, JSON.stringify(shop, null, 2));

    if (Object.keys(deps).length === 0) {
      return new Response(JSON.stringify({ ok: true }), {
        headers: { "Content-Type": "application/json" },
      });
    }

    await run("pnpm", ["--filter", `apps/shop-${id}`, "build"], root);
    await run("pnpm", ["--filter", `apps/shop-${id}`, "deploy"], root);

    return new Response(JSON.stringify({ ok: true }), {
      headers: { "Content-Type": "application/json" },
    });
  } catch (err) {
    const message = err instanceof Error ? err.message : String(err);
    return new Response(JSON.stringify({ error: message }), {
      status: 500,
      headers: { "Content-Type": "application/json" },
    });
  }
};

// Allow disabling MSW for suites that don't need it or to work around
// environment transform issues. When DISABLE_MSW=1, this setup becomes a no-op.
let rest: any;

if (process.env.DISABLE_MSW !== "1") {
  // eslint-disable-next-line @typescript-eslint/no-var-requires
  const shared = require("~test/msw/server");
  // eslint-disable-next-line @typescript-eslint/no-var-requires
  const { handlers } = require("./handlers");
  const server = shared.server;
  rest = shared.rest;

  // Extend the shared global server with CMS‑specific handlers for each test.
  // The workspace's global jest.setup.ts manages start/stop and reset, so we
  // simply re‑apply our app‑specific handlers before every test to ensure they
  // persist across resets between tests.
  beforeEach(() => server.use(...handlers));
}

export { rest };

// Lighthouse smoke for authenticated routes with explicit device config
describe('Lighthouse smoke (authenticated)', { tags: ['smoke', 'lh'] }, () => {
  const thresholdsMobile = {
    performance: 60,
    'largest-contentful-paint': 4000,
    'total-blocking-time': 300,
    'cumulative-layout-shift': 0.1,
  } as const;

  const thresholdsDesktop = {
    performance: 90,
    'largest-contentful-paint': 2500,
    'total-blocking-time': 200,
    'cumulative-layout-shift': 0.1,
  } as const;

  beforeEach(() => {
    // Sessionized login to ensure we audit real pages not /login
    cy.session('login', () => {
      cy.visit('/login');
      const user = Cypress.env('E2E_USERNAME');
      const pass = Cypress.env('E2E_PASSWORD');
      if (!user || !pass) {
        // eslint-disable-next-line no-console
        console.warn('E2E_USERNAME/E2E_PASSWORD not set; skipping auth.');
        return;
      }
      cy.get('#username').type(user);
      cy.get('#password').type(pass, { log: false });
      cy.get('button[type="submit"]').click();
      cy.url().should('include', '/dashboard');
    });
  });

  it('mobile dashboard', () => {
    cy.visit('/dashboard');
    cy.lighthouse(
      thresholdsMobile,
      {},
      { settings: { preset: 'mobile', throttlingMethod: 'simulate' } }
    );
  });

  it('desktop dashboard', () => {
    cy.visit('/dashboard');
    cy.lighthouse(
      thresholdsDesktop,
      {},
      { settings: { preset: 'desktop', throttlingMethod: 'simulate' } }
    );
  });
});

// apps/cms/src/app/[lang]/shop/ShopClient.tsx
"use client";

import FilterBar, {
  type Filters,
  type FilterDefinition,
} from "@platform-core/components/shop/FilterBar";
import { ProductGrid } from "@platform-core/components/shop/ProductGrid";
import type { SKU } from "@acme/types";
import { useEffect, useMemo, useRef, useState } from "react";
import { useTranslations } from "@i18n/Translations";
import { usePathname, useRouter, useSearchParams } from "next/navigation";

export default function ShopClient({ skus }: { skus: SKU[] }) {
  const t = useTranslations();
  // Extract to avoid eslint false positive from key substring 'ariaLabel' matching 'arial'
  const searchAriaLabel = String(t("shop.searchAriaLabel")); // eslint-disable-line ds/no-raw-font -- INTL-000 false positive: 'ariaLabel' substring [ttl=2026-03-31]
  const searchParams = useSearchParams();
  const router = useRouter();
  const pathname = usePathname();
  const [query, setQuery] = useState(() => searchParams.get("q") ?? "");
  const [filters, setFilters] = useState<Filters>(() => {
    const init: Filters = {};
    const size = searchParams.get("size");
    const color = searchParams.get("color");
    const maxPrice = searchParams.get("maxPrice");
    if (size) init.size = size;
    if (color) init.color = color;
    if (maxPrice) {
      const n = Number(maxPrice);
      if (!Number.isNaN(n)) init.maxPrice = n;
    }
    return init;
  });
  const synced = useRef(false);
  const sizes = useMemo(
    () => Array.from(new Set(skus.flatMap((p) => p.sizes))).sort(),
    [skus]
  );
  const colors = useMemo(
    () => Array.from(new Set(skus.map((p) => p.slug.split("-")[0]))).sort(),
    [skus]
  );
  const defs: FilterDefinition[] = [
    { name: "size", label: "Size", type: "select", options: sizes },
    { name: "color", label: "Color", type: "select", options: colors },
    { name: "maxPrice", label: "Max Price", type: "number" },
  ];

  const visible = useMemo(() => {
    return skus.filter((p) => {
      if (query && !p.title.toLowerCase().includes(query.toLowerCase())) {
        return false;
      }
      if (filters.size && !p.sizes.includes(filters.size as string)) return false;
      if (filters.color && !p.slug.startsWith(filters.color as string)) return false;
      if (typeof filters.maxPrice === "number" && p.price > filters.maxPrice) {
        return false;
      }
      return true;
    });
  }, [filters, query, skus]);

  useEffect(() => {
    if (!synced.current) {
      synced.current = true;
      return;
    }
    const params = new URLSearchParams();
    if (query) params.set("q", query);
    Object.entries(filters).forEach(([k, v]) => {
      if (v !== undefined && v !== "") params.set(k, String(v));
    });
    const search = params.toString();
    router.push(`${pathname}${search ? `?${search}` : ""}`);
  }, [filters, pathname, query, router]);

  return (
    <div className="px-4 py-10">
      <h1 className="mb-4 text-3xl font-bold">{t("nav.shop")}</h1>
      <input
        aria-label={searchAriaLabel}
        type="search"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder={t("shop.searchPlaceholder") as string}
        className="mb-4 w-full border rounded px-2 py-1 sm:w-64"
      />
      <FilterBar
        definitions={defs}
        values={filters}
        onChange={setFilters}
      />
      <ProductGrid skus={visible} />
    </div>
  );
}

import { render, screen, fireEvent } from "@testing-library/react";
import RichTextEditor from "@cms/app/cms/blog/posts/RichTextEditor";
import type { PortableTextBlock } from "@cms/app/cms/blog/posts/schema";
import { PortableTextEditor } from "@portabletext/editor";

const translations: Record<string, string> = {
  "cms.blog.editor.loadFailedProducts": "Failed to load products",
};
const translate = (key: string) => translations[key] ?? key;

jest.mock("@ui/components/atoms", () => ({
  Button: ({ children, ...props }: any) => (
    <button {...props}>{children}</button>
  ),
  Input: ({ label, ...props }: any) => (
    <label>
      <span>{label}</span>
      <input {...props} />
    </label>
  ),
}));
jest.mock("@ui/components/cms/page-builder", () => ({
  ImagePicker: ({ children }: any) => <div>{children}</div>,
}));
jest.mock("next/image", () => ({
  __esModule: true,
  default: (props: any) => <img {...props} />,
}));
jest.mock("@acme/i18n", () => ({
  useTranslations: () => translate,
}));
jest.mock("@acme/shared-utils", () => ({
  formatCurrency: (value: number) => `$${value}`,
}));
jest.mock("@portabletext/editor", () => ({
  defineSchema: (x: any) => x,
  EditorProvider: ({ children }: any) => <div>{children}</div>,
  PortableTextEditable: () => <div />,
  PortableTextEditor: {
    insertBlock: jest.fn(),
    toggleMark: jest.fn(),
    toggleBlockStyle: jest.fn(),
    addAnnotation: jest.fn(),
    removeAnnotation: jest.fn(),
    isAnnotationActive: jest.fn(),
  },
  useEditor: () => ({}),
  usePortableTextEditor: () => ({}),
}));

let captured: any;
jest.mock("@portabletext/editor/plugins", () => ({
  EventListenerPlugin: ({ on }: any) => {
    captured = on;
    return null;
  },
}));

describe("RichTextEditor", () => {
  afterEach(() => {
    jest.restoreAllMocks();
  });

  it("invokes onChange on mutation", () => {
    const onChange = jest.fn();
    const initial: PortableTextBlock[] = [];
    render(<RichTextEditor value={initial} onChange={onChange} />);
    captured({ type: "mutation", value: [{ _type: "block", _key: "a" }] });
    expect(onChange).toHaveBeenCalledWith([{ _type: "block", _key: "a" }]);
  });

  it("renders product matches and inserts reference block", async () => {
    jest
      .spyOn(global, "fetch")
      .mockResolvedValue({
        ok: true,
        json: async () => [
          {
            slug: "p1",
            title: "Product 1",
            price: 1000,
            media: [{ type: "image", url: "/i.png" }],
          },
        ],
      } as any);

    const { unmount } = render(<RichTextEditor value={[]} onChange={jest.fn()} />);

    const input = screen.getByRole("textbox");
    fireEvent.change(input, { target: { value: "prod" } });

    expect(await screen.findByText("Product 1")).toBeInTheDocument();

    fireEvent.click(screen.getByRole("button", { name: /Product 1/ }));
    expect(PortableTextEditor.insertBlock).toHaveBeenCalledWith(
      expect.anything(),
      { name: "productReference" },
      { slug: "p1" }
    );

    unmount();
  });

  it("shows error when product search fails", async () => {
    jest.spyOn(global, "fetch").mockRejectedValue(new Error("fail"));

    const { unmount } = render(<RichTextEditor value={[]} onChange={jest.fn()} />);

    const input = screen.getByRole("textbox");
    fireEvent.change(input, { target: { value: "prod" } });

    expect(
      await screen.findByText("Failed to load products")
    ).toBeInTheDocument();

    unmount();
  });
});

"use client";

import { useCallback, useMemo, useState } from "react";
import type { PageComponent } from "@acme/types";
import { ulid } from "ulid";
import { ComponentEditor } from "@ui/components/cms/page-builder";
import { useTranslations } from "@acme/i18n";
import { saveLibraryStrict } from "@ui/components/cms/page-builder/libraryStore";
import { validateComponentRules } from "@acme/platform-core/validation/componentRules";

export default function ComponentEditorClient() {
  const t = useTranslations();
  const initial = useMemo<PageComponent>(
    () => ({ id: ulid(), type: "Text", text: String(t("cms.componentEditor.initialText")) }),
    [t]
  );
  const [component, setComponent] = useState<PageComponent>(initial);
  const [label, setLabel] = useState<string>("");
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState<string>("");
  const [isError, setIsError] = useState(false);
  const [issues, setIssues] = useState<Array<{ path: Array<string | number>; message: string }>>([]);
  // Extract to avoid eslint false positive from key substring 'ariaLabel' matching 'arial'
  const nameAriaLabel = String(t("cms.componentEditor.name.ariaLabel")); // eslint-disable-line ds/no-raw-font -- false positive: 'ariaLabel' substring triggers rule (ABC-123 [ttl=2025-12-31])

  const onChange = useCallback((patch: Partial<PageComponent>) => {
    setComponent((prev: PageComponent) => ({ ...prev, ...patch } as PageComponent));
  }, []);

  const onResize = useCallback((patch: Partial<PageComponent>) => {
    setComponent((prev: PageComponent) => ({ ...prev, ...patch } as PageComponent));
  }, []);

  return (
    <div className="space-y-4">
      <ComponentEditor component={component} onChange={onChange} onResize={onResize} issues={issues} />
      <div className="rounded border p-3 space-y-2">
        <div className="font-semibold">{t("cms.componentEditor.saveToGlobalLibrary.title")}</div>
        <div className="flex items-center gap-2">
          <input
            type="text"
            className="min-w-0 flex-1 rounded border px-2 py-1 text-sm min-h-10"
            placeholder={String(t("cms.componentEditor.name.placeholder"))}
            value={label}
            onChange={(e) => setLabel(e.target.value)}
            aria-label={nameAriaLabel}
          />
          <button
            type="button"
            className="rounded border px-3 py-1 text-sm min-h-11 min-w-11"
            disabled={saving || !label.trim()}
            onClick={async () => {
              setSaving(true);
              setMessage("");
              setIsError(false);
              setIssues([]);
              try {
                // Client-side preflight validation for faster feedback
                const pre = validateComponentRules([component]);
                if (pre.ok === false) {
                  setIsError(true);
                  setIssues(pre.issues ?? []);
                  setMessage(pre.errors.join("\n"));
                  return;
                }
                const item = { id: ulid(), label: label.trim(), template: component, createdAt: Date.now() };
                await saveLibraryStrict("_global", item);
                setMessage(String(t("cms.componentEditor.savedToGlobal")));
                setIsError(false);
                setIssues([]);
              } catch (err) {
                console.error(err);
                const msg = (err as Error)?.message || String(t("cms.componentEditor.saveFailed"));
                setMessage(msg);
                setIsError(true);
                type ValidationIssue = { path: Array<string | number>; message: string };
                type ApiError = Error & { data?: { error?: string; issues?: ValidationIssue[] } };
                const data = (err as ApiError)?.data;
                if (data && Array.isArray(data.issues)) setIssues(data.issues);
              } finally {
                setSaving(false);
              }
            }}
          >
            {saving ? t("actions.saving") : t("cms.componentEditor.button.saveToGlobal")}
          </button>
        </div>
        {message && (
          <div
            className={
              isError
                ? "text-xs rounded border border-red-300 bg-red-50 text-red-700 px-2 py-1"
                : "text-xs text-muted-foreground"
            }
            role={isError ? "alert" : undefined}
            aria-live={isError ? "assertive" : "polite"}
          >
            {message}
          </div>
        )}
      </div>
      <div className="rounded border p-3 text-xs">
        <div className="mb-1 font-semibold">{t("cms.componentEditor.componentJson")}</div>
        <pre className="whitespace-pre-wrap break-all text-xs leading-snug">
          {JSON.stringify(component, null, 2)}
        </pre>
      </div>
    </div>
  );
}

