import { PortableText } from "@portabletext/react";
import type {
  PortableTextComponents,
  PortableTextMarkComponentProps,
  PortableTextTypeComponentProps,
  PortableTextComponentProps,
} from "@portabletext/react";
import Link from "next/link";
import { getProductBySlug, getProductById, type SKU } from "../../products";
import { ProductCard } from "../shop/ProductCard";

const components: PortableTextComponents = {
  types: {
    productReference: ({
      value,
    }: PortableTextTypeComponentProps<{
      ids?: string[];
      slugs?: string[];
      id?: string;
      slug?: string;
    }>) => {
      const ids: string[] = Array.isArray(value?.ids)
        ? value.ids
        : Array.isArray(value?.slugs)
          ? value.slugs
          : typeof value?.id === "string"
            ? [value.id]
            : typeof value?.slug === "string"
              ? [value.slug]
              : [];
      const products: SKU[] = ids
        .map((id) => getProductById(id) ?? getProductBySlug(id))
        .filter((p): p is SKU => Boolean(p));
      if (products.length === 0) return null;
      if (products.length === 1) {
        const p = products[0]!;
        return (
          <Link href={`../product/${p.slug}`} className="underline">
            {p.title}
          </Link>
        );
      }
      return (
        <>
          {/* eslint-disable-next-line ds/enforce-layout-primitives -- ABC-123 horizontal scroller with mapped items */}
          <div className="inline-flex gap-4 overflow-x-auto py-4">
          {products.map((p) => (
            <ProductCard key={p.id} sku={p} />
          ))}
          </div>
        </>
      );
    },
    embed: ({ value }: PortableTextTypeComponentProps<{ url: string }>) => (
      <div className="aspect-video">
        <iframe src={value.url} className="h-full w-full" />
      </div>
    ),
  },
  marks: {
    link: ({ children, value }: PortableTextMarkComponentProps) => {
      const href = (value as { href?: string })?.href ?? "";
      return (
        <a
          href={href}
          className="text-blue-600 underline inline-flex min-h-10 min-w-10"
          target="_blank"
          rel="noopener noreferrer"
        >
          {children}
        </a>
      );
    },
    em: ({ children }: PortableTextMarkComponentProps) => <em>{children}</em>,
  },
  block: {
    h1: ({ children }: PortableTextComponentProps<unknown>) => <h1>{children}</h1>,
    h2: ({ children }: PortableTextComponentProps<unknown>) => <h2>{children}</h2>,
    h3: ({ children }: PortableTextComponentProps<unknown>) => <h3>{children}</h3>,
  },
};

type PortableBlock = { _type: string; [key: string]: unknown };

export function BlogPortableText({ value }: { value: PortableBlock[] }) {
  return <PortableText value={value} components={components} />;
}

// packages/platform-core/components/shop/AddToCartButton.tsx
"use client";

import { useCart } from "../../contexts/CartContext";
import type { SKU } from "@acme/types";
import { useEffect, useRef, useState } from "react";
import { flushSync } from "react-dom";

type Props = {
  sku: SKU;
  /** Optional selected size */
  size?: string;
  /** Disable button until prerequisites are met (e.g. size chosen) */
  disabled?: boolean;
  /** Number of items to add */
  quantity?: number;
  /** Optional metadata to tag the line item (e.g., try-on) */
  meta?: { source?: string; tryOn?: { idempotencyKey?: string; transform?: Record<string, unknown> } };
};

export default function AddToCartButton({
  sku,
  size,
  disabled = false,
  quantity = 1,
  meta,
}: Props) {
  const [, dispatch] = useCart();
  const btnRef = useRef<HTMLButtonElement | null>(null);
  const [adding, setAdding] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [forceDisabled, setForceDisabled] = useState(false);

  // Keep the actual DOM disabled state in sync to satisfy testing-library's
  // toBeDisabled matcher under JSDOM consistently when timers are mocked.
  useEffect(() => {
    const el = btnRef.current;
    if (!el) return;
    const off = !!disabled;
    const isDisabled = forceDisabled || adding || off;
    el.disabled = isDisabled;
    try {
      if (isDisabled) el.setAttribute("disabled", "");
      else el.removeAttribute("disabled");
    } catch {
      // ignore
    }
  }, [adding, forceDisabled, disabled]);

  async function handleClick(_e: React.MouseEvent<HTMLButtonElement>) {
    if (disabled) return;
    if (quantity < 1) {
      setError("Quantity must be at least 1"); // i18n-exempt -- ABC-123 validation message
      return;
    }
    // Snapshot the target to survive React event pooling
    // Synchronously mark disabled before any async work to avoid flake
    flushSync(() => {
      setForceDisabled(true);
    });
    flushSync(() => {
      setAdding(true);
      setError(null);
    });

    try {
      await dispatch({ type: "add", sku, size, qty: quantity, meta });
    } catch (err) {
      setError((err as Error).message ?? "Unable to add to cart"); // i18n-exempt -- ABC-123 fallback error message
    } finally {
      flushSync(() => {
        setAdding(false);
      });
      flushSync(() => {
        setAdding(false);
        setForceDisabled(false);
      });
    }
  }

  return (
    <>
      <fieldset
        disabled={forceDisabled || adding || disabled}
        className="border-0 m-0 p-0"
      >
        <button
          ref={btnRef}
          onClick={handleClick}
          disabled={forceDisabled || adding || disabled}
          aria-disabled={forceDisabled || adding || disabled ? true : undefined}
          aria-label="Add to cart"
          className="mt-auto rounded bg-gray-900 px-4 py-2 text-sm text-white hover:bg-gray-800 disabled:opacity-50 min-h-10 min-w-10"
        >
          {adding ? (
            <span className="inline-flex items-center gap-2" aria-live="polite">
              <svg className="h-4 w-4 animate-spin" viewBox="0 0 24 24" aria-hidden="true">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v4a4 4 0 00-4 4H4z" />
              </svg>
              {"Adding..."} {/* i18n-exempt -- ABC-123 loading state text */}
            </span>
          ) : (
            // i18n-exempt -- ABC-123 button copy pending translation integration
            "Add to cart"
          )}
        </button>
      </fieldset>
      {error && (
        <p className="mt-2 text-sm text-red-600" role="alert">
          {error}
        </p>
      )}
    </>
  );
}

// src/components/shop/FilterBar.tsx
"use client";

import { useDeferredValue, useEffect, useState } from "react";

export type FilterDefinition =
  | { name: string; label: string; type: "select"; options: string[] }
  | { name: string; label: string; type: "number" };

export type Filters = Record<string, string | number | undefined>;

export interface FilterBarProps {
  definitions: FilterDefinition[];
  /**
   * Current values for the filters. When provided, the component becomes
   * controlled and will mirror any external updates. When omitted, an empty
   * object is assumed.
   */
  values?: Filters;
  onChange: (filters: Filters) => void;
}

export default function FilterBar({
  definitions,
  values: externalValues,
  onChange,
}: FilterBarProps) {
  const [values, setValues] = useState<Filters>(externalValues ?? {});
  const deferred = useDeferredValue(values);

  // Keep internal state in sync when parent updates its values (e.g. after a
  // reload where values are read from the URL).
  useEffect(() => {
    setValues(externalValues ?? {});
  }, [externalValues]);

  useEffect(() => {
    onChange(deferred);
  }, [deferred, onChange]);

  function handleChange(def: FilterDefinition, value: string) {
    setValues((prev) => ({
      ...prev,
      [def.name]:
        value === ""
          ? undefined
          : def.type === "number"
          ? Number(value)
          : value,
    }));
  }

  function handleClear() {
    setValues({});
  }

  return (
    <form
      aria-label="Filters"
      className="mb-6 flex gap-4 items-center justify-between flex-wrap"
      onSubmit={(e) => e.preventDefault()}
    >
      {definitions.map((def) =>
        def.type === "select" ? (
          <label key={def.name} className="flex items-center gap-2 text-sm">
            {def.label}:
            <select
              value={(values[def.name] as string) ?? ""}
              onChange={(e) => handleChange(def, e.target.value)}
              className="border rounded px-2 py-1 text-sm"
            >
              <option value="">All</option> {/* i18n-exempt -- ABC-123 select placeholder */}
              {def.options.map((opt) => (
                <option key={opt}>{opt}</option>
              ))}
            </select>
          </label>
        ) : (
          <label key={def.name} className="flex items-center gap-2 text-sm">
            {def.label}:
            <input
              type="number"
              value={
                values[def.name] === undefined
                  ? ""
                  : (values[def.name] as number)
              }
              onChange={(e) => handleChange(def, e.target.value)}
              className="border rounded px-2 py-1 text-sm"
            />
          </label>
        )
      )}
      <button
        type="button"
        onClick={handleClear}
        className="text-sm underline min-h-10 min-w-10"
      >
        {/* i18n-exempt -- ABC-123 control label */}
        Clear Filters
      </button>
    </form>
  );
}

// packages/template-app/src/app/[lang]/shop/ShopClient.client.tsx

"use client";

import type { SKU } from "@acme/types";
import { useTranslations } from "@acme/i18n";
import FilterBar, {
  type FilterDefinition,
  type Filters,
} from "@platform-core/components/shop/FilterBar";
import { ProductGrid } from "@platform-core/components/shop/ProductGrid";
import { usePathname, useRouter, useSearchParams } from "next/navigation";
import { useEffect, useMemo, useRef, useState } from "react";

export default function ShopClient({ skus }: { skus: SKU[] }) {
  const t = useTranslations();
  const searchParams = useSearchParams();
  const router = useRouter();
  const pathname = usePathname();

  // Safe initial state reading
  const [query, setQuery] = useState(() => searchParams?.get("q") ?? "");
  const [filters, setFilters] = useState<Filters>(() => {
    const init: Filters = {};
    const size = searchParams?.get("size");
    const color = searchParams?.get("color");
    const maxPrice = searchParams?.get("maxPrice");
    if (size) init.size = size;
    if (color) init.color = color;
    if (maxPrice) {
      const n = Number(maxPrice);
      if (!Number.isNaN(n)) init.maxPrice = n;
    }
    return init;
  });

  const synced = useRef(false);
  const sizes = useMemo(
    () => Array.from(new Set(skus.flatMap((p) => p.sizes))).sort(),
    [skus]
  );
  const colors = useMemo(
    () => Array.from(new Set(skus.map((p) => p.slug.split("-")[0]))).sort(),
    [skus]
  );
  const defs: FilterDefinition[] = [
    { name: "size", label: "Size", type: "select", options: sizes },
    { name: "color", label: "Color", type: "select", options: colors },
    { name: "maxPrice", label: "Max Price", type: "number" },
  ];

  const visible = useMemo(() => {
    return skus.filter((p) => {
      if (query && !p.title.toLowerCase().includes(query.toLowerCase())) {
        return false;
      }
      if (filters.size && !p.sizes.includes(filters.size as string))
        return false;
      if (filters.color && !p.slug.startsWith(filters.color as string))
        return false;
      if (typeof filters.maxPrice === "number" && p.price > filters.maxPrice) {
        return false;
      }
      return true;
    });
  }, [filters, query, skus]);

  useEffect(() => {
    if (!synced.current) {
      synced.current = true;
      return;
    }
    const params = new URLSearchParams();
    if (query) params.set("q", query);
    Object.entries(filters).forEach(([k, v]) => {
      if (v !== undefined && v !== "") params.set(k, String(v));
    });
    const search = params.toString();
    router.push(`${pathname}${search ? `?${search}` : ""}`);
  }, [filters, pathname, query, router]);

  return (
    <div className="container mx-auto px-4 py-10">
      <h1 className="mb-4 text-3xl font-bold">{t("nav.shop")}</h1>
      {/* eslint-disable-next-line ds/no-raw-font -- DS-1234: false positive; rule matches "aria-label" as "arial" */}
      <input aria-label={t("shop.searchAriaLabel") as string}
        type="search"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder={t("shop.searchPlaceholder") as string}
        className="mb-4 w-full rounded border px-2 py-1"
      />
      <FilterBar definitions={defs} values={filters} onChange={setFilters} />
      <ProductGrid skus={visible} />
    </div>
  );
}

