import { resolveLocale, type Locale } from "@i18n/locales";
import { useTranslations as getServerTranslations } from "@i18n/useTranslations.server";
import { stripe } from "@acme/stripe";
import type Stripe from "stripe";
import { coreEnv } from "@acme/config/env/core";
import { readShop } from "@platform-core/repositories/shops.server";
import { getCustomerSession } from "@auth";
import { notFound } from "next/navigation";
import {
  setUserPlan,
} from "@platform-core/repositories/subscriptionUsage.server";
import { setStripeSubscriptionId } from "@platform-core/repositories/users";
import type { SubscriptionPlan } from "@acme/types";

export default async function SubscribePage({
  params,
}: {
  params: Promise<{ lang?: string }>;
}) {
  const { lang } = await params;
  const locale: Locale = resolveLocale(lang);
  const t = await getServerTranslations(locale);
  const shopId =
    ((coreEnv as { NEXT_PUBLIC_SHOP_ID?: string }).NEXT_PUBLIC_SHOP_ID as
      | string
      | undefined) || "shop";
  const shop = await readShop(shopId);
  if (!shop.subscriptionsEnabled) return notFound();

  async function selectPlan(formData: FormData) {
    "use server";
    const planId = formData.get("plan") as string;
    if (!planId) return;
    const session = await getCustomerSession();
    if (!session?.customerId) return;
    const priceId = planId;
    const params: Stripe.SubscriptionCreateParams & { prorate?: boolean } = {
      customer: session.customerId,
      items: [{ price: priceId }],
      prorate: true,
      metadata: { userId: session.customerId, shop: shopId },
    };
    const sub = await stripe.subscriptions.create(params);
    await setStripeSubscriptionId(session.customerId, sub.id, shopId);
    await setUserPlan(shopId, session.customerId, planId);
  }

  return (
    <div className="container mx-auto flex flex-col gap-6 p-6">
      <h1 className="text-2xl font-bold">{t("subscribe.title")}</h1>
      <form action={selectPlan} className="grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
        {shop.rentalSubscriptions.map((p: SubscriptionPlan) => (
          <label
            key={p.id}
            className="flex cursor-pointer flex-col gap-2 rounded border p-4 hover:border-black"
          >
            {/* eslint-disable-next-line ds/min-tap-size -- UI-1423 label provides the 40px+ tap target; input is visually hidden */}
            <input type="radio" name="plan" value={p.id} className="sr-only" />
            <span className="text-lg font-semibold">{p.id}</span>
            {shop.subscriptionsEnabled && (
              <>
                <span>{t("subscribe.itemsIncluded", { count: p.itemsIncluded })}</span>
                <span>{t("subscribe.swapsPerMonth", { count: p.swapLimit })}</span>
                <span>{t("subscribe.shipmentsPerMonth", { count: p.shipmentsPerMonth })}</span>
              </>
            )}
          </label>
        ))}
        <button
          type="submit"
          className="mt-4 inline-flex min-h-10 min-w-10 items-center justify-center rounded bg-black px-4 text-white"
        >
          {t("subscribe.cta")}
        </button>
      </form>
    </div>
  );
}

"use client";

import * as React from "react";
import { useTranslations } from "@acme/i18n";

interface ReturnFormProps {
  bagType?: string;
  tracking?: boolean;
}

export default function ReturnForm({
  bagType,
  tracking: trackingEnabled,
}: ReturnFormProps) {
  const tRaw = useTranslations();
  const t = React.useCallback(
    (key: string, vars?: Record<string, string | number>) =>
      tRaw(`account.returns.form.${key}`, vars),
    [tRaw]
  );
  const videoRef = React.useRef<HTMLVideoElement>(null);
  const [sessionId, setSessionId] = React.useState("");
  const [labelUrl, setLabelUrl] = React.useState<string | null>(null);
  const [trackingNumber, setTrackingNumber] = React.useState<string | null>(null);
  const [error, setError] = React.useState<string | null>(null);

  React.useEffect(() => {
    let stream: MediaStream | null = null;
    let active = true;
    async function init() {
      if (!("BarcodeDetector" in window)) return;
      try {
        stream = await navigator.mediaDevices.getUserMedia({
          video: { facingMode: "environment" },
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
          await videoRef.current.play();
        }
        const detector = new window.BarcodeDetector({
          formats: ["qr_code"],
        });
        const scan = async () => {
          if (!active || !videoRef.current) return;
          try {
            const codes = await detector.detect(videoRef.current);
            if (codes.length > 0) {
              const code = codes[0].rawValue;
              setSessionId(code);
              active = false;
              stream?.getTracks().forEach((t) => t.stop());
              return;
            }
          } catch {
            /* noop */
          }
          requestAnimationFrame(scan);
        };
        requestAnimationFrame(scan);
      } catch {
        setError(String(t("cameraError")));
      }
    }
    init();
    return () => {
      active = false;
      stream?.getTracks().forEach((t) => t.stop());
    };
  }, [t]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLabelUrl(null);
    setTrackingNumber(null);
    try {
      const res = await fetch("/api/returns/mobile", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ sessionId }),
      });
      const data = await res.json();
      if (!res.ok) {
        setError(data.error || String(t("createReturnFailed")));
        return;
      }
      setLabelUrl(data.labelUrl ?? null);
      setTrackingNumber(data.tracking ?? null);
    } catch {
      setError(String(t("createReturnFailed")));
    }
  };

  return (
    <div className="space-y-4 p-6">
      <h1 className="text-xl font-semibold">{t("title")}</h1>
      {bagType ? <p>{t("reuseBag", { bagType })}</p> : null}
      <video ref={videoRef} className="w-full" data-aspect="16/9" />
      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          className="w-full border p-2"
          placeholder={String(t("sessionIdPlaceholder"))}
          value={sessionId}
          onChange={(e) => setSessionId(e.target.value)}
        />
        <button
          type="submit"
          className="bg-primary text-white px-4 min-h-10 min-w-10 inline-flex items-center justify-center"
        >
          {t("submit")}
        </button>
      </form>
      {labelUrl && trackingEnabled && (
        <p>
          <a
            href={labelUrl}
            className="text-blue-600 underline inline-flex items-center min-h-10 min-w-10"
            target="_blank"
            rel="noreferrer"
          >
            {t("printLabel")}
          </a>
          {trackingNumber ? (
            <span className="block">{t("tracking", { trackingNumber })}</span>
          ) : null}
        </p>
      )}
      {error && <p className="text-red-600">{error}</p>}
    </div>
  );
}

// packages/template-app/src/app/account/swaps/page.tsx
import {
  CART_COOKIE,
  decodeCartCookie,
  type CartState,
} from "@platform-core/cartCookie";
import {
  getCart,
  removeItem,
  incrementQty,
} from "@platform-core/cartStore";
import { getProductById } from "@platform-core/products";
import { cookies } from "next/headers";
import { getCustomerSession } from "@auth";
import { readShop } from "@platform-core/repositories/shops.server";
import { notFound } from "next/navigation";
import { coreEnv, type CoreEnv } from "@acme/config/env/core";
import type { SubscriptionPlan } from "@acme/types";
import { nowIso } from "@date-utils";
import { useTranslations as getServerTranslations } from "@i18n/useTranslations.server";
import {
  getUserPlan,
  getRemainingSwaps,
  incrementSwapCount,
} from "@platform-core/repositories/subscriptionUsage.server";

export default async function SwapPage() {
  const tBase = await getServerTranslations("en");
  const t = (key: string, vars?: Record<string, unknown>) => {
    const msg = tBase(`account.swaps.${key}`);
    if (!vars) return msg;
    return msg.replace(/\{(.*?)\}/g, (m, name) => (Object.prototype.hasOwnProperty.call(vars, name) ? String(vars[name]) : m));
  };
  const cookieStore = await cookies();
  const cartId = decodeCartCookie(cookieStore.get(CART_COOKIE)?.value);
  const cart: CartState =
    typeof cartId === "string" ? await getCart(cartId) : {};
  const session = await getCustomerSession();
  const shopId =
    (coreEnv as CoreEnv).NEXT_PUBLIC_SHOP_ID ?? "shop";
  const shop = await readShop(shopId);
  if (!shop.subscriptionsEnabled) return notFound();
  const planId = session?.customerId
    ? await getUserPlan(shopId, session.customerId)
    : undefined;
  const selectedPlan = planId
    ? shop.rentalSubscriptions.find(
        (p: SubscriptionPlan) => p.id === planId,
      )
    : undefined;
  const month = nowIso().slice(0, 7);
  const remainingSwaps =
    session?.customerId && selectedPlan
      ? await getRemainingSwaps(
          shopId,
          session.customerId,
          month,
          selectedPlan.swapLimit,
        )
      : 0;
  const canSwap = remainingSwaps > 0;

  async function swap(formData: FormData) {
    "use server";
    const oldSku = formData.get("old") as string;
    const newSku = formData.get("new") as string;
    const cookieStore = await cookies();
    const cartId = decodeCartCookie(cookieStore.get(CART_COOKIE)?.value);
    const session = await getCustomerSession();
    if (typeof cartId !== "string" || !session?.customerId) return;
    const shop = await readShop(shopId);
    if (!shop.subscriptionsEnabled) return;
    const planId = await getUserPlan(shopId, session.customerId);
    const selectedPlan = planId
      ? shop.rentalSubscriptions.find(
          (p: SubscriptionPlan) => p.id === planId,
        )
      : undefined;
    const month = nowIso().slice(0, 7);
    if (!selectedPlan) return;
    const remaining = await getRemainingSwaps(
      shopId,
      session.customerId,
      month,
      selectedPlan.swapLimit,
    );
    if (remaining <= 0) return;
    const sku = getProductById(newSku);
    if (!sku) return;
    await removeItem(cartId, oldSku);
    await incrementQty(cartId, sku, 1);
    await incrementSwapCount(shopId, session.customerId, month);
  }

  return (
    <div className="mx-auto p-6">
      <h1 className="mb-4 text-2xl font-bold">{t("title")}</h1>
      <p className="mb-4">{t("remaining", { count: remainingSwaps })}</p>
      {Object.entries(cart).map(([id, line]) => (
        <form key={id} action={swap} className="mb-3 flex gap-2">
          <span className="flex-1">{line.sku.title}</span>
          <input type="hidden" name="old" value={id} />
          <input
            type="text"
            name="new"
            placeholder={t("placeholderNewSku")}
            className="w-40 border p-1"
          />
          <button
            type="submit"
            disabled={!canSwap}
            className="rounded bg-black px-4 text-white disabled:opacity-50 inline-flex items-center justify-center min-h-10 min-w-10"
          >
            {t("action.swap")}
          </button>
        </form>
      ))}
    </div>
  );
}

