// packages/ui/src/components/account/MfaSetup.tsx
import { useEffect, useState } from "react";
import Image from "next/image";
import QRCode from "qrcode";
import { getCsrfToken } from "@acme/shared-utils";
import { useTranslations } from "@acme/i18n";

export default function MfaSetup() {
  const t = useTranslations();
  const [secret, setSecret] = useState<string | null>(null);
  const [otpauth, setOtpauth] = useState<string | null>(null);
  const [token, setToken] = useState("");
  const [status, setStatus] = useState<string | null>(null);
  const [qrCode, setQrCode] = useState<string | null>(null);

  // Fallback translator: if key is returned verbatim, use a readable default
  const tf = (key: string, fallback: string) => {
    const val = t(key);
    return val === key ? fallback : val;
  };

  const begin = async () => {
    const csrfToken = getCsrfToken();
    // i18n-exempt
    const res = await fetch("/api/mfa/enroll", {
      method: "POST",
      headers: { "x-csrf-token": csrfToken ?? "" },
    });
    if (res.ok) {
      const data = await res.json();
      setSecret(data.secret);
      setOtpauth(data.otpauth);
    }
  };

  const verify = async (e: React.FormEvent) => {
    e.preventDefault();
    const csrfToken = getCsrfToken();
    // i18n-exempt
    const res = await fetch("/api/mfa/verify", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-csrf-token": csrfToken ?? "",
      },
      body: JSON.stringify({ token }),
    });
    const data = await res.json();
    setStatus(
      data.verified
        ? tf("account.mfa.enabled", "MFA enabled")
        : tf("account.mfa.error.invalid", "Invalid code")
    );
  };

  useEffect(() => {
    if (!otpauth) return;
    QRCode.toDataURL(otpauth).then(setQrCode).catch(console.error);
  }, [otpauth]);

  return (
    <div className="space-y-4">
      {!secret && (
        <button
          type="button"
          onClick={begin}
          className="rounded bg-primary px-4 py-2 min-h-10 min-w-10"
          data-token="--color-primary" // i18n-exempt -- DS-1234 [ttl=2025-11-30] — DS token attribute
        >
          <span className="text-primary-fg" data-token="--color-primary-fg"> {/* i18n-exempt -- DS-1234 [ttl=2025-11-30] — DS token attribute */}
            {tf("account.mfa.generateSecret", "Generate Secret")}
          </span>
        </button>
      )}
      {secret && (
        <div>
          {qrCode && (
            <Image
              src={qrCode}
              alt={tf("account.mfa.qr.alt", "MFA QR Code")}
              className="mb-2"
              width={256}
              height={256}
              unoptimized
            />
          )}
          <p className="mb-2">{tf("account.mfa.secret.label", "Secret:")} {secret}</p>
          <form onSubmit={verify} className="space-y-2">
            <input
              value={token}
              onChange={(e) => setToken(e.target.value)}
              className="rounded border p-2"
              placeholder={tf("account.mfa.input.placeholderShort", "Enter code")}
            />
            <button
              type="submit"
              className="rounded bg-primary px-4 py-2 min-h-10 min-w-10"
              data-token="--color-primary" // i18n-exempt -- DS-1234 [ttl=2025-11-30] — DS token attribute
            >
              <span className="text-primary-fg" data-token="--color-primary-fg"> {/* i18n-exempt -- DS-1234 [ttl=2025-11-30] — DS token attribute */}
                {tf("actions.verify", "Verify")}
              </span>
            </button>
          </form>
        </div>
      )}
      {status && <p>{status}</p>}
    </div>
  );
}

// packages/ui/src/components/account/Orders.tsx
import { getCustomerSession, hasPermission } from "@auth";
import { getOrdersForCustomer } from "@acme/platform-core/orders";
import { getTrackingStatus as getShippingTrackingStatus } from "@acme/platform-core/shipping";
import { getTrackingStatus as getReturnTrackingStatus } from "@acme/platform-core/returnAuthorization";
import type { RentalOrder } from "@acme/types";
import { redirect } from "next/navigation";
import type { Locale } from "@acme/i18n/locales";
import type { TranslatableText } from "@acme/types/i18n";
import { useTranslations as getServerTranslations } from "@acme/i18n/useTranslations.server";
import StartReturnButton from "./StartReturnButton";
import type { OrderStep } from "../organisms/OrderTrackingTimeline";
import { OrderTrackingTimeline } from "../organisms/OrderTrackingTimeline";
import AccountNavigation from "./AccountNavigation";
import type { ReactNode } from "react";
import { resolveTranslatableText } from "./translations";
import {
  ACCOUNT_PROFILE_PATH,
  ACCOUNT_ORDERS_PATH,
  ACCOUNT_SESSIONS_PATH,
  ACCOUNT_SHELL_TEST_ID,
} from "./constants";

export interface OrdersPageProps {
  /** ID of the current shop for fetching orders */
  shopId: string;
  /** Optional heading override */
  title?: TranslatableText;
  /** Destination to return to after login */
  callbackUrl?: string;
  /** Locale for resolving inline values */
  locale?: Locale;
  /** Whether returns are enabled */
  returnsEnabled?: boolean;
  /** Optional return policy link */
  returnPolicyUrl?: string;
  /** Whether tracking is enabled for this shop */
  trackingEnabled?: boolean;
  /** List of carriers supported for tracking */
  trackingProviders?: string[];
}

export const metadata = { title: "Orders" };

export default async function OrdersPage({
  shopId,
  title,
  callbackUrl = ACCOUNT_ORDERS_PATH,
  returnsEnabled = false,
  returnPolicyUrl,
  trackingEnabled = true,
  trackingProviders = [],
  locale = "en",
}: OrdersPageProps) {
  const t = await getServerTranslations(locale);
  const session = await getCustomerSession();
  if (!session) {
    redirect(`/login?callbackUrl=${encodeURIComponent(callbackUrl)}`);
    return null as never;
  }

  const navLabel = t("account.navigation.ariaLabel");
  const navItems = [
    { href: ACCOUNT_PROFILE_PATH, label: t("account.profile.title") },
    { href: ACCOUNT_ORDERS_PATH, label: t("account.orders.title") },
    { href: ACCOUNT_SESSIONS_PATH, label: t("account.sessions.title") },
  ];
  const headingId = "account-orders-heading";
  const heading = resolveTranslatableText(t, title, "account.orders.title", locale);

  const canViewOrders = hasPermission(session.role, "view_orders");
  let body: ReactNode;

  if (!canViewOrders) {
    body = <p>{t("account.orders.notAuthorized")}</p>;
  } else {
    const orders: RentalOrder[] = await getOrdersForCustomer(
      shopId,
      session.customerId,
    );

    if (!orders.length) {
      body = <p>{t("account.orders.empty")}</p>;
    } else {
      const items = await Promise.all(
        orders.map(async (o) => {
          let shippingSteps: OrderStep[] = [];
          let returnSteps: OrderStep[] = [];
          let status: string | null = null;
          const returnStatus = o.returnStatus ?? null;
          if (trackingEnabled && trackingProviders.length > 0 && o.trackingNumber) {
            const provider = trackingProviders[0] as "ups" | "dhl";
            const ship = await getShippingTrackingStatus({
              provider,
              trackingNumber: o.trackingNumber,
            });
            shippingSteps = ship.steps as OrderStep[];
            if (typeof ship.status === "string") {
              status = ship.status as string;
            }
            const ret = await getReturnTrackingStatus({
              provider,
              trackingNumber: o.trackingNumber,
            });
            returnSteps = ret.steps as OrderStep[];
          }
          return (
            <li key={o.id} className="rounded border p-4">
              <div>{t("account.orders.orderLabel")} {o.id}</div>
              {o.expectedReturnDate && <div>{t("account.orders.returnLabel")} {o.expectedReturnDate}</div>}
              <OrderTrackingTimeline
                shippingSteps={shippingSteps}
                returnSteps={returnSteps}
                trackingEnabled={trackingEnabled}
                className="mt-2"
              />
              {status && <p className="mt-2 text-sm">{t("account.orders.statusLabel")} {status}</p>}
              {returnStatus && (
                <p className="mt-2 text-sm">{t("account.orders.returnLabel")} {returnStatus}</p>
              )}
              {returnsEnabled && !o.returnedAt && (
                <StartReturnButton sessionId={o.sessionId} />
              )}
            </li>
          );
        }),
      );

      body = (
        <>
          {returnsEnabled && returnPolicyUrl && (
            <p className="pb-4">
              <a
                href={returnPolicyUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="underline inline-block min-h-10 min-w-10 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 focus-visible:ring-offset-background"
              >
                {t("account.orders.returnPolicy")}
              </a>
            </p>
          )}
          <ul className="space-y-2" data-testid="orders-list" data-cy="orders-list">
            {items}
          </ul>
        </>
      );
    }
  }

  return (
    <div className="p-6">
      <div className="flex flex-col gap-6 md:flex-row" data-testid={ACCOUNT_SHELL_TEST_ID}>
        <AccountNavigation ariaLabel={navLabel} currentPath={ACCOUNT_ORDERS_PATH} items={navItems} />
        <main
          className="flex-1 rounded border p-4 md:p-6"
          role="main"
          aria-labelledby={headingId}
        >
          <h1 id={headingId} className="mb-4 text-xl">{heading}</h1>
          {body}
        </main>
      </div>
    </div>
  );
}

"use client";

// packages/ui/src/components/account/ProfileForm.tsx
import { useState, type ReactNode } from "react";
import { getCsrfToken } from "@acme/shared-utils";
import { useTranslations } from "@acme/i18n";

export interface ProfileFormProps {
  /** Pre-filled name value; may be undefined if profile data is missing */
  name?: string | undefined;
  /** Pre-filled email value; may be undefined if profile data is missing */
  email?: string | undefined;
}

export default function ProfileForm({ name = "", email = "" }: ProfileFormProps) {
  const t = useTranslations();
  const [form, setForm] = useState({ name, email });
  const [status, setStatus] = useState<"idle" | "success" | "error">("idle");
  const [message, setMessage] = useState<ReactNode | null>(null);
  const [errors, setErrors] = useState<Record<string, string>>({});

  const focusFirstError = (errs: Record<string, string>) => {
    const first = Object.keys(errs)[0];
    if (first) {
      document.getElementById(first)?.focus();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    setErrors((prev) => {
      const next = { ...prev };
      delete next[e.target.name];
      return next;
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setStatus("idle");
    setMessage(null);
    setErrors({});
    const newErrors: Record<string, string> = {};
    if (!form.name) newErrors.name = t("forms.validation.name.required") as string;
    if (!form.email) newErrors.email = t("forms.validation.email.required") as string;
    if (Object.keys(newErrors).length) {
      setErrors(newErrors);
      setStatus("error");
      setMessage(t("forms.error.fixBelow"));
      focusFirstError(newErrors);
      return;
    }
    try {
      const csrfToken = getCsrfToken();
      const res = await fetch("/api/account/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "x-csrf-token": csrfToken ?? "",
        },
        body: JSON.stringify(form),
      });
      if (!res.ok) {
        const data = await res.json();
        setStatus("error");
        if (res.status === 409) {
          const conflict = { email: (data.error ?? t("forms.email.inUse")) as string };
          setErrors(conflict);
          setMessage(conflict.email);
          focusFirstError(conflict);
        } else if (res.status === 400 && data && typeof data === "object" && !("error" in data)) {
          const fieldErrors = data as Record<string, string[]>;
          const formatted = Object.fromEntries(
            Object.entries(fieldErrors).map(([key, value]) => [key, value[0]])
          );
          setErrors(formatted);
          setMessage(t("forms.error.fixBelow"));
          focusFirstError(formatted);
        } else {
          setMessage(data.error ?? t("forms.error.updateFailed"));
        }
        return;
      }
      setStatus("success");
      setMessage(t("forms.profile.success"));
    } catch {
      setStatus("error");
      setMessage(t("forms.error.unexpected"));
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4" noValidate>
      <div className="flex flex-col">
        <label htmlFor="name" className="mb-1">{t("fields.name")}</label>
        <input
          id="name"
          name="name"
          value={form.name}
          onChange={handleChange}
          className="rounded border p-2"
          required
          aria-invalid={errors.name ? "true" : "false"}
          aria-describedby={errors.name ? "name-error" : undefined}
        />
        {errors.name && (
          <p
            id="name-error"
            className="text-danger text-sm"
            data-token="--color-danger" // i18n-exempt -- DS-1234 [ttl=2025-11-30] — dynamic validation message
            role="alert"
          >
            {errors.name}
          </p>
        )}
      </div>
      <div className="flex flex-col">
        <label htmlFor="email" className="mb-1">{t("fields.email")}</label>
        <input
          id="email"
          name="email"
          type="email"
          value={form.email}
          onChange={handleChange}
          className="rounded border p-2"
          required
          aria-invalid={errors.email ? "true" : "false"}
          aria-describedby={errors.email ? "email-error" : undefined}
        />
        {errors.email && (
          <p
            id="email-error"
            className="text-danger text-sm"
            data-token="--color-danger" // i18n-exempt -- DS-1234 [ttl=2025-11-30] — dynamic validation message
            role="alert"
          >
            {errors.email}
          </p>
        )}
      </div>
      <button
        type="submit"
        className="rounded bg-primary px-4 py-2 min-h-10 min-w-10"
        data-token="--color-primary" // i18n-exempt -- DS-1234 [ttl=2025-11-30] — label below is translated
      >
        <span className="text-primary-fg" data-token="--color-primary-fg"> {/* i18n-exempt -- DS-1234 [ttl=2025-11-30] — child content uses t() */}
          {t("Save")}
        </span>
      </button>
      {status === "success" && (
        <p className="text-success" data-token="--color-success" role="status"> {/* i18n-exempt -- DS-1234 [ttl=2025-11-30] — dynamic status message */}
          {message}
        </p>
      )}
      {status === "error" && (
