  initialQty: number;
  nav?: { label: string; url: string }[];
  height?: string;
  padding?: string;
}) {
  const [cart] = useCart();
  const [qty, setQty] = useState(initialQty);
  const t = useTranslations();

  // keep qty in sync after hydration
  useEffect(() => {
    setQty(
      (Object.values(cart) as Array<{ qty: number }>).reduce(
        (s, line) => s + line.qty,
