"use client";

import { useCallback, useMemo, useState } from "react";
import type { PageComponent } from "@acme/types";
import { ulid } from "ulid";
import { ComponentEditor } from "@ui/components/cms/page-builder";
import { saveLibrary } from "@ui/components/cms/page-builder/libraryStore";

export default function ComponentEditorClient() {
  const initial = useMemo<PageComponent>(
    () => ({ id: ulid(), type: "Text", text: "Edit me" }),
    []
  );
  const [component, setComponent] = useState<PageComponent>(initial);
  const [label, setLabel] = useState<string>("");
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState<string>("");

  const onChange = useCallback((patch: Partial<PageComponent>) => {
    setComponent((prev: PageComponent) => ({ ...prev, ...patch } as PageComponent));
  }, []);

  const onResize = useCallback((patch: Partial<PageComponent>) => {
    setComponent((prev: PageComponent) => ({ ...prev, ...patch } as PageComponent));
  }, []);

  return (
    <div className="space-y-4">
      <ComponentEditor component={component} onChange={onChange} onResize={onResize} />
      <div className="rounded border p-3 space-y-2">
        <div className="font-semibold">Save to Global Library</div>
        <div className="flex items-center gap-2">
          <input
            type="text"
            className="min-w-0 flex-1 rounded border px-2 py-1 text-sm min-h-10"
            placeholder="Component name (e.g. Hero, Promo Card)"
            value={label}
            onChange={(e) => setLabel(e.target.value)}
            aria-label="Component name"
          />
          <button
            type="button"
            className="rounded border px-3 py-1 text-sm min-h-10 min-w-10"
            disabled={saving || !label.trim()}
            onClick={async () => {
              setSaving(true);
              setMessage("");
              try {
                const item = { id: ulid(), label: label.trim(), template: component, createdAt: Date.now() };
                await saveLibrary("_global", item);
                setMessage("Saved to Global Library");
              } catch (err) {
                console.error(err);
                setMessage("Save failed");
              } finally {
                setSaving(false);
              }
            }}
          >
            {saving ? "Saving..." : "Save to Global"}
          </button>
        </div>
        {message && <div className="text-xs text-muted-foreground" aria-live="polite">{message}</div>}
      </div>
      <div className="rounded border p-3 text-xs">
        <div className="mb-1 font-semibold">Component JSON</div>
        <pre className="whitespace-pre-wrap break-all text-xs leading-snug">
          {JSON.stringify(component, null, 2)}
        </pre>
      </div>
    </div>
  );
}

// apps/cms/src/app/cms/pages/page.tsx

import Link from "next/link";
import { listShops } from "../../../lib/listShops";
import { Card, CardContent } from "@/components/atoms/shadcn";
import { Tag } from "@ui/components/atoms";
import { Grid } from "@ui/components/atoms/primitives/Grid";

export default async function PagesIndexPage() {
  const shops = await listShops();

  return (
    <div className="space-y-8 text-foreground">
      <section className="relative overflow-hidden rounded-3xl border border-border-1 bg-hero-contrast text-hero-foreground shadow-elevation-4">
        <div className="relative space-y-4 px-6 py-7">
          <Tag variant="default">
            Content · Choose a shop
          </Tag>
          <h1 className="text-3xl font-semibold md:text-4xl">
            Start crafting the story for each storefront
          </h1>
          <p className="text-sm text-hero-foreground/90">
            Select a shop to manage its landing pages, navigation, and editorial layouts.
          </p>
        </div>
      </section>

      <section>
        <Card className="border border-border-1 bg-surface-2 shadow-elevation-3">
          <CardContent className="p-6">
            <Grid cols={1} gap={4} className="sm:grid-cols-2 lg:grid-cols-3">
            {shops.map((shop) => (
              <Link
                key={shop}
                href={`/cms/shop/${shop}/pages/edit/page`}
                className="group rounded-2xl border border-border-1 bg-surface-2 px-4 py-5 text-sm font-medium text-foreground shadow-elevation-1 transition hover:border-border-3 hover:bg-surface-3"
              >
                <span className="block text-xs uppercase tracking-wider text-foreground">
                  Shop
                </span>
                <span className="text-lg font-semibold text-foreground">{shop.toUpperCase()}</span>
                <span className="mt-2 block text-xs text-foreground">
                  Manage static pages, navigation routes, and published experiences.
                </span>
              </Link>
            ))}
            {shops.length === 0 && (
              <div className="rounded-2xl border border-border-1 bg-surface-2 px-4 py-5 text-sm text-foreground">
                No shops found yet.
              </div>
            )}
            </Grid>
          </CardContent>
        </Card>
      </section>
    </div>
  );
}

"use client";

import { useMemo, useState } from "react";
import type { ReturnAuthorization } from "@acme/types";
import { Alert, Tag } from "@ui/components/atoms";
import { Grid } from "@ui/components/atoms/primitives/Grid";
import { Stack } from "@ui/components/atoms/primitives/Stack";
import { Sidebar } from "@ui/components/atoms/primitives/Sidebar";
import {
  Button,
  Card,
  CardContent,
  Input,
} from "@/components/atoms/shadcn";
import { cn } from "@ui/utils/style";
import Link from "next/link";

export interface RaDashboardProps {
  ras: ReturnAuthorization[];
  error?: string | null;
}

type QuickFilterKey = "all" | "pending" | "resolved" | "highRisk";

interface QuickFilter {
  key: QuickFilterKey;
  label: string;
  description: string;
  test: (ra: ReturnAuthorization) => boolean;
}

const quickFilters: QuickFilter[] = [
  {
    key: "all",
    label: "All",
    description: "Every authorization",
    test: () => true,
  },
  {
    key: "pending",
    label: "Awaiting review",
    description: "Needs manual inspection",
    test: (ra) => /pending|open|awaiting/i.test(ra.status),
  },
  {
    key: "resolved",
    label: "Resolved",
    description: "Completed or refunded",
    test: (ra) => /approved|completed|closed|refunded/i.test(ra.status),
  },
  {
    key: "highRisk",
    label: "High risk",
    description: "Escalate immediately",
    test: (ra) => classifyRisk(ra) === "high",
  },
];

type RiskLevel = "high" | "medium" | "low";

function classifyRisk(ra: ReturnAuthorization): RiskLevel {
  const note = ra.inspectionNotes?.toLowerCase() ?? "";
  const status = ra.status.toLowerCase();
  if (/(fraud|chargeback|damage|broken|escalated)/.test(note) || /fraud|escalated/.test(status)) {
    return "high";
  }
  if (/(manual|verify|awaiting|inspection)/.test(note) || /pending|hold|review/.test(status)) {
    return "medium";
  }
  return "low";
}

function statusVariant(status: string): "default" | "success" | "warning" | "destructive" {
  const normalized = status.toLowerCase();
  if (/approved|completed|closed|resolved/.test(normalized)) return "success";
  if (/pending|awaiting|hold|review/.test(normalized)) return "warning";
  if (/rejected|fraud|cancelled|denied/.test(normalized)) return "destructive";
  return "default";
}

function riskVariant(level: RiskLevel): "default" | "warning" | "destructive" | "success" {
  if (level === "high") return "destructive";
  if (level === "medium") return "warning";
  return "success";
}

export function RaDashboard({ ras, error }: RaDashboardProps) {
  const [activeFilter, setActiveFilter] = useState<QuickFilterKey>("all");
  const [searchTerm, setSearchTerm] = useState("");

  const metrics = useMemo(() => {
    const pending = ras.filter(quickFilters.find((f) => f.key === "pending")!.test)
      .length;
    const resolved = ras.filter(quickFilters.find((f) => f.key === "resolved")!.test)
      .length;
    const highRisk = ras.filter((ra) => classifyRisk(ra) === "high").length;
    return [
      { label: "Active queue", value: pending, caption: "Awaiting attention" },
      { label: "Resolved", value: resolved, caption: "Closed authorizations" },
      { label: "High risk", value: highRisk, caption: "Escalations to triage" },
      { label: "Total", value: ras.length, caption: "Records in system" },
    ];
  }, [ras]);

  const filtered = useMemo(() => {
    const quick = quickFilters.find((filter) => filter.key === activeFilter) ?? quickFilters[0];
    return ras.filter((ra) => {
      const matchesQuick = quick.test(ra);
      if (!matchesQuick) return false;
      if (!searchTerm.trim()) return true;
      const query = searchTerm.trim().toLowerCase();
      return (
        ra.raId.toLowerCase().includes(query) ||
        ra.orderId.toLowerCase().includes(query) ||
        (ra.inspectionNotes ?? "").toLowerCase().includes(query)
      );
    });
  }, [ras, activeFilter, searchTerm]);

  if (error) {
    return (
      <Alert variant="danger" tone="soft" title="Unable to load return authorizations">
        <p className="text-sm">{error}</p>
        <p className="text-xs">Retry shortly or verify the data source connection for the RA service.</p>
      </Alert>
    );
  }

  return (
    <div className="space-y-6 text-foreground">
      <header className="space-y-3">
        <Tag variant="default" className="bg-primary-soft text-foreground">
          Return authorizations
        </Tag>
        <div className="flex flex-wrap items-end justify-between gap-4">
          <div className="min-w-0 space-y-1">
            <h2 className="text-2xl font-semibold">RA workflow overview</h2>
            <p className="text-sm text-muted-foreground">
              Quickly identify escalations, filter by status, and move authorizations forward.
            </p>
          </div>
        </div>
      </header>

      <section>
        <Grid cols={1} gap={4} className="sm:grid-cols-2 xl:grid-cols-4">
          {metrics.map((metric) => (
            <Card
              key={metric.label}
              className="border border-border-1 bg-surface-2"
            >
              <CardContent className="space-y-1 px-4 py-3">
                <p className="text-xs uppercase tracking-wide text-muted-foreground">
                  {metric.label}
                </p>
                <p className="text-2xl font-semibold">{metric.value}</p>
                <p className="text-xs text-muted-foreground">{metric.caption}</p>
              </CardContent>
            </Card>
          ))}
        </Grid>
      </section>

      <section>
        <Sidebar sideWidth="w-72" gap={5} className="lg:items-start">
          <Card className="border border-border-1 bg-surface-2">
            <CardContent className="space-y-4 p-6">
            <div className="space-y-1">
              <h3 className="text-base font-semibold">Quick filters</h3>
              <p className="text-xs text-muted-foreground">
                Snap to a subset and broadcast the count in the live region below.
              </p>
            </div>
            <Stack gap={2}>
              {quickFilters.map((filter) => (
                <button
                  key={filter.key}
                  type="button"
                  onClick={() => setActiveFilter(filter.key)}
                  aria-pressed={activeFilter === filter.key}
                  className={cn(
                    "rounded-xl border px-3 py-2 text-left text-sm transition",
                    activeFilter === filter.key
                      ? "border-primary/60 bg-primary-soft text-foreground"
                      : "border-border-2 bg-surface-2 text-muted-foreground hover:border-primary/50 hover:bg-primary-soft"
                  )}
                >
                  <span className="block font-semibold">{filter.label}</span>
                  <span className="block text-xs text-muted-foreground">{filter.description}</span>
                </button>
              ))}
            </Stack>
            <div className="space-y-1">
              <label className="text-xs font-semibold uppercase tracking-wide text-muted-foreground">
                Search
              </label>
              <Input
                value={searchTerm}
                onChange={(event) => setSearchTerm(event.target.value)}
                placeholder="Filter by RA, order, or note"
                className="border-border-2 bg-surface-2 text-foreground placeholder:text-muted-foreground"
              />
            </div>
          </CardContent>
          </Card>

          <Card className="border border-border-1 bg-surface-2">
            <CardContent className="space-y-4 p-6">
            <div className="flex flex-wrap items-start justify-between gap-3">
              <div className="min-w-0 space-y-1">
                <h3 className="text-lg font-semibold">Authorization queue</h3>
                <p className="text-xs text-muted-foreground">
                  Actions provide suggested follow-ups so teams can unblock customers quickly.
                </p>
              </div>
              <Tag className="shrink-0" variant="default">
                {filtered.length} showing
              </Tag>
            </div>
            <div
              role="status"
              aria-live="polite"
              aria-atomic="true"
              data-testid="ra-announce"
              data-cy="ra-announce"
              className="sr-only"
            >
              Showing {filtered.length} of {ras.length} return authorizations
            </div>
            <Stack gap={3}>
              {filtered.map((ra) => {
                const risk = classifyRisk(ra);
                return (
                  <Card
                    key={ra.raId}
                    data-testid="ra-card"
                    data-cy="ra-card"
                  className={cn(
                    "border border-border-1 bg-surface-2 text-foreground",
                    risk === "high" && "border-danger/40",
                    risk === "medium" && "border-warning/40"
                  )}
                >
                    <CardContent className="space-y-3 p-6">
                    <div className="flex flex-wrap items-start justify-between gap-3">
                      <div className="min-w-0 space-y-1">
                        <p className="text-sm font-semibold">RA {ra.raId}</p>
                        <p className="text-xs text-muted-foreground">Order {ra.orderId}</p>
                      </div>
                      <div className="flex shrink-0 flex-wrap gap-2">
                        <Tag variant={statusVariant(ra.status)}>{ra.status}</Tag>
                        <Tag variant={riskVariant(risk)}>Risk: {risk}</Tag>
                      </div>
                    </div>
                    {ra.inspectionNotes && (
                      <p className="text-sm text-muted-foreground">{ra.inspectionNotes}</p>
                    )}
                    <div className="flex flex-wrap gap-3">
                      <Button
                        asChild
                        className="h-9 rounded-lg bg-primary px-4 text-sm font-semibold text-primary-foreground hover:bg-primary/90"
                      >
                        <Link href={`/cms/orders/${ra.orderId}`}>View order</Link>
                      </Button>
                      <Button
                        type="button"
                        variant="outline"
                        className="h-9 rounded-lg border-border-2 bg-surface-2 text-sm text-foreground hover:bg-surface-3"
                      >
                        Mark for follow-up
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
            {filtered.length === 0 && (
              <Card className="border border-border-1 bg-surface-2 text-foreground">
                <CardContent className="space-y-3 px-6 py-6 text-center text-sm text-muted-foreground">
                  <Tag variant="warning">
                    No matches
                  </Tag>
                  <p>
                    Try clearing filters or widening your search to surface more return authorizations.
                  </p>
                </CardContent>
              </Card>
            )}
            </Stack>
            </CardContent>
          </Card>
        </Sidebar>
      </section>
    </div>
  );
}

export default RaDashboard;

import { Button, Card, CardContent, Input, Tag } from "@/components/atoms/shadcn";
import { Tooltip } from "@/components/atoms";
import { Cluster } from "@ui/components/atoms/primitives/Cluster";
import { Inline } from "@ui/components/atoms/primitives/Inline";

import type { Role } from "@cms/auth/roles";

import type { InviteFormState } from "./useRbacManagementPanel";
import type { RoleDetail } from "../components/roleDetails";

type InviteUserFormProps = {
  roles: Role[];
  roleDetails: Record<Role, RoleDetail>;
  form: InviteFormState;
  helperText: string;
  isInviting: boolean;
  onFieldChange: (field: "name" | "email" | "password", value: string) => void;
  onToggleRole: (role: Role) => void;
  onSubmit: () => void;
  onReset: () => void;
};

export default function InviteUserForm({
  roles,
  roleDetails,
  form,
  helperText,
  isInviting,
  onFieldChange,
  onToggleRole,
  onSubmit,
  onReset,
}: InviteUserFormProps) {
  return (
    <Card>
      <CardContent className="space-y-4">
        <Cluster gap={2} alignY="center" justify="between">
          <h3 className="min-w-0 text-base font-semibold text-foreground">Invite User</h3>
          <Tag className="shrink-0" variant="warning">Manual invite</Tag>
        </Cluster>
        <div className="grid gap-3 sm:grid-cols-2">
          <label className="flex flex-col gap-1 text-sm">
            <span className="font-medium text-foreground">Name</span>
            <Input
              name="name"
              value={form.name}
              onChange={(event) => onFieldChange("name", event.target.value)}
            />
          </label>
          <label className="flex flex-col gap-1 text-sm">
            <span className="font-medium text-foreground">Email</span>
            <Input
              name="email"
              type="email"
              value={form.email}
              onChange={(event) => onFieldChange("email", event.target.value)}
            />
          </label>
          <label className="flex flex-col gap-1 text-sm sm:col-span-2">
            <span className="font-medium text-foreground">Temporary password</span>
            <Input
              name="password"
              type="password"
              value={form.password}
              onChange={(event) => onFieldChange("password", event.target.value)}
            />
          </label>
        </div>

        <section className="space-y-3" aria-labelledby="invite-roles">
          <p
            id="invite-roles"
            className="text-xs font-semibold uppercase tracking-wide text-muted-foreground"
          >
            Assign starter roles
          </p>
          <Cluster gap={2} role="group" aria-label="Assign roles for the invitation">
            {roles.map((role) => {
              const detail = roleDetails[role];
              const isSelected = form.roles.includes(role);
              return (
                <Button
                  key={role}
                  type="button"
                  variant={isSelected ? "default" : "outline"}
                  aria-pressed={isSelected}
                  onClick={() => onToggleRole(role)}
                  className="h-auto px-3 py-2 text-sm"
                >
                  <Inline gap={2} alignY="center">
                    <span className="font-medium">{detail?.title ?? role}</span>
                    {detail && (
                      <Tooltip text={detail.description}>
                        <span
                          aria-hidden="true"
                          className="text-xs text-muted-foreground underline decoration-dotted"
                        >
                          ?
                        </span>
                      </Tooltip>
                    )}
                  </Inline>
                </Button>
              );
            })}
          </Cluster>
          <p className="text-xs text-muted-foreground">{helperText}</p>
        </section>

        <div className="flex flex-col gap-2 sm:flex-row">
          <Button type="button" onClick={onSubmit} disabled={isInviting} className="flex-1">
            {isInviting ? "Sending invite…" : "Send invite"}
          </Button>
          <Button
            type="button"
            variant="outline"
            onClick={onReset}
            disabled={isInviting}
            className="flex-1"
          >
            Clear form
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}
