"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[10013],{10013:t=>{t.exports=JSON.parse('{"meta":{"title":"سياسة الخصوصية | Hostel Brikette","description":"كيف يجمع Hostel Brikette البيانات الشخصية ويستخدمها ويخزنها ويشاركها عندما تتصفح موقعنا أو تحجز غرفة."},"headings":{"pageTitle":"سياسة الخصوصية","subtitle":"كيف نتعامل مع بياناتك الشخصية"},"lastUpdated":{"label":"آخر تحديث","date":"2026-01-09"},"toc":{"title":"في هذه الصفحة","aria":"جدول محتويات سياسة الخصوصية"},"sections":{"intro":{"title":"نظرة عامة","body":["تشرح سياسة الخصوصية هذه البيانات الشخصية التي نجمعها، ولماذا نجمعها، وكيف نستخدمها عندما تتصفح موقعنا أو تتواصل معنا أو تحجز غرفة.","إذا كنت تبحث عن القواعد والشروط التجارية للحجز (المدفوعات، مواعيد الإلغاء، تسجيل الوصول/المغادرة، والتزامات الضيوف)، يُرجى قراءة الشروط والأحكام لحجوزات الغرف."]},"controller":{"title":"من المسؤول عن بياناتك","body":["الجهة المسؤولة عن معالجة البيانات هي Skylar SRL التي تعمل باسم Hostel Brikette (\xabنحن\xbb).","إذا كانت لديك أسئلة حول هذه السياسة أو ترغب في ممارسة حقوق الخصوصية، تواصل معنا على {{email}}."]},"dataWeCollect":{"title":"البيانات الشخصية التي نجمعها","body":["عندما تتواصل معنا أو تطلب حجزًا، قد تزودنا بتفاصيل مثل الاسم، البريد الإلكتروني، رقم الهاتف، الجنسية، تواريخ الإقامة، وأي رسائل ترسلها لنا.","إذا أكملت الحجز عبر وكالة سفر عبر الإنترنت (OTA) أو محرك حجز تابع لطرف ثالث، يقوم ذلك المزود بمعالجة تفاصيل الدفع الخاصة بك وفقًا لسياسة الخصوصية الخاصة به. عادةً ما نتلقى معلومات الحجز اللازمة لاستضافتك (مثل اسم الضيف، بيانات الاتصال، التواريخ، ورقم الحجز المرجعي).","عند تصفح موقعنا، قد تعالج خوادمنا بيانات تقنية أساسية (مثل عنوان IP، معلومات الجهاز والمتصفح، والصفحات المطلوبة) لأغراض الأمن وتصحيح الأخطاء والأداء."]},"howWeUse":{"title":"كيف نستخدم بياناتك","body":["للرد على الاستفسارات وتقديم دعم العملاء.","لمعالجة وإدارة حجزك وتسجيل الوصول والتواصل المتعلق بالإقامة.","للامتثال للالتزامات القانونية (بما في ذلك تلك المتعلقة بالضيافة والسلامة العامة).","لتأمين وصيانة موقعنا (منع الاحتيال، مراقبة إساءة الاستخدام، والموثوقية)."]},"legalBases":{"title":"الأسس القانونية (لماذا يحق لنا معالجة البيانات)","body":["العقد: لاتخاذ خطوات بناءً على طلبك قبل إبرام الحجز ولتنفيذ عقد الحجز.","الالتزام القانوني: للامتثال للمتطلبات التي يفرضها القانون المعمول به (بما في ذلك اللوائح الإيطالية الخاصة بالضيافة).","المصالح المشروعة: لتشغيل موقعنا وحمايته وتحسينه واتصالاتنا، بشرط ألا تطغى هذه المصالح على حقوقك."]},"sharing":{"title":"مع من نشارك البيانات","body":["نشارك البيانات الشخصية فقط عند الحاجة لتقديم الخدمات أو الامتثال للقانون. قد يشمل ذلك منصات الحجز التي تختارها، مزودي الدفع المستخدمين في تلك المنصات، مزودي البريد/الاتصالات، ومزودي الخدمات التقنية التي تستضيف أو تحمي موقعنا.","لا نبيع البيانات الشخصية."]},"retention":{"title":"مدة الاحتفاظ بالبيانات","body":["نحتفظ بالبيانات الشخصية فقط للمدة اللازمة للأغراض الموضحة أعلاه ولتلبية المتطلبات القانونية أو المحاسبية أو الخاصة بالتقارير.","قد تختلف فترات الاحتفاظ حسب نوع السجل والالتزام القانوني المعمول به."]},"security":{"title":"كيف نحمي بياناتك","body":["نستخدم تدابير تقنية وتنظيمية معقولة لحماية البيانات الشخصية من الفقدان أو سوء الاستخدام أو الوصول غير المصرح به.","لا يمكن ضمان أمان أي نظام بنسبة 100%، لكننا نعمل باستمرار على تحسين الأمن."]},"yourRights":{"title":"حقوقك","body":["اعتمادًا على موقعك والقانون المعمول به، قد تكون لك حقوق في الوصول إلى بياناتك الشخصية أو تصحيحها أو حذفها أو تقييد معالجتها، والاعتراض على بعض المعالجات.","لممارسة حقوقك، تواصل معنا على {{email}}. قد نحتاج إلى التحقق من هويتك."]},"international":{"title":"عمليات النقل الدولية","body":["قد يعالج بعض مقدمي الخدمات البيانات خارج بلدك. وعند الحاجة نستخدم ضمانات مناسبة لعمليات نقل البيانات الدولية."]},"changes":{"title":"التغييرات على هذه السياسة","body":["قد نقوم بتحديث سياسة الخصوصية هذه من وقت لآخر. أحدث نسخة هي المنشورة على هذه الصفحة."]}},"footer":{"backToTop":"العودة إلى الأعلى"}}')}}]);