"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[11301],{11301:n=>{n.exports=JSON.parse('{"seo":{"title":"L\xe1i xe tr\xean Amalfi Coast: mẹo cho road trip","description":"Luật đường, v\xf9ng ZTL, thực tế chỗ đỗ, ng\xe0y giới hạn biển số v\xe0 khi n\xe0o kh\xf4ng n\xean l\xe1i — c\xf9ng lựa chọn an to\xe0n hơn."},"linkLabel":"L\xe1i xe tr\xean Amalfi Coast: mẹo cho chuyến đi","intro":["L\xe1i xe tr\xean Amalfi Coast vừa ngoạn mục vừa căng thẳng — đường hẹp, quy định thay đổi theo m\xf9a v\xe0 chỗ đỗ xe lu\xf4n khan hiếm.","C\xe1c gợi \xfd dưới đ\xe2y gi\xfap bạn quyết định khi n\xe0o n\xean l\xe1i, n\xean gửi xe ở đ\xe2u v\xe0 l\xfac n\xe0o n\xean chuyển sang phương \xe1n kh\xe1c."],"sections":[{"id":"rules","title":"Quy định v\xe0 hạn chế","body":["Kiểm tra khu vực ZTL, lịch biển số chẵn lẻ v\xe0 giới hạn chiều rộng xe trước khi khởi h\xe0nh — cuối tuần m\xf9a h\xe8 thường cấm xe lớn chạy tr\xean c\xe1c đoạn đẹp nhất.","Giữ tốc độ thấp, bấm c\xf2i khi \xf4m cua khuất như người bản địa v\xe0 ch\xfa \xfd xe m\xe1y len qua hai b\xean."]},{"id":"parking","title":"Thực tế b\xe3i đỗ xe","body":["Đặt trước garage tư nh\xe2n ở Positano/Amalfi, x\xe1c nhận giờ mở cửa, giới hạn chiều cao v\xe0 xem họ chấp nhận thẻ hay y\xeau cầu đặt cọc tiền mặt.","M\xf9a cao điểm ph\xed 35–60 € mỗi ng\xe0y (khoảng 38–65 $); đỗ ngo\xe0i đường rất hiếm v\xe0 bị kiểm tra gắt gao."]},{"id":"when-to-park","title":"Khi n\xe0o n\xean gửi xe","body":["Ở ngắn ng\xe0y, h\xe3y gửi xe tại Sorrento hoặc Salerno rồi đi tiếp bằng xe bu\xfdt hay ph\xe0 để giảm \xe1p lực v\xe0 chi ph\xed trong trung t\xe2m l\xe0ng.","Tr\xe1nh l\xe1i xe khi c\xf3 b\xe3o, lễ hội hay về khuya — đường c\xf3 thể bị chặn v\xe0 bạn dễ mệt mỏi; phương tiện c\xf4ng cộng hoặc xe đặt trước sẽ an to\xe0n hơn."]}],"tips":["Mang cả thẻ thanh to\xe1n kh\xf4ng chạm lẫn tiền lẻ; một số b\xe3i y\xeau cầu đặt cọc cho điều khiển từ xa hoặc ra v\xe0o ban đ\xeam.","Sau bữa tối thưởng rượu hay buổi h\xf2a nhạc muộn, n\xean đặt taxi/xe đưa đ\xf3n thay v\xec l\xe1i xe mệt mỏi tr\xean đường v\xe1ch đ\xe1."],"faqs":[{"q":"T\xf4i c\xf3 cần giấy ph\xe9p đặc biệt kh\xf4ng?","a":["Kh\xf4ng, nhưng bạn phải tu\xe2n lịch biển số chẵn lẻ v\xe0 khu ZTL — h\xe3y kiểm tra th\xf4ng b\xe1o của thị trấn hoặc Prefettura trước khi l\xe1i xe v\xe0o."]},{"q":"T\xf4i c\xf3 thể đỗ tạm b\xean đường kh\xf4ng?","a":["Chỉ được dừng ở \xf4 được kẻ r\xf5; chắn điểm dừng xe bu\xfdt hoặc đầu cua sẽ bị phạt hoặc cẩu ngay."]}]}')}}]);