"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[129352],{129352:i=>{i.exports=JSON.parse('{"seo":{"title":"Guida a Sorrento (porta della Costiera Amalfitana)","description":"Come usare Sorrento come base: trasporti, alloggi, cucina e collegamenti verso Capri e Positano."},"linkLabel":"Guida a Sorrento (porta della Costiera Amalfitana)","intro":["Sorrento \xe8 una base pratica: ampia scelta di alloggi, collegamenti ferroviari comodi e accessi rapidi a Capri e Pompei. La nota dolente \xe8 una scenografia meno iconica rispetto a Positano."],"sections":[{"id":"why","title":"Perch\xe9 scegliere Sorrento come base","body":["Pi\xf9 scelta di alloggi e accesso diretto ai treni. In cambio, meno cartoline rispetto a Positano."]},{"id":"connections","title":"Collegamenti con Positano","body":["Autobus SITA frequenti e traghetti stagionali. Acquista i biglietti del bus prima di salire; i traghetti esauriscono i posti nei giorni di punta."]},{"id":"see","title":"Cosa vedere","body":["Centro storico, marine e belvedere: ottimo punto di partenza per Capri e Pompei."]}],"faqs":[{"q":"Sorrento \xe8 pi\xf9 economica di Positano?","a":["Di solito s\xec: conta risparmi di 30–60 € a notte sugli alloggi e pi\xf9 ristoranti a prezzi medi."]},{"q":"Fino a che ora partono i treni per Napoli?","a":["La Circumvesuviana viaggia fino alle ~22:30; dopo usa bus regionali o prevedi una notte fuori se hai un volo tardo."]}]}')}}]);