"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[130744],{130744:n=>{n.exports=JSON.parse('{"meta":{"title":"Trung t\xe2m Naples đến Positano bằng ph\xe0 | Hostel Brikette","description":"Từng bước từ trung t\xe2m Naples tới Positano bằng ph\xe0: chuyển từ ga tới cảng, mua v\xe9, l\xean t\xe0u v\xe0 mẹo khi cập bến."},"hero":{"eyebrow":"Cẩm nang di chuyển của Hostel Brikette","title":"Trung t\xe2m Naples đến Positano — Ph\xe0","description":"L\xe0m theo chỉ dẫn n\xe0y để đi từ trung t\xe2m Naples tới Positano ho\xe0n to\xe0n bằng đường biển. Hướng dẫn gồm chuyển tuyến ngắn từ Napoli Centrale ra cảng, mua v\xe9 ph\xe0, quy tr\xecnh l\xean t\xe0u v\xe0 những điều cần biết khi đến cầu cảng Positano."},"tip":{"label":"Mẹo","copy":"Cần tuyến kh\xe1c hoặc phương \xe1n dự ph\xf2ng khi biển động? Mở <link>Tổng quan C\xe1ch đến đ\xe2y</link>."},"sections":{"journey":{"title":"Tổng quan h\xe0nh tr\xecnh","body":"Ph\xe0 giữa Naples v\xe0 Positano chạy d\xe0y v\xe0o m\xf9a xu\xe2n – h\xe8 v\xe0 c\xf3 thể bị hủy khi thời tiết xấu. Tổng thời gian thường 1,5–2,5 giờ t\xf9y loại t\xe0u v\xe0 thời gian chờ ở cảng."},"transfer":{"title":"Ra cảng từ Napoli Centrale","intro":"Phương tiện c\xf4ng cộng nhanh nhất l\xe0 t\xe0u điện Tram 1 từ Piazza Garibaldi tới Municipio (ra Molo Beverello) hoặc Marittima (ra Porta di Massa). Đi taxi cũng đơn giản v\xe0 tiết kiệm cho nh\xf3m.","items":["Từ Napoli Garibaldi/Centrale, đi theo biển chỉ đến điểm t\xe0u điện Piazza Garibaldi.","Bắt Tram 1 hướng “Municipio/Porto”.","Xuống tại “Municipio” để tới Molo Beverello (t\xe0u cao tốc) hoặc “Marittima” cho Porta di Massa (ph\xe0 thường)."],"map":"Xem lộ tr\xecnh mẫu tr\xean <map>Google Maps (t\xe0u điện ra cảng)</map>."},"tickets":{"title":"V\xe9 ph\xe0 v\xe0 h\xe3ng khai th\xe1c","items":["Mua v\xe9 trực tuyến trước hoặc tại quầy ở Molo Beverello; m\xf9a h\xe8 c\xf3 thể phải xếp h\xe0ng.","Mang theo giấy tờ t\xf9y th\xe2n c\xf3 ảnh nếu d\xf9ng v\xe9 điện tử; nh\xe2n vi\xean c\xf3 thể kiểm tra trước khi l\xean t\xe0u.","Dịch vụ c\xf3 thể hủy khi biển động; c\xe1c chuyến buổi s\xe1ng thường ổn định hơn."],"copy":"Giờ chạy v\xe0 gi\xe1 v\xe9: <capri>Capri.net – Naples ↔ Positano</capri>. Bạn cũng c\xf3 thể so s\xe1nh giữa c\xe1c h\xe3ng tr\xean <positano>Positano.com (lịch tổng hợp)</positano>."},"boarding":{"title":"L\xean t\xe0u tại cảng","body":"Đến bến trước 20–30 ph\xfat để đổi phiếu (voucher) nếu cần v\xe0 xếp h\xe0ng l\xean t\xe0u. L\xe0m theo hướng dẫn của nh\xe2n vi\xean khi xếp h\xe0nh l\xfd v\xe0 ch\xfa \xfd bước ch\xe2n khi cầu dẫn lắc theo s\xf3ng."},"arrival":{"title":"Đến Positano","items":["Xuống t\xe0u tại cầu cảng Positano Spiaggia Grande.","Phu khu\xe2n v\xe1c ở bến c\xf3 thể gi\xfap mang h\xe0nh l\xfd với một khoản ph\xed; thống nhất gi\xe1 trước khi giao h\xe0nh l\xfd.","Ph\xe0 cập ở cao độ mặt biển n\xean sẽ c\xf3 nhiều bậc thang đi l\xean. C\xe2n nhắc đi xe bu\xfdt địa phương Interno từ bến để giảm leo thang."],"copy":"Lộ tr\xecnh th\xe2n thiện với h\xe0nh l\xfd từ bến đến Hostel Brikette xem tại hướng dẫn từng bước: <link>Bến ph\xe0 → Hostel Brikette</link>."},"fallback":{"title":"Nếu ph\xe0 bị hủy","body":"Nếu ph\xe0 bị hủy do thời tiết hoặc t\xecnh trạng biển, h\xe3y chuyển sang tuyến t\xe0u + xe bu\xfdt qua Sorrento. Tuy l\xe2u hơn nhưng hầu hết điều kiện đều chạy được.","cta":"Phương \xe1n dự ph\xf2ng: <link>Trung t\xe2m Naples → Positano bằng t\xe0u rồi xe bu\xfdt</link>."}},"externalLinks":{"map":"https://www.google.com/maps/dir/40.8408941,14.2611372/Napoli+Centrale,+P.za+Giuseppe+Garibaldi,+80142+Napoli+NA/@40.8375677,14.2464964,14z/data=!4m10!4m9!1m0!1m5!1m1!1s0x133b081659811bf3:0x26f7eaf2d2c03d87!2m2!1d14.2723487!2d40.8529222!3e3!5i4","capri":"https://www.capri.net/en/t/napoli/positano","positano":"https://www.positano.com/en/ferry-schedule"}}')}}]);