"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[134408],{134408:e=>{e.exports=JSON.parse('{"seo":{"title":"بوسيتانو بميزانية رحّالة: مسارات 1-3 أيام","description":"خطط بسيطة لمدّة 1-3 أيام مع شواطئ ونقاط مشاهدة ورحلات يومية منخفضة التكلفة."},"linkLabel":"بوسيتانو بميزانية رحّالة: مسارات 1-3 أيام","intro":["اختر خطة من 1 أو 2 أو 3 أيام لتبقى التكاليف منخفضة مع عدم تفويت الإطلالات والشواطئ والرحلات اليومية."],"toc":[{"href":"#day1","label":"اليوم 1: أساسيات بوسيتانو"},{"href":"#day2","label":"اليوم 2: درب الآلهة + الشاطئ"},{"href":"#day3","label":"اليوم 3: كابري أو أمالفي + رافيللو"},{"href":"#savings","label":"نصائح لتوفير المال"},{"href":"#food","label":"أطعمة منخفضة التكلفة"},{"href":"#transport","label":"نصائح المواصلات"}],"sections":[{"id":"pacing","title":"ابدأ بالصعودات الكبيرة","body":["اجعل يوم الوصول للمشي في الجزء السفلي (سبياجا غرانده، فورنيلو) واحتفظ بالأيام ذات الدرج الطويل (درب الآلهة، درج أرينزو) لما بعد نوم كافٍ.","جمّع التوقفات حسب الارتفاع لتبقى في مستوى شرفة واحد؛ أنهِ نقاط المشاهدة العلوية قبل النزول إلى المرسى."]},{"id":"budget-mix","title":"تجربة مدفوعة واحدة يوميًا","body":["ضع في الميزانية تجربة مدفوعة واحدة يوميًا (عبّارة إلى كابري، قارب مشترك، تذاكر حدائق) واملأ الباقي بالمشي المجاني والشاطئ.","تزود بالوجبات الخفيفة والماء قرب كيييزا نوفا؛ المقاهي على الواجهة البحرية هي الأعلى سعرًا."]},{"id":"viewpoints","title":"نقاط مشاهدة بلا تذاكر","body":["تمنحك فيا كريستوفورو كولومبو زاوية البطاقة البريدية عند الشروق أو الساعة الذهبية من دون رسوم دخول.","لصور أكثر هدوءًا، مرّ بالساحة المبلّطة قرب كيييزا نوفا أو المنصة الصغيرة فوق فورنيلو قبل 08:30."]},{"id":"swim-breaks","title":"استراحات سباحة اقتصادية","body":["لشاطئ فورنيلو قسم عام مجاني؛ سر على المسار الساحلي من سبياجا غرانده واستأجر فقط ما تحتاجه.","لأجواء نادي شاطئ أرينزو، انزل الدرج قبل 10:30 لتحجز مكانًا في المنطقة العامة ثم اصنع نزهة بفوكاتشيا وفاكهة."]},{"id":"evenings","title":"أمسيات بتكلفة منخفضة","body":["مشاهدة الغروب من تراس الهوستل مجانية؛ حضّر سبريتز بسيطًا من بروسيكو السوبرماركت وشارك الوجبات الخفيفة.","اطلع على لوحة إعلانات الهوستل؛ وإذا خرجت فاذهب متأخرًا عندما تكون التذكرة أقل سعرًا واحمل الهوية."]}],"day1Title":"يوم واحد: أساسيات بوسيتانو","day1":["الصباح: إطلالات سبياجا غرانده، ثم المسار الساحلي إلى فورنيلو للسباحة.","الغداء: بيتزا بالتقطيع أو بانيني قرب كيييزا نوفا؛ تناولها على التراسات.","الغروب: فيا كريستوفورو كولومبو أو تراس الهوستل."],"day2Title":"يومان: أضف درب الآلهة","day2":["الصباح: %LINK:pathOfTheGodsNocelle|Sentiero degli Dei / Path of the Gods% (حافلة إلى نوتشيليه، ثم مشي نزولًا إلى بوسيتانو).","بعد الظهر: %LINK:fornilloBeachGuide|تبريد على شاطئ فورنيلو% أو غداء متأخر في البلدة."],"day3Title":"ثلاثة أيام: كابري أو أمالفي + رافيللو","day3":["الخيار A: %LINK:capriDayTrip|رحلة يومية إلى كابري% - اركب أول عبّارة، خطط حلقة مشي DIY واحمل وجبات خفيفة.","الخيار B: %LINK:positanoRavello|أمالفي + رافيللو% - حافلة أو عبّارة إلى أمالفي ثم الصعود إلى فيلا روفولو أو فيلا تشيمبروني."],"savingsTitle":"نصائح لتوفير المال","savings":["سافر في مواسم الكتف (أبريل-مايو، أواخر سبتمبر) لأسعار أقل وحشود أخف.","استخدم حافلات SITA لمعظم التنقلات؛ أضف عبّارة واحدة إذا أردت الإطلالة.","أقم في العنابر أو شارك غرفة قرب كيييزا نوفا لتقليل رسوم الحمالين والدرج.","املأ زجاجات الماء في الهوستل أو من النوافير العامة قبل الخروج.","إذا حجزت مباشرة، استخدم الإفطار والشراب المسائي المجانيين لتغطية وجبة.","امشِ بين نقاط المشاهدة بدل الحافلة؛ الدرج مجاني ومشاهدته جميلة."],"foodTitle":"أطعمة منخفضة التكلفة","foodText":"فكر ببيتزا بالتقطيع، بانيني، وسلطات الديلي حول Via dei Mulini وكييزا نوفا. اشترِ من المتاجر الصغيرة للبيكنيك واترك الوجبات الجالسة لمرّة واحدة فقط.","transportTitle":"نصائح المواصلات","transportCompareLabel":"قارن المسارات:","transportCompareLinks":[{"key":"naplesPositano","label":"نابولي → بوسيتانو"},{"key":"salernoPositano","label":"ساليرنو → بوسيتانو"}],"transportFerryPrefix":"تحقق من ","transportFerryLinkLabel":"جداول العبّارات","transportFerrySuffix":" والطقس؛ الحافلات أرخص.","faqs":[{"q":"ما أفضل خطة ليوم واحد؟","a":["ابدأ في سبياجا غرانده، سر عبر المسار الساحلي إلى فورنيلو، ثم شاهد الغروب من فيا كريستوفورو كولومبو أو تراس الهوستل."]},{"q":"كيف أقلل تكاليف المواصلات؟","a":["استخدم حافلات SITA لمعظم الرحلات وأضف عبّارة واحدة للمنظر؛ اشترِ التذاكر قبل الصعود لتجنب المفاجآت."]},{"q":"ما برنامج اليومين؟","a":["اليوم الأول يغطي أساسيات بوسيتانو؛ اليوم الثاني يضيف درب الآلهة عبر نوتشيليه مع استراحة شاطئية."]},{"q":"وماذا عن خطة ثلاثة أيام؟","a":["اختر واحدًا: رحلة يومية إلى كابري أو مزيج أمالفي + رافيللو؛ اخرج مبكرًا واحمل وجبات خفيفة لتوفير المال."]}]}')}}]);