"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[135084],{135084:t=>{t.exports=JSON.parse('{"seo":{"title":"Sentiero degli Dei (देवताओं का पथ) गाइड","description":"होस्टल से Sentiero degli Dei ट्रेल तक पहुँचने का सटीक मार्ग, परिवहन विकल्प और सुरक्षा टिप्स।"},"linkLabel":"ब्रिकेट से देवताओं का पथ","intro":["सेंटिएरो डिगली देई (देवताओं का पथ) चट्टान की चोटी की छतों, शाहबलूत के पेड़ों और अमाल्फी तट के व्यापक दृश्यों को एक साथ जोड़ता है। हॉस्टल ब्रिकेट से आप किसी महंगे दौरे में शामिल हुए बिना स्थानीय फ़ेरी और एसआईटीए बसों को मिलाकर ट्रेल तक पहुँच सकते हैं।","हॉस्टल से तीन मुख्य मार्ग शुरू होते हैं: अमाल्फी के लिए नौकायन और बोमेरानो तक बस चलाना, पूरे रास्ते बस नेटवर्क की सवारी करना, या स्थानान्तरण को छोड़ना और नोसेले से बाहर-और-वापस लंबी पैदल यात्रा करना। इस अवलोकन का उपयोग यह तय करने के लिए करें कि कौन सा विस्तृत मार्ग आपके समय, बजट और ऊंचाई के साथ आराम के लिए उपयुक्त है।"],"sections":[{"id":"highlights","title":"मुख्य बातें एक नज़र में","body":["नयनाभिराम बालकनियाँ पोसिटानो, प्राइआनो और कैपरी के ऊपर तैरती हैं, इसलिए छोटे खंड भी आपको नाटकीय फोटो स्टॉप के साथ पुरस्कृत करते हैं।","ग्रेडिएंट तकनीकी के बजाय स्थिर हैं; आत्मविश्वास से भरे पैदल यात्री अच्छे जूते और भरपूर पानी के साथ रास्ता तय कर सकते हैं।","सार्वजनिक परिवहन निजी ड्राइवरों या निर्देशित भ्रमणों की तुलना में लागत कम रखता है, जिससे आपको यात्रा के बाद की दावतों पर पैसा खर्च करने का मौका मिलता है।"]},{"id":"considerations","title":"इससे पहले कि आप प्रतिबद्ध हों","body":["खुले किनारों, रेलिंग-मुक्त सीढ़ियों और लंबी बूंदों की अपेक्षा करें। वर्टिगो से पीड़ित किसी भी व्यक्ति को सौम्य नोसेले का बार-बार समर्थन करना चाहिए।","आपके मार्ग के आधार पर घर-घर यात्रा में 3-5 घंटे लगते हैं। गर्मी की तपिश के कारण समय से पहले शुरुआत करना या दोपहर में देर से प्रस्थान करना आवश्यक हो जाता है।"]},{"id":"route-options","title":"हॉस्टल ब्रिकेट से मार्ग विकल्प","body":["अमाल्फी के माध्यम से नौका: स्पिआगिया ग्रांडे तक पैदल चलें, अमाल्फी तक 25 मिनट की दूरी तय करें, बोमेरानो तक बस से चढ़ें, नोसेले तक पैदल चलें और शटल से वापसी करें। सबसे लंबा दिन लेकिन अमाल्फी को देखने के लिए समय के साथ समुद्र के दृश्य भी मिलते हैं।","अमाल्फी के माध्यम से बस: बार इंटरनजियोनेल में टिकट खरीदें, सीधे अमाल्फी के लिए एसआईटीए बस लें, एगेरोला सेवा से जुड़ें, फिर क्लासिक बोमेरानो → नोसेले ट्रैवर्स का पालन करें। आदर्श तब जब समुद्र अशांत हो या नौकाएँ बिक जाएँ।","डायरेक्ट नोसेले लूप: मोंटेपर्टुसो-नोसेले मिनीबस पकड़ें, कोले सेरा की ओर पैनोरमिक सेक्शन से बाहर और पीछे चलें, और उसी बस से वापस लौटें। यदि आप हल्का दिन चाहते हैं या तंग स्थानांतरण खिड़कियों से बचना चाहते हैं तो बिल्कुल सही।"]},{"id":"trail-basics","title":"ट्रेल मूल बातें","body":["सेंटिएरो डिगली देई बोमेरानो और नोसेले को लगभग 6 किलोमीटर के उतार-चढ़ाव वाले रास्ते पर लगभग 200 मीटर की ऊंचाई में बदलाव के साथ जोड़ता है।","वेमार्क लाल और सफेद सीएआई धारियां और लकड़ी के \\"सेंटीरो\\" तीर दिखाते हैं। पक्की सीढ़ियाँ, खचाखच भरी गंदगी और चट्टानी किनारों के बीच वैकल्पिक सतहें - घिसे-पिटे जूते पैक करें और अपने हाथों को मुक्त रखें।"]},{"id":"planning","title":"योजना युक्तियाँ","body":["एक रात पहले पूर्वानुमान की जाँच करें; बारिश से पत्थर की सीढ़ियाँ चिकनी हो जाती हैं और तेज़ हवाएँ नौकाओं को रोक सकती हैं।","प्रति व्यक्ति कम से कम 1.5 लीटर पानी साथ रखें। रिफिल बोमेरानो में कैफे, नोसेले में कियोस्क और कभी-कभार फार्महाउस स्टैंड तक सीमित हैं।","बोर्डिंग से पहले एसआईटीए बस टिकटों को मान्य करें और अप्रत्याशित चक्करों के लिए अपने डेपैक में एक अतिरिक्त टिकट रखें।"]}],"essentialsTitle":"अनिवार्य","essentials":["पकड़ के साथ-साथ धूप से सुरक्षा के साथ आरामदायक जूते और उजागर रिजलाइन के लिए परतें।","आप जिस प्रत्येक चरण पर सवारी करने की योजना बना रहे हैं, उसके लिए पहले से खरीदे गए एसआईटीए बस टिकट (टैबाची या बार इंटरनजियोनेल)।","मोबाइल डेटा बीहड़ों में गिरने की स्थिति में ऑफ़लाइन मानचित्र या सहेजे गए दिशा-निर्देश।"],"typicalCostsTitle":"विशिष्ट लागत","typicalCosts":["एसआईटीए बस पोसिटानो ⇄ अमाल्फी/बोमेरानो/नोसेले: टिकट के प्रकार के आधार पर प्रति पैर €2-€4।","ट्रैवेलमर या लुसीबेलो फ़ेरी पोसिटानो ⇄ अमाल्फ़ी: पीक सीज़न में हर तरफ़ से लगभग €12-€16।","गाँव की दुकानों से ट्रेल स्नैक्स और पानी: €3–€8; पथ तक पहुँच स्वयं निःशुल्क है।"],"tipsTitle":"स्मार्ट टाइमिंग","tips":["जुलाई-अगस्त में 09:00 बजे से पहले निकलें या ठंडे तापमान और नरम रोशनी के लिए वसंत और शरद ऋतु में सुनहरे घंटों का लक्ष्य रखें।","जब तक आपके घुटने तरोताजा न हों, पोसिटानो तक 1,700 सीढ़ियाँ उतरने से बचें - लंबी पैदल यात्रा के बाद मोंटेपर्टुसो सीढ़ियाँ उतरना क्रूर है।","एक हल्के डेपैक का उपयोग करें और जब तक आप ऊंची सीढ़ियों तक नहीं पहुंच जाते, तब तक ट्रैकिंग पोल्स को मोड़कर रखें।"],"steps":[{"name":"अपना मार्ग चुनें","text":"मौसम, नौका कार्यक्रम और ऊर्जा स्तर के आधार पर अमाल्फी के माध्यम से नौका, अमाल्फी के माध्यम से बस, या सीधे नोसेले विकल्प के बीच चयन करें।"},{"name":"सुरक्षित परिवहन टिकट","text":"यदि आप नौकायन की योजना बना रहे हैं तो बार इंटरनजियोनेल से एसआईटीए बस टिकट और बंदरगाह पर नौका टिकट खरीदें।"},{"name":"ट्रेलहेड की यात्रा करें","text":"पूरी यात्रा के लिए बोमेरानो तक या छोटे आउट-एंड-बैक सेक्शन के लिए नोसेले तक सवारी करें।"},{"name":"हाइक सेंटिएरो डिगली देई","text":"मनोरम छतों के बीच सीएआई मार्करों का पालन करें, फोटो और स्नैक स्टॉप के लिए खुद को गति दें।"},{"name":"पोसिटानो को लौटें","text":"चिएसा नुओवा के लिए वापस बस पकड़ें या केवल तभी चलें जब आप जानबूझकर लंबी सीढ़ी से उतरना चाहते हों।"}],"faqsTitle":"प्रश्नोत्तर","faq":[{"q":"घर-घर जाने में कितना समय लगता है?","a":"परिवहन सहित बजट 3-5 घंटे। नौका मार्ग सबसे लंबा है; नोसेल आउट-एंड-बैक आधे दिन में फिट बैठता है।"},{"q":"क्या यह मार्ग शुरुआती लोगों के लिए उपयुक्त है?","a":"हां, बशर्ते आप अपने पैरों पर स्थिर हों और ऊंचाई के साथ सहज हों। यहां कोई तकनीकी चढ़ाई नहीं है, बस स्थिर चढ़ाई और अवरोह है।"},{"q":"यदि घाट रद्द कर दिए जाएं तो क्या होगा?","a":"अमाल्फी के माध्यम से बस में जाएँ या सीधे नोसेले तक जाएँ। हमेशा एक अतिरिक्त बस टिकट रखें ताकि आप समुद्र या मौसम की स्थिति के अनुकूल बन सकें।"}],"faqs":[{"q":"घर-घर जाने में कितना समय लगता है?","a":["परिवहन सहित बजट 3-5 घंटे। नौका मार्ग सबसे लंबा है; नोसेल आउट-एंड-बैक आधे दिन में फिट बैठता है।"]},{"q":"क्या यह मार्ग शुरुआती लोगों के लिए उपयुक्त है?","a":["हां, बशर्ते आप अपने पैरों पर स्थिर हों और ऊंचाई के साथ सहज हों। यहां कोई तकनीकी चढ़ाई नहीं है, बस स्थिर चढ़ाई और अवरोह है।"]},{"q":"यदि घाट रद्द कर दिए जाएं तो क्या होगा?","a":["अमाल्फी के माध्यम से बस में जाएँ या सीधे नोसेले तक जाएँ। हमेशा एक अतिरिक्त बस टिकट रखें ताकि आप समुद्र या मौसम की स्थिति के अनुकूल बन सकें।"]}],"galleryTitle":"फोटो गैलरी","gallery":{"items":[{"alt":"देवताओं के पथ के साथ देखें","caption":"पगडंडी के साथ"},{"alt":"नोसेले गांव के दृश्य","caption":"नोसेले दृश्य"}]},"toc":{"onThisPage":"इस पेज पर","essentials":"अनिवार्य","costs":"विशिष्ट लागत","tips":"स्मार्ट टाइमिंग","faqs":"प्रश्नोत्तर","gallery":"फोटो गैलरी"}}')}}]);