"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[148541],{148541:i=>{i.exports=JSON.parse('{"meta":{"title":"सोरेंटो से पोसिटानो बस द्वारा | हॉस्टल ब्रिकेट","description":"SITA बस से पोसिटानो (Chiesa Nuova) तक सोरेंटो से हॉस्टल ब्रिकेट पहुँचने के चरण: टिकट, समय-सारिणी सुझाव, और स्टॉप से हॉस्टल तक कैसे पहुँचे।"},"hero":{"eyebrow":"हॉस्टल ब्रिकेट ट्रैवल गाइड","title":"सोरेंटो से पोसिटानो — SITA बस","description":"सोरेंटो से पोसिटानो यात्रा करते समय इस गाइड का उपयोग करें। आप SITA Sud बस लेंगे और पोसिटानो (Chiesa Nuova) पर उतरेंगे, जो हॉस्टल के सबसे नज़दीक स्टॉप है। हम टिकट के मूल बातें, समय-सारिणी कैसे पढ़ें, और स्टॉप से हॉस्टल तक पहुँचने के तरीके कवर करते हैं।"},"tip":{"label":"सुझाव","copy":"सभी परिवहन विकल्प चाहिए? फेरी, टैक्सी जानकारी और विकल्पों के लिए <link>यहाँ कैसे पहुँचे अवलोकन</link> देखें।"},"sections":{"journey":{"title":"यात्रा का अवलोकन","body":"उच्च सीज़न में 1–1.5 घंटे का समय रखें। सोरेंटो और पोसिटानो के बीच बसें अक्सर चलती हैं। बस में चढ़ने से पहले टिकट खरीदें और सत्यापन के लिए तैयार रखें।"},"tickets":{"title":"टिकट और सत्यापन","body":"इस मार्ग पर SITA (SITA Sud) संचालित करती है। टिकट समाचार स्टैंड या तबाक्की से खरीदें। बस में चढ़ते समय चालक को टिकट दिखाएँ; यात्रा समाप्त होने तक इसे सँभाल कर रखें। जहाँ आवश्यक हो, उपयोग से पहले सत्यापित करें।"},"timetable":{"title":"समय-सारिणी पढ़ना","points":["पहले कॉलम की पंक्तियाँ मार्ग के प्रत्येक बस स्टॉप को सूचीबद्ध करती हैं।","स्तंभ अक्षरों से चिह्नित निर्धारित यात्राओं के अनुरूप होते हैं।","सोमवार से शनिवार यात्रा कर रहे हैं? स्तंभ “F” और “G” देखें।","रविवार या सार्वजनिक अवकाश पर यात्रा कर रहे हैं? स्तंभ “G” और “H” चुनें।"],"footer":"नवीनतम समय-सारिणी के लिए <link>Positano.com</link> देखें।"},"arrival":{"title":"पोसिटानो (Chiesa Nuova) पर आगमन","points":["पोसिटानो के लिए दो SITA स्टॉप सूचीबद्ध हैं: “Sponda” और “Chiesa Nuova”.","हॉस्टल के लिए, “Positano (Chiesa Nuova)” वाया जी. मार्कोनी पर सही स्टॉप है।","यह स्टॉप कैफ़े/बार “Bar Internazionale” के बाहर है।"],"image":{"src":"/img/directions/positano-bus-stop.png","alt":"बार इंटरनाज़ियोनाले के बाहर पोसिटानो कीज़ा नुओवा पर SITA बस स्टॉप","caption":"हॉस्टल तक सबसे आसान पैदल मार्ग के लिए “Positano — Chiesa Nuova” पर उतरें।"}},"walk":{"title":"हॉस्टल ब्रिकेट तक पैदल चलना","points":["वाया जी. मार्कोनी पर लगभग 100 मीटर (330 फीट) पैदल चलें।","दाईं ओर हमारी दूसरी मंज़िल की टैरेस के ऊपर लगे साइन को देखें।","हॉस्टल के मुख्य द्वार तक पहुँचने के लिए दाईं ओर सीढ़ियों से ऊपर जाएँ।"],"link":"मानचित्रित मार्ग पसंद है? हमारे <link>Chiesa Nuova → Hostel गाइड</link> का अनुसरण करें।"}}}')}}]);