"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[153252],{153252:t=>{t.exports=JSON.parse('{"seo":{"title":"Positano ऑन ए बजट","description":"भोजन, परिवहन और गतिविधियों पर लागत घटाने के व्यवहारिक तरीके, साथ ही मुफ़्त अनुभवों की सूची।"},"linkLabel":"Positano ऑन ए बजट","intro":["कार-फ्री ट्रांसपोर्ट, बजट भोजन और मुफ्त व्यूपॉइंट्स को जोड़कर अपने यूरो बचाएँ—Positano के हाइलाइट्स मिस किए बिना।"],"sections":[{"id":"transport","title":"सस्ते परिवहन विकल्प","body":["Sorrento के लिए Alibus या Curreri शटल लें, फिर Positano के लिए SITA बस; मार्क-अप से बचने के लिए tabacchi से बस टिकट ब्लॉक्स में खरीदें।","समुद्र शांत हो तो आराम के लिए एक फेरी लेग जोड़ें—केवल ज़रूरी सेगमेंट बुक करें ताकि ओवरपे न हो।"]},{"id":"stay","title":"स्मार्ट ठहराव","body":["Chiesa Nuova के पास हॉस्टल डॉर्म या शेयर अपार्टमेंट बुक करें ताकि पोर्टर फीस और सीढ़ियाँ कम हों।","लंबी यात्राओं के लिए Meta या Praiano में बेस पर विचार करें—कमीउट 30–40 मिनट है लेकिन पीक सीज़न में कमरे काफी सस्ते होते हैं।"]},{"id":"food","title":"भोजन और पेय में बचत","body":["सिट-डाउन ब्रंच की बजाय बार से panini, sfogliatelle और espresso लें; निकलने से पहले हॉस्टल नलों से 1 L (34 oz) बोतल भर लें।","antipasti शेयर करें, कैज़ुअल trattorias चुनकर कवर चार्ज से बचें, और सूर्यास्त पर डेली खरीद के साथ टेरेस पर पिकनिक करें।"]},{"id":"experiences","title":"कम लागत वाले अनुभव","body":["Path of the Gods (Nocelle तक बस) या Via Cristoforo Colombo पर सूर्यास्त वॉक जैसी मुफ्त हाइकिंग प्लान करें।","एक मुख्य एक्टिविटी पर खर्च करें—जैसे साझा बोट टूर या कुकिंग क्लास—और बाकी समय बीच डेज़ व व्यूपॉइंट्स से बैलेंस करें।"]}],"tips":["कम कीमतों और आसान फेरी सीट्स के लिए शोल्डर महीनों (अप्रैल–मई, लेट सितंबर) में जाएँ।","खर्च को नोट्स ऐप में ट्रैक करें; रोज़ साझा लागत बाँटने से ग्रुप अलाइन्ड रहता है।"],"faqs":[{"q":"मुझे कितना दैनिक बजट रखना चाहिए?","a":["€70–€90 में डॉर्म बेड, डेली भोजन, बस टिकट और एक पेड एक्टिविटी कवर होती है; प्राइवेट रूम या सनसेट ड्रिंक्स के लिए €20–€30 जोड़ें।"]},{"q":"क्या टिप देना अपेक्षित है?","a":["सेवा शामिल होती है; बेहतरीन सेवा पर €1–€2 राउंड अप करें, या अपस्केल जगहों पर केवल तब 10% दें जब अनुभव इसके योग्य लगे।"]}],"tocTitle":"इस पेज पर","faqsTitle":"अक्सर पूछे जाने वाले सवाल","heroAlt":"टेरेस से Positano के बजट व्यूज़","costBreakdown":{"title":"दैनिक खर्च विभाजन","slices":[{"label":"बेड (डॉरम)","value":45},{"label":"भोजन और स्नैक्स","value":25},{"label":"परिवहन","value":12},{"label":"गतिविधियां और अतिरिक्त","value":18}]},"gallery":[{"alt":"Positano पर मुफ्त व्यूपॉइंट","caption":"मुफ़्त व्यूपॉइंट"},{"alt":"Positano व्यू के साथ पिकनिक","caption":"भोजन बचाने के लिए पिकनिक"}]}')}}]);