"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[157515],{157515:n=>{n.exports=JSON.parse('{"seo":{"title":"Bản dịch đang được chuẩn bị.","description":"Lộ tr\xecnh ba l\xf4 kết hợp Naples, Amalfi Coast v\xe0 c\xe1c điểm l\xe2n cận."},"linkLabel":"Du lịch bụi miền Nam nước \xdd: Tuyến đường bao gồm. Amalfi","intro":["L\xean kế hoạch v\xf2ng quanh miền Nam \xdd, giữ Positano l\xe0m t\xe2m điểm m\xe0 vẫn th\xe2n thiện ng\xe2n s\xe1ch."],"sections":[{"id":"route","title":"Lộ tr\xecnh gợi \xfd","body":["Bắt đầu ở Naples v\xec thuận tiện chuyến bay v\xe0 t\xe0u, ở 2–3 đ\xeam tại Positano hoặc Praiano, tiếp tục tới Salerno rồi v\xf2ng v\xe0o đất liền thăm Matera hoặc Puglia trước khi quay lại.","D\xf9ng t\xe0u v\xf9ng v\xe0 xe kh\xe1ch cho những đoạn d\xe0i để tiết kiệm; đặt v\xe9 trước 4–6 tuần c\xf3 chỗ 15–25 €."]},{"id":"arrivals","title":"Đến nơi & h\xe0nh l\xfd","body":["Bản dịch đang được chuẩn bị.","Chuẩn bị quần \xe1o mau kh\xf4, khăn nhỏ gọn v\xe0 d\xe9p d\xf9ng trong ph\xf2ng tắm hostel; tiệm giặt ở Positano t\xednh 12–15 € cho một mẻ giặt sấy."]},{"id":"budget","title":"Mẹo ng\xe2n s\xe1ch","body":["Kết hợp mua đồ ở si\xeau thị (focaccia n\xf3ng, tr\xe1i c\xe2y, mozzarella) với picnic tr\xean s\xe2n thượng; đặt mục ti\xeau bữa trưa 8 € thay v\xec 18 € ở nh\xe0 h\xe0ng.","Đi chung chuyến trong ng\xe0y với bạn hostel: chia taxi tới điểm đầu đường m\xf2n hoặc thu\xea xe tay ga ở Salerno/Praiano, nơi gi\xe1 khoảng 35 € mỗi ng\xe0y."]}],"tips":["K\xedch hoạt SIM/eSIM v\xf9ng (TIM/Vodafone) để tra lịch xe bu\xfdt v\xe0 l\xe0m thủ tục hostel khi đang di chuyển.","Đặt trước chỗ ở cho đ\xeam đầu v\xe0 cuối h\xe0nh tr\xecnh, giữ linh hoạt giữa chuyến để xoay theo thời tiết."],"faqs":[{"q":"C\xf3 cần thu\xea \xf4 t\xf4 kh\xf4ng?","a":["Kh\xf4ng. T\xe0u, xe bu\xfdt v\xe0 ph\xe0 đủ cho cả v\xf2ng. Thu\xea xe tay ga cho qu\xe3ng ngắn kh\xe1 vui, nhưng giao th\xf4ng Amalfi đ\xf2i hỏi tay l\xe1i vững."]},{"q":"N\xean d\xe0nh bao nhi\xeau ng\xe0y cho Positano?","a":["Tối thiểu ba đ\xeam: một cho Positano, một cho Con Đường của C\xe1c Vị Thần hoặc tour thuyền, v\xe0 một ng\xe0y linh hoạt cho Amalfi/Ravello hoặc ph\xf2ng khi trời xấu."]}]}')}}]);