"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[160017],{160017:e=>{e.exports=JSON.parse('{"seo":{"title":"इटली में वर्क-एक्सचेंज अवसर","description":"होस्टल वर्क-एक्सचेंज का मूल्यांकन कैसे करें, अनुबंधों की जाँच और वैध घंटे समझें।"},"linkLabel":"इटली में वर्क-एक्सचेंज अवसर","intro":["इटली में वर्क एक्सचेंज सोच रहे हैं? जानें होस्टल/फार्म शिफ्ट्स कैसे बनाते हैं, कौन सा कागज़ात चाहिए, और भरोसेमंद प्रोग्राम कैसे चुनें।"],"sections":[{"id":"expect","title":"क्या उम्मीद करें","body":["आमतौर पर 20–25 घंटे हाउसकीपिंग, रिसेप्शन सपोर्ट या सोशल होस्टिंग के बदले में बेड और ब्रेकफास्ट मिलता है।","शिफ्ट्स अक्सर पाँच दिनों में बँटी होती हैं, जिससे दो पूरे दिन घूमने को मिलते हैं। कमिट करने से पहले रोटा लिखित में कन्फर्म करें।"]},{"id":"vet","title":"होस्ट की जाँच","body":["Workaway, Worldpackers और होस्टल Facebook ग्रुप्स पर रिव्यू पढ़ें। हालिया फीडबैक और स्पष्ट टास्क लिस्ट वाले होस्ट चुनें।","अकॉमोडेशन सेटअप (डॉर्म vs प्राइवेट), कर्फ्यू, और क्या भोजन/स्टाफ डिस्काउंट शामिल हैं, पूछें।"]},{"id":"paperwork","title":"कागज़ात और नियम","body":["EU, UK, Canada, Australia और US के नागरिक पर्यटक के रूप में Schengen में 90 दिनों तक रह सकते हैं—वर्क एक्सचेंज को स्वैच्छिक ही रहना चाहिए।","वॉलंटियरिंग और अनौपचारिक काम को कवर करने वाला ट्रैवल इंश्योरेंस रखें; कुछ होस्ट आगमन पर liability waiver मांगते हैं।"]}],"tips":["होस्ट के साथ वीडियो कॉल शेड्यूल करें ताकि जिम्मेदारियाँ स्पष्ट हों और स्पेस देख सकें, फिर फ्लाइट्स बुक करें।","पहली शिफ्ट से पहले एक दिन जल्दी पहुँचें ताकि बस सकें, हाउस रूल्स सीखें और एडजस्ट कर सकें।"],"faqs":[{"q":"क्या मुझे पॉकेट मनी मिलती है?","a":["ज्यादातर इटालियन एक्सचेंज केवल रूम‑एंड‑बोर्ड देते हैं। कुछ बार शिफ्ट या एक्सकर्शन से टिप्स मिल सकती हैं, लेकिन इन्हें बोनस मानें, तय आय नहीं।"]},{"q":"क्या मैं वर्क एक्सचेंज पर 90 दिनों से ज्यादा रुक सकता/सकती हूँ?","a":["नहीं। Schengen सीमा का पालन करना होगा जब तक कि आपको वर्क वीज़ा या स्टूडेंट परमिट न मिल जाए। आगे की यात्रा या Schengen रीसेट प्लान करें।"]}]}')}}]);