"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[168794],{168794:n=>{n.exports=JSON.parse('{"seo":{"title":"Bản dịch đang được chuẩn bị.","description":"Hướng dẫn từng bước tới Pompeii từ Positano bằng xe bu\xfdt + t\xe0u hoặc ph\xe0 + t\xe0u (m\xf9a h\xe8)."},"linkLabel":"Positano to Pompeii: how to go","intro":["Pompeii rộng lớn, ấn tượng v\xe0 rất dễ l\xean lịch qu\xe1 tải. Lộ tr\xecnh n\xe0y gi\xfap bạn thấy những điểm ch\xednh m\xe0 kh\xf4ng phải d\xe0nh cả ng\xe0y cho việc di chuyển.","M\xf9a h\xe8 sẽ c\xf3 nắng gắt, bụi v\xe0 đ\xf4ng người — h\xe3y mang theo nước, kem chống nắng v\xe0 x\xe1c định thực tế bạn c\xf3 thể tham quan được bao nhi\xeau."],"toc":[{"href":"#at-a-glance","label":"T\xf3m tắt nhanh"},{"href":"#entry-cost-and-hours","label":"Gi\xe1 v\xe9 & giờ mở cửa"},{"href":"#other-facilities","label":"Tiện \xedch tại chỗ"},{"href":"#getting-there","label":"C\xe1ch đi"},{"href":"#self-guided-tour","label":"Lộ tr\xecnh tự do"},{"href":"#faqs","label":"C\xe2u hỏi thường gặp"}],"sections":[{"id":"at-a-glance","title":"T\xf3m tắt nhanh","body":["Ngay khi bước v\xe0o bạn sẽ cảm nhận được quy m\xf4 khổng lồ của Pompeii; gần như to\xe0n bộ th\xe0nh phố đ\xe3 được khai quật v\xe0 c\xf3 thể đi bộ dễ d\xe0ng.","Nếu tự kh\xe1m ph\xe1, chi ph\xed kh\xe1 thấp — bối cảnh lịch sử vẫn hiện l\xean r\xf5 r\xe0ng ngay cả khi kh\xf4ng c\xf3 hướng dẫn vi\xean ri\xeang.","Herculaneum nhỏ hơn v\xe0 được bảo tồn tốt hơn, n\xean c\xe2n nhắc nếu bạn muốn thấy c\xe1c bức b\xedch họa v\xe0 khảm c\xf2n nguy\xean vẹn thay v\xec những con phố trải d\xe0i của Pompeii.","Đi từ Positano bằng phương tiện c\xf4ng cộng nghĩa l\xe0 phải chịu nắng n\xf3ng, bụi v\xe0 thời gian di chuyển d\xe0i; nhớ dự tr\xf9 trong kế hoạch."]},{"id":"entry-cost-and-hours","title":"Gi\xe1 v\xe9 & giờ mở cửa","body":["Gi\xe1 v\xe9 v\xe0 giờ mở cửa thay đổi theo m\xf9a — h\xe3y kiểm tra trang ch\xednh thức của Parco Archeologico di Pompei trước khi khởi h\xe0nh.","V\xe9 ti\xeau chuẩn thường ở mức hơn 15 euro một ch\xfat; thời gian v\xe0o cửa cuối c\xf9ng dao động từ cuối buổi chiều đến đầu buổi tối t\xf9y th\xe1ng."]},{"id":"other-facilities","title":"Tiện \xedch tại chỗ","body":["Nhận bản đồ miễn ph\xed tại quầy th\xf4ng tin b\xean cạnh quầy v\xe9; bản đồ gi\xfap bạn định hướng trong khu di t\xedch dễ d\xe0ng hơn nhiều.","Ngay cổng v\xe0o c\xf3 cho thu\xea audio guide v\xe0 c\xf3 thể thu\xea hướng dẫn vi\xean được cấp ph\xe9p nếu muốn nghe giải th\xedch chi tiết.","C\xf3 quầy đồ ăn nhẹ nhưng số lượng hạn chế — đặc biệt v\xe0o m\xf9a n\xf3ng h\xe3y chủ động mang đủ nước.","Bạn c\xf3 thể gửi h\xe0nh l\xfd c\xf3 ph\xed tại ga Pompeii Scavi hoặc gửi miễn ph\xed ở ph\xf2ng k\xfd gửi ngay b\xean trong cổng."]},{"id":"getting-there","title":"C\xe1ch đi","body":["Từ Positano, bắt xe bu\xfdt SITA tới Sorrento; mua v\xe9 ở quầy tabacchi hoặc tr\xean xe, thời gian di chuyển khoảng một tiếng.","Đến Sorrento, đi l\xean cầu thang tới s\xe2n ga Circumvesuviana v\xe0 l\xean t\xe0u địa phương tới ‘Pompei Scavi – Villa dei Misteri’.","Ra khỏi ga rẽ phải, đi chưa tới một ph\xfat l\xe0 tới cổng Porta Marina Superiore ở ph\xeda b\xean tr\xe1i."]},{"id":"self-guided-tour","title":"Lộ tr\xecnh tự do","body":["Vừa qua cổng h\xe3y rẽ tr\xe1i tới quầy th\xf4ng tin để lấy bản đồ v\xe0 gửi h\xe0nh l\xfd nếu cần, sau đ\xf3 quay lại quầy đối diện để mua v\xe9.","B\xean trong, h\xe3y x\xe1c định vị tr\xed Porta Marina Superiore tr\xean bản đồ — bạn đang đứng ở Via Marina với bảng chỉ dẫn r\xf5 r\xe0ng trước mặt.","Đi theo Via Marina về ph\xeda đấu trường, rẽ v\xe0o c\xe1c ng\xf4i nh\xe0, nh\xe0 tắm hay quảng trường bạn quan t\xe2m rồi quay lại trục ch\xednh.","Sau khi tham quan đấu trường, đi qua nửa dưới của khu di t\xedch để quay về v\xe0 tranh thủ xem th\xeam c\xe1c điểm nổi bật kh\xe1c m\xe0 kh\xf4ng phải v\xf2ng lại đường cũ.","Tuyến n\xe0y gi\xfap bạn bao qu\xe1t khoảng 60–80% điểm quan trọng nhanh hơn nhiều so với việc cố gắng đi hết mọi con phố."]}],"howTo":{"steps":[{"name":"Xe bu\xfdt tới Sorrento","text":"Bắt xe bu\xfdt SITA từ Positano đến ga trung t\xe2m Sorrento (khoảng 60 ph\xfat)."},{"name":"T\xe0u tới Pompeii Scavi","text":"L\xean s\xe2n ga Circumvesuviana v\xe0 đi t\xe0u đến ‘Pompei Scavi – Villa dei Misteri’."},{"name":"Đi bộ tới cổng","text":"Ra khỏi ga rẽ phải; Porta Marina Superiore nằm b\xean tr\xe1i c\xe1ch khoảng 50 m\xe9t."}]},"tipsTitle":"Mẹo trong ng\xe0y","tips":["Nhận bản đồ miễn ph\xed trước khi qua cổng so\xe1t v\xe9 để lập kế hoạch rẽ nh\xe1nh m\xe0 kh\xf4ng phải quay lại.","Mang đủ nước v\xe0 tranh thủ tiếp nước khi gặp v\xf2i phun — m\xf9a h\xe8 quầy b\xe1n h\xe0ng thường c\xf3 h\xe0ng d\xe0i.","Ghi ch\xfa lại những g\xf3c chụp đẹp hoặc b\xedch họa muốn xem lại l\xfac ra về để khỏi lỡ v\xec trời n\xf3ng."],"warningsTitle":"Lưu \xfd quan trọng","warnings":["V\xe0o m\xf9a cao điểm, h\xe0ng chờ xe bu\xfdt SITA v\xe0 t\xe0u Circumvesuviana c\xf3 thể khiến mỗi chặng chậm th\xeam 20–30 ph\xfat; h\xe3y chừa thời gian dự ph\xf2ng.","Phần lớn khu di t\xedch ngo\xe0i trời v\xe0 \xedt b\xf3ng m\xe1t, v\xec vậy nếu bạn nhạy cảm với nhiệt h\xe3y tr\xe1nh đi v\xe0o giữa trưa."],"faqsTitle":"C\xe2u hỏi thường gặp","faqs":[{"q":"Cổng n\xe0o tiện nhất?","a":["Porta Marina Superiore ngay cạnh ga Pompei Scavi thuận tiện nhất cho kh\xe1ch tự t\xfac — c\xe1ch t\xe0u v\xe0i bước v\xe0 ph\xf2ng gửi đồ nằm ngay b\xean cạnh."]},{"q":"C\xf3 thể tự đi m\xe0 kh\xf4ng cần hướng dẫn vi\xean kh\xf4ng?","a":["Được. Bản đồ ch\xednh thức miễn ph\xed rất chi tiết v\xe0 hầu hết c\xe1c c\xf4ng tr\xecnh ch\xednh đều c\xf3 bảng th\xf4ng tin tiếng \xdd v\xe0 tiếng Anh. Nếu muốn nghe giải th\xedch s\xe2u hơn, bạn c\xf3 thể thu\xea audio guide hoặc hướng dẫn vi\xean được cấp ph\xe9p."]},{"q":"Gửi h\xe0nh l\xfd ở đ\xe2u?","a":["H\xe0nh l\xfd nhỏ c\xf3 thể gửi mất ph\xed tại ga Pompei Scavi, c\xf2n ngay sau cổng v\xe0o cũng c\xf3 ph\xf2ng k\xfd gửi miễn ph\xed."]}]}')}}]);