"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[177554],{177554:n=>{n.exports=JSON.parse('{"seo":{"title":"Huỷ ph\xe0 v\xe0 khi biển động","description":"L\xe0m g\xec khi ph\xe0 huỷ v\xec thời tiết, phương \xe1n thay thế v\xe0 c\xe1ch kiểm tra trạng th\xe1i."},"linkLabel":"Huỷ ph\xe0 v\xe0 khi biển động","intro":["Biển động c\xf3 thể khiến ph\xe0 đi/đến Positano bị hủy đột ngột.","Lu\xf4n chuẩn bị phương \xe1n di chuyển đường bộ v\xe0 theo d\xf5i th\xf4ng b\xe1o trực tiếp để kịp chuyến tiếp theo."],"sections":[{"id":"monitoring","title":"Theo d\xf5i cập nhật trực tiếp","body":["Xem website v\xe0 ứng dụng của h\xe3ng (Travelmar, NLG, Alilauro) c\xf9ng bảng điện tử tại bến mỗi 30 ph\xfat khi trời gi\xf3.","Bật th\xf4ng b\xe1o SMS/email hoặc theo d\xf5i k\xeanh Telegram/mạng x\xe3 hội địa phương để nhận tin hủy d\xf9 bạn rời bến."]},{"id":"plan-b","title":"Chuyển sang đường bộ","body":["Bản dịch đang được chuẩn bị.","Chừa \xedt nhất 60 ph\xfat dự ph\xf2ng cho chuyến cuối ng\xe0y v\xe0 nhớ k\xedch hoạt v\xe9 trước khi l\xean xe."]},{"id":"refunds","title":"Ho\xe0n tiền & phiếu t\xedn dụng","body":["Giữ h\xf3a đơn v\xe0 chụp m\xe0n h\xecnh th\xf4ng b\xe1o hủy để h\xe3ng xử l\xfd ho\xe0n tiền, đổi chuyến hoặc cấp voucher.","Trao đổi với nh\xe2n vi\xean trước khi rời quầy xem bạn muốn chuyến kế tiếp hay nhận phiếu sử dụng m\xf9a sau."]},{"id":"overnight","title":"Xử l\xfd khi phải qua đ\xeam","body":["B\xe1o cho nơi lưu tr\xfa hoặc đơn vị tour ngay khi c\xf3 th\xf4ng b\xe1o trễ; nhiều nơi sẽ dời giờ nhận ph\xf2ng hoặc khởi h\xe0nh nếu biết trước.","Hỏi quầy th\xf4ng tin bến về taxi gh\xe9p, xe thay thế hoặc gửi h\xe0nh l\xfd nếu bạn phải chờ tới s\xe1ng h\xf4m sau."]}],"tips":["Theo d\xf5i dự b\xe1o h\xe0ng hải — gi\xf3 tr\xean 20 hải l\xfd/giờ hoặc s\xf3ng vượt 1,5 m\xe9t thường khiến ph\xe0 bị hủy.","Bảo hiểm du lịch c\xf3 điều khoản chậm do thời tiết gi\xfap bạn nhận lại chi ph\xed kh\xe1ch sạn hoặc xe thay thế.","Chuyến buổi s\xe1ng đ\xe1ng tin cậy nhất; đừng tr\xf4ng chờ v\xe0o chuyến cuối ng\xe0y khi dự b\xe1o c\xf3 b\xe3o."],"faqs":[{"q":"L\xe0m sao biết khi ph\xe0 chạy lại?","a":["Tải lại website hoặc k\xeanh mạng x\xe3 hội của h\xe3ng v\xe0 nghe loa tại bến — quyết định thường được th\xf4ng b\xe1o trong v\xf2ng 30 ph\xfat so với giờ khởi h\xe0nh dự kiến.","Nh\xe2n vi\xean quầy v\xe9 cũng c\xf3 thể x\xe1c nhận chuyến kế tiếp trước khi bạn rời bến."]},{"q":"V\xe9 của t\xf4i được xử l\xfd thế n\xe0o?","a":["Xuất tr\xecnh h\xf3a đơn hoặc m\xe3 QR để xin ho\xe0n tiền, đổi sang voucher hoặc chuyển sang chuyến tiếp theo.","Nếu đặt qua đại l\xfd trực tuyến, h\xe3y gửi email k\xe8m ảnh chụp th\xf4ng b\xe1o hủy trong 24 giờ để họ bắt đầu ho\xe0n tiền."]},{"q":"C\xf3 ph\xe0 n\xe0o đảm bảo chạy khi biển động kh\xf4ng?","a":["Kh\xf4ng — khi lực lượng tuần duy\xean đ\xf3ng cảng, mọi chuyến đều dừng. H\xe3y chuẩn bị sẵn tuyến đường bộ.","Một v\xe0i chuyến charter tư nh\xe2n c\xf3 thể chạy khi s\xf3ng vừa phải nhưng chi ph\xed cao v\xe0 t\xf9y v\xe0o quyết định của thuyền trưởng; c\xe2n nhắc kỹ giữa sự thoải m\xe1i v\xe0 gi\xe1 tiền."]}]}')}}]);