"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[178874],{178874:n=>{n.exports=JSON.parse('{"seo":{"title":"Truyền thuyết & d\xe2n gian Amalfi Coast","description":"Những c\xe2u chuyện về n\xe0ng ti\xean c\xe1, th\xe1nh nh\xe2n v\xe0 thủy thủ gắn với địa danh ng\xe0y nay."},"linkLabel":"Truyền thuyết & d\xe2n gian Amalfi Coast","intro":["Kh\xe1m ph\xe1 truyền thuyết Amalfi Coast định h\xecnh lễ hội, tượng đ\xe0i v\xe0 những c\xe2u chuyện d\xe2n gian địa phương."],"sections":[{"id":"saint","title":"Th\xe1nh Andrew","body":["Nh\xe0 thờ ch\xednh t\xf2a Amalfi lưu giữ th\xe1nh t\xedch của Th\xe1nh Andrew; người d\xe2n tin ng\xe0i gi\xfap xoa dịu b\xe3o tố v\xe0 bảo vệ ngư d\xe2n.","Ng\xe0y 27/6 v\xe0 30/11, kiệu rước tượng th\xe1nh đi quanh thị trấn k\xe8m ph\xe1o hoa v\xe0 ban nhạc đồng."]},{"id":"sirens","title":"N\xe0ng ti\xean c\xe1 Li Galli","body":["Truyền thuyết kể rằng quần đảo Li Galli d\xf9ng tiếng h\xe1t m\xea hoặc thủy thủ; ng\xe0y nay c\xe1c tour thuyền vẫn kể c\xe2u chuyện khi đi ngang cụm đảo tư nh\xe2n n\xe0y.","Khi gh\xe9 Naples h\xe3y ch\xfa \xfd c\xe1c c\xe2u chuyện nhắc tới Parthenope v\xe0 n\xe0ng ti\xean c\xe1 — ch\xfang li\xean kết truyền thuyết của hai v\xf9ng."]},{"id":"traditions","title":"N\xe9t truyền thống c\xf2n sống","body":["Luminaria của Praiano v\xe0 nghi thức ch\xfac ph\xfac ngư d\xe2n ở Positano h\xf2a trộn t\xedn ngưỡng với nghi lễ thắp nến.","Gạch gốm thủ c\xf4ng thường vẽ h\xecnh th\xe1nh, chanh hoặc c\xe1c t\xedch biển — h\xe3y để \xfd tr\xean bậc thềm v\xe0 cửa nh\xe0."]}],"tips":["Kiểm tra lịch sự kiện của thị trấn để biết ng\xe0y lễ v\xe0 tham gia một c\xe1ch t\xf4n trọng.","Mang khăn cho\xe0ng mỏng; nhiều sự kiện nối tiếp nghi lễ trong nh\xe0 thờ n\xean trang phục k\xedn đ\xe1o lu\xf4n được hoan ngh\xeanh."],"faqs":[{"q":"Ai cũng c\xf3 thể tham gia rước kiệu chứ?","a":["C\xf3 — h\xe3y đứng b\xean đường, vỗ tay ch\xe0o v\xe0 nhường lối cho người d\xe2n khi\xeang tượng đi trước."]},{"q":"Nghe nhạc d\xe2n gian ở đ\xe2u?","a":["T\xecm c\xe1c buổi h\xf2a nhạc d\xe2n gian tại Atrani v\xe0 Ravello trong m\xf9a lễ hội h\xe8; văn ph\xf2ng du lịch c\xf4ng bố lịch hằng tuần."]}]}')}}]);