"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[18209],{18209:n=>{n.exports=JSON.parse('{"seo":{"title":"Xe bu\xfdt từ Hostel Brikette tới b\xe3i biển ch\xednh của Positano","description":"C\xe1ch đi xe bu\xfdt nội địa m\xe0u cam xuống dốc đến Piazza dei Mulini v\xe0 tản bộ đến Spiaggia Grande."},"linkLabel":"Bus down to Positano\'s main beach","intro":["Xe bu\xfdt m\xe0u cam Interno Positano nối Hostel Brikette với quảng trường Piazza dei Mulini, c\xe1ch Spiaggia Grande một qu\xe3ng đi bộ ngắn.","Thực hiện theo c\xe1c bước sau để mua v\xe9 tại tabacchi, đi xuống dốc v\xe0 đi bộ v\xe0o những ph\xfat cuối c\xf9ng."],"sections":[{"id":"reach-the-stop","title":"Đến điểm dừng Chiesa Nuova","body":["Ra khỏi kh\xe1ch sạn gi\xe1 rẻ, đi xuống cầu thang ph\xeda trước v\xe0 rẽ tr\xe1i dọc theo Viale Pasitea.","Sau khoảng 100 m\xe9t (330 feet), đi đến g\xf3c b\xean phải đầu ti\xean b\xean cạnh tabacchi/quầy b\xe1o — trạm xe bu\xfdt Chiesa Nuova nằm ngay b\xean ngo\xe0i.","Đi v\xe0o b\xean trong trước khi l\xean m\xe1y bay để mua v\xe9 Interno Positano trước; n\xf3 rẻ hơn v\xe0 tr\xe1nh sự chậm trễ tại cửa."]},{"id":"ride-the-bus","title":"Đi xe bu\xfdt Interno Positano xuống dốc","body":["L\xean xe bu\xfdt mini m\xe0u cam được đ\xe1nh dấu \\"Interno Positano\\". C\xe1c kiểu xe kh\xe1c nhau chạy tr\xean tuyến đường, nhưng điểm đến tr\xean biển b\xe1o điện tử vẫn giữ nguy\xean.","Tiếp tục đi khi xe bu\xfdt đi qua c\xe1c con đường b\xean sườn đồi của Positano v\xe0 đi xuống trung t\xe2m thị trấn."]},{"id":"piazza-dei-mulini","title":"Xuống tại Piazza dei Mulini","body":["Xuống xe tại Piazza dei Mulini, điểm dừng trung t\xe2m trong khu mua sắm gần hiệu thuốc.","Đ\xe2y l\xe0 điểm thấp nhất trong th\xe0nh phố m\xe0 c\xe1c phương tiện c\xf3 thể đến v\xe0 đưa bạn đến trong v\xf2ng v\xe0i ph\xfat từ bờ s\xf4ng."]},{"id":"final-walk","title":"Đi bộ đoạn đường cuối c\xf9ng đến b\xe3i biển","body":["Đi thẳng b\xean dưới cổng v\xf2m b\xean cạnh quảng trường v\xe0 đi theo lối đi dạo về ph\xeda cảng.","Trong v\xf2ng 3–4 ph\xfat, Spiaggia Grande mở cửa ở b\xean tr\xe1i của bạn — sử dụng %LINK:positanoMainBeachWalkDown|hướng dẫn đi bộ% nếu bạn muốn kiểm tra từng cầu thang."]}],"tipsTitle":"Lời khuy\xean về xe bu\xfdt","tips":["Mua v\xe9 interno tại tabacchi trước khi xếp h\xe0ng; mua h\xe0ng tr\xean t\xe0u c\xf3 gi\xe1 cao hơn một ch\xfat v\xe0 t\xe0i xế đ\xf4i khi b\xe1n hết v\xe9.","Chụp m\xe0n h\xecnh thời gian biểu từ quầy lễ t\xe2n hoặc nh\xe2n vi\xean trợ gi\xfap kỹ thuật số — c\xe1c dịch vụ buổi tối muộn mỏng đi trong m\xf9a vai.","Chuyển sang %LINK:positanoMainBeachWalkDown|tuyến đường đi bộ% nếu bạn muốn duỗi ch\xe2n tr\xean đường đi xuống."]}')}}]);