"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[2009],{2009:n=>{n.exports=JSON.parse('{"slug":"positano-amalfi-bus","meta":{"title":"Từ Positano đến Amalfi bằng xe bu\xfdt | Hostel Brikette","description":"Hướng dẫn từng bước đi xe bu\xfdt SITA từ Hostel Brikette đến Amalfi, gồm nơi mua v\xe9, điểm dừng Chiesa Nuova v\xe0 c\xe1ch đọc bảng giờ."},"header":{"eyebrow":"Cẩm nang di chuyển của Hostel Brikette","title":"Positano đến Amalfi – Xe bu\xfdt SITA","description":"D\xf9ng hướng dẫn n\xe0y để di chuyển su\xf4n sẻ từ Hostel Brikette tới Amalfi bằng xe bu\xfdt. Tuyến do SITA Sud vận h\xe0nh v\xe0 chạy thường xuy\xean v\xe0o m\xf9a cao điểm. Ch\xfang t\xf4i chỉ bạn nơi mua v\xe9, c\xe1ch đọc bảng giờ v\xe0 ch\xednh x\xe1c vị tr\xed l\xean xe tại Positano."},"tip":{"eyebrow":"Mẹo","copy":"Cần to\xe0n bộ lựa chọn di chuyển? Mở <link>Tổng quan C\xe1ch đến đ\xe2y</link> để xem ph\xe0, taxi v\xe0 c\xe1c h\xe0nh tr\xecnh kết hợp."},"sections":{"overview":{"title":"Tổng quan h\xe0nh tr\xecnh","body":"D\xe0nh khoảng 45–60 ph\xfat v\xe0o m\xf9a h\xe8 t\xf9y lưu lượng giao th\xf4ng v\xe0 h\xe0ng chờ. L\xean xe tại Positano (Chiesa Nuova) để đi thẳng tới Amalfi. Giờ cao điểm n\xean tới sớm để chắc c\xf3 chỗ ngồi."},"tickets":{"title":"SITA v\xe0 v\xe9 xe","body":"Tuyến xe do SITA (SITA Sud Srl) vận h\xe0nh. Mua v\xe9 trước tại sạp b\xe1o hoặc tiệm thuốc l\xe1 (tabacchi). Khi l\xean xe h\xe3y đưa v\xe9 cho t\xe0i xế để họ đ\xf3ng dấu hoặc x\xe9 kiểm so\xe1t. Giữ v\xe9 tới khi đến Amalfi."},"timetable":{"title":"C\xe1ch đọc bảng giờ SITA","body":"Bảng giờ ch\xednh thức liệt k\xea c\xe1c điểm dừng theo h\xe0ng v\xe0 chuyến xe theo cột chữ c\xe1i. Chọn h\xe0ng Positano v\xe0 Amalfi nằm c\xf9ng một cột chữ c\xe1i tương ứng với ng\xe0y bạn đi.","list":["Cột đầu ti\xean l\xe0 danh s\xe1ch từng điểm dừng tr\xean tuyến.","C\xe1c cột c\xf2n lại ứng với chuyến xe được g\xe1n chữ c\xe1i.","Đi từ thứ Hai tới thứ Bảy? D\xf9ng cột ‘F’ v\xe0 ‘G’.","Đi v\xe0o Chủ nhật hoặc ng\xe0y lễ? D\xf9ng cột ‘G’ v\xe0 ‘H’."],"link":"Xem lịch cập nhật tr\xean <link>Positano.com</link>.","linkHref":"https://www.positano.com/en/bus-schedule?route=amalfi_positano_sorrento#section-schedule-result"},"positanoStop":{"title":"Điểm dừng Positano (Chiesa Nuova)","list":["Điểm dừng SITA gần nhất l\xe0 Positano (Chiesa Nuova) tr\xean đường Via G. Marconi.","Điểm dừng c\xe1ch cửa hostel khoảng 140 m\xe9t (490 ft).","Bạn sẽ chờ b\xean ngo\xe0i qu\xe1n c\xe0 ph\xea/ba ‘Bar Internazionale’."],"image":{"alt":"Điểm dừng SITA Chiesa Nuova ở Positano ph\xeda trước Bar Internazionale","caption":"Đến sớm v\xe0i ph\xfat để đảm bảo c\xf3 chỗ; xe c\xf3 thể k\xedn nhanh v\xe0o m\xf9a h\xe8.","src":"/img/directions/positano-amalfi-bus-bus-stop.png"}},"walk":{"title":"Đi bộ từ hostel ra điểm dừng","list":["Rời hostel, đi xuống bậc thang ra đường ch\xednh rồi rẽ tr\xe1i.","Tiếp tục dọc Via G. Marconi khoảng 100 m\xe9t (330 ft).","Bar Internazionale nằm b\xean tr\xe1i; điểm dừng SITA ngay ph\xeda trước."]},"wait":{"title":"Mua v\xe9 v\xe0 chờ l\xean xe","list":["Mua v\xe9 xe bu\xfdt SITA tại qu\xe1n bar hoặc sạp b\xe1o/tabacchi gần đ\xf3.","Băng qua đường v\xe0 xếp h\xe0ng ph\xeda b\xean kia để đ\xf3n xe đi Amalfi.","Kiểm tra lại bảng hiển thị tr\xean xe ghi ‘Amalfi’."]},"arrival":{"title":"Đến Amalfi","body":"Hầu hết chuyến dừng tại bến xe Amalfi gần cảng. Từ đ\xe2y bạn c\xf3 thể chuyển sang Ravello v\xe0 Salerno, hoặc dạo bộ trung t\xe2m lịch sử Amalfi.","link":"Muốn l\xean lịch chiều về? Xem <link>Amalfi đến Positano bằng xe bu\xfdt</link>."}}}')}}]);