"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[219072],{219072:e=>{e.exports=JSON.parse('{"seo":{"title":"Naples शहर गाइड","description":"Naples में ठहरने, पिज़्ज़ा, संग्रहालय और Positano से पहले की व्यावहारिक तैयारियाँ।"},"linkLabel":"Naples सिटी गाइड","intro":["नेपल्स अमाल्फी तट का प्रवेश द्वार है। ऐतिहासिक गलियों, पिज़्ज़ा संस्थानों और आसान दिन-भर की यात्राओं को जानने के लिए एक-दो दिन यहाँ बिताएँ।"],"sections":[{"id":"neighbourhoods","title":"महत्त्वपूर्ण पड़ोस","body":["स्पाक्कानापोली में घूमकर चर्च, नटिविटी कार्यशालाएँ और सान लॉरेन्ज़ो के नीचे की भूमिगत टूर देखें।","चाइया के समुद्र तट और कास्टेल देल्ल\'ओवो की ओर जाएँ और शाम की ठंडी सैर का आनंद लें।"]},{"id":"food","title":"खाना और कॉफी","body":["सॉर्बिल्लो या दा मिकेले पर पिज़्ज़ा के लिए कतार लगाएँ (30–45 मिनट का इंतज़ार मानें)। ग्रान काफ़े गाम्ब्रिनुस में स्‍फोल्यातेला और एस्प्रेसो लें।","जब जल्दबाज़ी में कुछ चाहिए तो €6–€8 में कूप्पो फ्रित्तो (तले समुद्री भोजन से भरे कागज़ के शंकु) आज़माएँ।"]},{"id":"daytrips","title":"डे-ट्रिप की तैयारी","body":["नापोली चेंट्राले से रोम के लिए हाई-स्पीड ट्रेन (1 घंटा 10 मिनट) या सर्कुम्वेसुवियाना से पोम्पेई (40 मिनट) जाएँ।","यदि बाद में सीधे पोसितानो जा रहे हैं तो स्टेशन पर प्रति बैग €7 में सामान जमा करें।"]}],"tips":["मेट्रो यात्राओं के लिए कॉन्टैक्टलेस कार्ड साथ रखें और यदि कई सफ़र करने हैं तो डे पास (€5.60) खरीदें।","कीमती सामान आगे की जेब में रखें और भीड़भरी गलियों में क्रॉस-बॉडी बैग का उपयोग करें।"],"faqs":[{"q":"क्या नेपल्स को पैदल घूमना सुरक्षित है?","a":["हाँ, बड़े शहर जैसी सामान्य सावधानी रखें। रात में रोशनी वाली गलियों में रहें, अधिक नकदी न दिखाएँ और आधी रात के बाद आधिकारिक टैक्सी लें।"]},{"q":"मैं सामान कहाँ रख सकता हूँ?","a":["नापोली चेंट्राले और पियाज़ा गारीबाल्दी पर स्टाफ़ द्वारा संचालित स्टोरेज है। व्यस्त सप्ताहांत में जगह सुनिश्चित करने के लिए लॉकर ऑनलाइन बुक करें।"]}]}')}}]);