"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[219239],{219239:i=>{i.exports=JSON.parse('{"quickFilters":["positano","transport","porters","stairs","connectivity","laundry","ferry","bus","budgeting","free","event","photography","cuisine","itinerary","passes","train","off-season","winter","camping","cost","tips","work-life","souvenirs","comparison","insurance","hitchhiking","walking-tour","couples"],"labels":{"transport":"المواصلات","budgeting":"الميزانية","beaches":"الشواطئ","hiking":"مسارات المشي","photography":"التصوير","safety":"السلامة","connectivity":"الاتصال","cuisine":"المطبخ","itinerary":"المسارات","day-trip":"الرحلات اليومية","culture":"الثقافة","events":"الفعاليات","nightlife":"الحياة الليلية","shopping":"التسوق","accommodation":"الإقامة","hostel-life":"حياة الهوستل","faq":"الأسئلة الشائعة","logistics":"اللوجستيات","laundry":"الغسيل","porters":"الحمّالون","stairs":"درج أقل","cost":"التكاليف","tips":"نصائح","passes":"بطاقات","insurance":"التأمين","hitchhiking":"التنقل بالوقوف","walking-tour":"جولة سيرًا","camping":"التخييم","work-life":"العمل والسفر","souvenirs":"التذكارات","comparison":"مقارنة","boat":"القوارب","train":"القطارات","bus":"الحافلات","ferry":"العبّارات","car":"السيارات","free":"مجاني","planning":"التخطيط","decision":"اختيار","experiences":"تجارب","positano":"بوسيتانو","amalfi":"أمالفي","ravello":"رافيللو","sorrento":"سورينتو","praiano":"برايانو","capri":"كابري","ischia":"إيشيا","procida":"بروتشيدا","naples":"نابولي","salerno":"ساليرنو","tramonti":"ترامونتي","pompei":"بومبي","herculaneum":"هركولانيوم","winter":"الشتاء","summer":"الصيف","shoulder":"الموسم المتوسط","ferragosto":"فيرّاغوستو","off-season":"خارج الموسم"},"titles":{"transport":"أدلة المواصلات على ساحل أمالفي","budgeting":"بوسيتانو بميزانية محدودة","beaches":"شواطئ بوسيتانو","hiking":"مسارات المشي ونقاط المشاهدة","photography":"أماكن التصوير في بوسيتانو","connectivity":"الواي فاي وشرائح eSIM والتغطية","itinerary":"المسارات","day-trip":"الرحلات اليومية","events":"الفعاليات والمهرجانات","logistics":"الوصول واللوجستيات","laundry":"الغسيل في بوسيتانو","porters":"خدمات الحمّالين","stairs":"مسارات بدرج أقل","bus":"أدلة الحافلات وSITA","ferry":"أدلة العبّارات","car":"القيادة والمواقف","free":"مجاني ومنخفض التكلفة","decision":"أدلة اتخاذ القرار","planning":"أدلة التخطيط","experiences":"تجارب","safety":"السلامة على ساحل أمالفي","cuisine":"المطبخ المحلي","camping":"التخييم على الساحل","insurance":"تأمين السفر","passes":"بطاقات النقل","comparison":"مقارنة خيارات الوصول والقواعد","souvenirs":"التذكارات والتسوق","work-life":"العمل عن بُعد والمقاهي","positano":"أدلة بوسيتانو","amalfi":"أدلة أمالفي","ravello":"أدلة رافيللو","capri":"أدلة كابري","naples":"نابولي ↔ بوسيتانو","salerno":"ساليرنو ↔ بوسيتانو"},"descriptions":{"transport":"أدلة لاتخاذ القرار بين الحافلات والعبّارات والشاتل والنقل الخاص مع نصائح للأمتعة.","budgeting":"طرق ذكية لتقليل الإنفاق على المواصلات والطعام والأنشطة.","beaches":"مناطق عامة مقابل النوادي الشاطئية، وطرق الوصول، وملاحظات السلامة.","hiking":"مسار درب الآلهة وبدائل أكثر أمانًا مع الانتباه للدرج.","photography":"أفضل زوايا الالتقاط، والإضاءة، وآداب تجنب الازدحام.","connectivity":"آداب استخدام الواي فاي، السرعات المعتادة، تغطية eSIM، ونصائح احتياطية للعمل على الساحل.","itinerary":"مسارات يومية منطقية تقلل الالتفاف وتلائم الضوء/الموسم.","day-trip":"كابري وبومبي ورافيللو وأمالفي — توقيتات واقعية وخيارات العودة.","events":"كيفية حضور الفعاليات الموسمية: التذاكر، الأزياء، اللوجستيات، والعودة المتأخرة.","logistics":"الوصول مع أقل قدر من الدرج، وخيارات الحمّالين، وحلول الأمتعة الأذكى.","laundry":"مقارنة بين الخدمة الذاتية والتسليم، أوقات الذروة، الدفع بالبطاقات/العملات، وخيارات التجفيف.","porters":"متى تستعين بالحمّالين، وأين تلتقيهم، والمدى السعري المتوقع، والبدائل.","stairs":"مسارات ومواقف حافلات تقلل عدد الدرجات إلى الحد الأدنى.","bus":"التذاكر، التوثيق، آداب أوقات الذروة، وفارق موقفي كيييزا نوفا مقابل سبوندا.","ferry":"مواعيد عمل العبّارات، وكيفية توقيت الرحلات، والتنبيه لعوامل الطقس، ودمجها مع الحافلات.","car":"مناطق المرور المحدودة، نظام تبديل اللوحات، واقع المواقف، والبدائل الأكثر أمانًا.","free":"مطلات، شواطئ عامة، وتجارب ذاتية منخفضة التكلفة أو مجانية.","decision":"خيارات واضحة حسب الموسم، والأمتعة، والدرج، والميزانية — دون الالتزام بجداول ثابتة.","planning":"أدلة تخطيط سهلة القراءة تقود إلى زيارة خالية من التوتر.","experiences":"أماكن التصوير والتذوق والمغامرات الخفيفة دون توتر.","safety":"نصائح سلامة عملية والوقاية من الحرارة على المسارات والشواطئ والحافلات.","cuisine":"ما الذي يجب تجربته، وكيف تطلب، وأين توفّر في الوجبات.","camping":"خيارات التخييم والقواعد والتوقعات الواقعية على الساحل.","insurance":"ما الذي ينبغي أن تغطيه وثيقة جيدة لسفر الساحل.","passes":"بطاقات الحافلات/العبّارات: متى توفّر المال وكيف تستخدمها.","comparison":"قارن بين خيارات الوصول (نابولي مقابل ساليرنو) ومدن الإقامة حسب أسلوبك.","souvenirs":"تذكارات صادقة وحرف يدوية دون دفع مبالغ مبالغ فيها.","work-life":"كيفية موازنة العمل عن بُعد والاستكشاف في بوسيتانو.","positano":"كل ما يتعلق ببوسيتانو: اللوجستيات والأنشطة والوصول الأذكى.","amalfi":"لوجستيات مدينة أمالفي وتخطيط الرحلات اليومية.","ravello":"زيارات الحدائق والفعاليات مع خطط للعودة المتأخرة.","capri":"عبّارات مبكرة، مسارات مشي، ومتى تُسرف لتجربة مميزة.","naples":"روابط القطارات وما إذا كان عليك متابعة الرحلة بالعبّارة أو الحافلة.","salerno":"استراتيجية Airlink + العبّارة وخيارات اقتصادية."},"intros":{"positano":"أهلاً بك في بوسيتانو — قرية رأسية من الأزقة والسلالم وإطلالات المتوسط الساحرة. يجمع هذا الوسم الأدلة العملية التي تساعدك على اختيار مكان الإقامة، والوصول بأقل قدر من السلالم، واستكشاف ما تفعله بعد الوصول. توقّع نصائح صادقة حول موقفي الحافلات (كيييزا نوفا مقابل سبوندا)، وشرفات المشاهدة، وأزمنة السير الواقعية كي تستمتع بالمشهد من دون التفاف غير ضروري.","transport":"التنقل على الساحل جزء من المغامرة. تحت هذا الوسم ستجد أدلة مقارنة بين العبّارات والحافلات وخدمات الشاتل والنقل الخاص، مع خطط بديلة للبحار الهائجة وأيام الذروة. نركز على الاعتمادية طوال العام (قطار + حافلة عبر سورينتو) ونضيف خيارات العبّارات عندما يسمح الطقس والجداول.","ferry":"العبّارات طريقة خلابة للتنقل بين البلدات الساحلية خلال الموسم. استخدم هذه الأدلة لمعرفة أوقات التشغيل، وكيفية قراءة جداول المشغّلين، وماذا تفعل إذا أصبح البحر هائجًا. نربطك بالمصادر الرسمية، ونقترح أوقات صعود ذكية، ونوضح كيف تدمج رحلة عبّارة واحدة مع الحافلات لموازنة الراحة والتكلفة.","bus":"حافلات SITA هي العمود الفقري للسفر من دون سيارة على ساحل أمالفي. يغطي هذا الوسم التذاكر، وختمها، وآداب أوقات الذروة، وأقرب المواقف التي تقلل صعود السلالم في بوسيتانو. كما نشرح كيفية دمج الحافلات مع العبّارات لمسارات أسرع أو أكثر إطلالة من دون إنفاق زائد.","budgeting":"نعم، يمكن الاستمتاع بساحل أمالفي بميزانية معقولة. ابدأ بقراءة تقسيمات التكاليف وقوائم التوفير، ثم اختر مسارات تعطي الأولوية لنقاط المشاهدة المجانية، وأقسام الشواطئ العامة، والاستخدام الذكي للتذاكر المتعددة. سنشير إلى أين يستحق أن تنفق للمتعة القصوى — وأين يمكنك التوفير من دون تفويت التجربة.","free":"المجاني والمنخفض التكلفة لا يعني مملًا. هذا الوسم يجمع أفضل نقاط المشاهدة لدينا، والشواطئ العامة، ونزهات البلدات، والأنشطة التي يمكنك ترتيبها بنفسك لأيام لا تُنسى — وكل ذلك مع إبقاء النفقات تحت السيطرة.","itinerary":"سواء كنت مضغوط الوقت أو تخطط لإقامة أطول، تساعدك مساراتنا على تنظيم الأيام منطقيًا، وتجنب الرجوع المتكرر، وملاحقة أفضل ضوء في أكثر المواقع تصويرًا. توقّع اقتراحات يومية واضحة وبدائل للطقس أو تغيير مستوى النشاط.","amalfi":"مدينة أمالفي هي محور شبكة العبّارات وتتصل بسهولة برافيللو بالحافلة. استخدم هذه الأدلة لتخطيط الرحلات اليومية، وفهم لوجستيات الرصيف، ومعرفة متى تستحق العبّارة مقارنة بالحافلة من حيث الراحة والإطلالات.","ravello":"تقع رافيللو فوق أمالفي بحدائق مشهورة وحفلات صيفية. نشرح كيفية الوصول، وما تتوقعه في الفعاليات، وكيفية توقيت العودة عندما تقل الرحلات المسائية.","capri":"تكافئك جزيرة كابري على البدايات المبكرة: اركب أولى العبّارات، زر أن ناكابري ومارينا بيكولا، وارجع قبل زحمة الرحلات الأخيرة. تسلط أدلتنا الضوء على الخيارات الاقتصادية ومسارات تجنب الازدحام.","photography":"بوسيتانو خلابة — ومزدحمة. تبيّن هذه المقالات متى وأين تلتقط اللقطات الكلاسيكية من دون طوابير، مع بدائل أقل شهرة وإرشادات تضمن حصول الجميع على صورهم بأمان.","viewpoints":"من منعطفات فيا باسيتيّا إلى مسارات القمم، هذه هي الزوايا التي تُعرّف ساحل أمالفي. تعلّم كيفية الوصول إليها، ومتى يكون الضوء أفضل، وكيف تتجنب إعاقة الممرات الضيقة.","hiking":"توفر المسارات مثل درب الآلهة مشاهد عالمية المستوى. تعرّف على كيفية الوصول بالحافلة، وخيارات المسارات الأقصر أو الأنسب للركبتين، وما يجب حمله لمواجهة الحر أو تغيّر الطقس المفاجئ.","stairs":"السلالم جزء من حياة بوسيتانو. تقلل مقالاتنا اللوجستية عمليات الصعود غير الضروري وتوضح أي مواقف الحافلات والمسارات الصغيرة تبقي حمل الأمتعة في حده الأدنى.","porters":"عندما تكون الحقائب ثقيلة أو تصل بالعبّارة، قد يكون الحمالون خيارًا ذكيًا. تشرح هذه الأدلة متى تستعين بهم، وكيفية سير العمل المعتاد، والبدائل المتاحة ليلًا أو في الطقس السيئ.","connectivity":"واي فاي، شرائح eSIM، ومخارج الكهرباء: معلومات واقعية مناسبة للعمل عن بُعد. نذكر السرعات المعتادة، وخيارات النسخ الاحتياطي، وآداب العمل من المقاهي.","digital-nomads":"موازنة العمل والسفر في بوسيتانو ممكنة مع التخطيط. ستجد نصائح عن الروتين، والزوايا الهادئة، وتأثير المواسم حتى تلحق بالمواعيد النهائية وتلحق أيضًا بغروب الشمس.","cuisine":"من الليمونتشيللو إلى المعكرونة البحرية البسيطة والحلويات المليئة بالليمون، تعرّف على ما يجب تذوقه وكيف تطلب بثقة. نفضّل التجارب الصغيرة الصادقة وطرق التذوق المتنوعة من دون إسراف.","event":"الاحتفالات الموسمية والحفلات تضيف سحرًا للساحل. تركّز هذه الأدلة على حضور سلس: التذاكر، قواعد اللباس، الوصول والعودة، وأفضل الأماكن للمشاهدة.","seasonal":"الحشود والجداول تختلف باختلاف الفصول. نشرح ما يظل مفتوحًا، وكيف تتغير المواصلات، وكيف تعدّل خططك لمواسم الكتف أو الشتاء مع الحفاظ على تجربة ممتعة.","no-car":"لا تحتاج إلى سيارة هنا. امزج بين العبّارات للتنقل البحري والحافلات لبلدات القمم؛ توضح أدلتنا كيفية تنظيم الرحلات من دون قيادة مع الحفاظ على الراحة والميزانية.","passes":"التذاكر المتعددة يمكن أن توفر الوقت والمال إذا استخدمتها بذكاء. نقدّم مقارنة بين البطاقات المتاحة، ومتى يستحق شراؤها، وكيف توثّقها بشكل صحيح.","decision":"تحتاج إلى اختيار بين خيارات متعددة؟ تجمع أدلة اتخاذ القرار هذه الفروقات الأساسية (السرعة، التكلفة، الجهد) حتى تختار الطريق الأنسب لك بسرعة.","train":"القطارات تربط نابولي وساليرنو بالعالم — وهي جزء من أفضل طرق الوصول إلى الساحل. نشرح المسارات، والحجوزات، وكيفية الانتقال إلى الحافلات أو العبّارات من دون توتر.","off-season":"خارج الموسم تسود الهدوء وتقل الخدمات، لكن السحر لا يختفي. ستجد نصائح حول ما يظل متاحًا، وكيفية التخطيط للطقس غير المتوقع، وأماكن تناول الطعام خلال الفترة الهادئة.","winter":"الشتاء في بوسيتانو يعني درجات أبرد، وأمطارًا محتملة، وبلدة أكثر هدوءًا. تعرف على ما يظل مفتوحًا، وأفضل الطرق للتنقل، وكيف تحافظ على دفئك وأنت تستكشف.","camping":"التخييم حول ساحل أمالفي محدود لكنه ممكن. نرشدك إلى المواقع الرسمية، ومتطلبات الحجز، والنصائح العملية للتعامل مع الطقس والتضاريس.","cost":"ارسم ميزانيتك بناءً على متوسطات فعلية. نستعرض نطاقات الإنفاق اليومية والعوامل التي ترفع التكلفة أو تخفضها، ونقترح تعديلات بسيطة تحقق أكبر أثر.","tips":"حيل صغيرة تصنع فرقًا كبيرًا: من اختيار وقت العبّارات إلى التنقل بين مواقف الحافلات. نجمع تجاربنا لتجعل أيامك أكثر سلاسة.","work-life":"اجعل وقت العمل والترفيه متوازنًا. ابنِ روتينًا حول ساعات الهدوء، ونقاط الواي فاي الموثوقة، وفترات الغروب حتى تعيش أفضل ما في العالمين.","souvenirs":"احمل معك تذكارات أنيقة وسهلة النقل: سيراميك، مطبوعات، ومؤن صغيرة للمطبخ. نشارك أماكن الأسعار العادلة وكيف تعبئ أو تشحن القطع بأمان.","comparison":"تبحث عن قاعدة الانطلاق المثلى؟ قارن بين إطلالات بوسيتانو، وروابط سورينتو، وتوازن أمالفي لتجد ما يناسب أسلوبك وميزانيتك.","insurance":"دليل سريع للتأمين على الصحة، والإلغاءات، والإلكترونيات، والرحلات، والعبّارات. احتفظ بالوثائق الأساسية جاهزة ليكون تقديم أي مطالبة أمرًا بسيطًا.","hitchhiking":"الطرق هنا ضيقة ونقاط التوقف الآمنة قليلة. لا نوصي بالتنقل المجاني؛ نشرح بدائل أكثر أمانًا وموثوقة تحافظ على التكلفة المنخفضة.","walking-tour":"الأزقة الملتوية وإطلالات الشرفات تمنحك جولات مشي رائعة افعلها بنفسك. اتبع مساراتنا لتحقيق توازن بين المشاهد والزمن والجهد.","audio-guide":"نزّل الأدلة الصوتية قبل الانطلاق أو استخدم نقاط الواي فاي على الطريق. احفظ الصوت منخفضًا وراعِ الآخرين في المسارات المزدحمة.","work-exchange":"تفكر في التطوع داخل هوستل؟ افهم الأدوار، والساعات، والأساسيات القانونية في إيطاليا قبل الالتزام — وتعلم كيف تقيم الفرص بمسؤولية.","wellness":"حافظ على نشاطك عبر جولات السلالم، والجري على الشاطئ، والمسارات. احزم حذاءً مناسبًا، وابقَ مرتويًا، وجدول جلساتك في الأوقات الأبرد.","couples":"الأزواج يزدهرون في الهوستلات مع التوقعات الصحيحة. اختر غرفًا خاصة أو أسرّة استراتيجية، واحمل سدادات الأذن، وخطط لأنشطة مبكرة لأجواء أكثر هدوءًا.","accommodation":"اختر القاعدة الملائمة لأسلوبك وميزانيتك. نشرح المناطق، والمقايضات، وكيفية تقليل السلالم عند الوصول والمغادرة.","hostel-life":"الهوستلات اجتماعية وقيمة رائعة. تعرّف على ما تتوقعه في بريكيت — من التراس والبار إلى نصائح عملية للمساحات المشتركة.","logistics":"التفاصيل الصغيرة تحدث فرقًا كبيرًا: التذاكر، ومواقف الحافلات، والأمتعة، والمسارات القصيرة التي توفر الوقت والجهد على السلالم.","travel-tips":"نصائح عملية مستمدة من خبرة مباشرة — ما تحمله، وكيف تحزم بخفة، ومتى تعدل الخطة بسبب الطقس أو الازدحام.","experiences":"جولات القوارب، وأيام الشاطئ، والمشي، ونقاط المشاهدة — تساعدك هذه الأدلة على اختيار التجارب التي تلائم وقتك وميزانيتك وطاقتك.","boat":"الجولات الخاصة أو الجماعية على طول الساحل متعة حقيقية في الطقس الهادئ. نغطي التوقعات، والموسمية، والبدائل عندما يكون البحر مضطربًا.","rome":"الوصول من روما سهل بفضل القطارات السريعة ومرحلة أخيرة بسيطة. نقارن الخيارات ونوضح كيف تحافظ على الرحلة سلسة.","car":"القيادة على الساحل جميلة لكنها متطلبة. تعرف على مناطق منع المرور (ZTL)، وحقائق مواقف السيارات، ونظام تناوب اللوحات في موسم الذروة قبل أن تقرر.","wineries":"تنتج مصانع النبيذ العائلية الصغيرة في ترامونتي والجبال المحيطة خمور DOC مميزة. احجز التذوق مبكرًا، واذهب مع سائق معيّن أو جولة، واختر زجاجات تتحمل السفر.","tramonti":"كروم ترامونتي المدرّجة توفر نقيضًا هادئًا لضجيج البحر. توقّع طرقًا خلابة، ومنتجين صغار، ومواصلات عامة محدودة — خطّط مسبقًا.","scooter":"السكوترات رشيقة لكنها خطرة على المنحنيات العمياء لطريق SS163. استأجر فقط إذا كنت متمرسًا، واحمل الأوراق المناسبة، وفكّر في سورينتو لتوافر أفضل وأسعار أقل.","pets":"السفر مع الحيوانات الأليفة ممكن مع التخطيط. تحقق من السياسات مسبقًا، واحمل أقفاصًا للحافلات/العبّارات، وتجنّب الأرصفة الحارة وقت الظهيرة.","shopping":"ادعم الحرفيين المحليين: صنادل مفصّلة، سيراميك، ومطبوعات. اسأل عن المواد والأصل، وكيف تشحن القطع الهشّة بأمان.","stories":"الأساطير المحلية — من حوريات البحر إلى القديسين — تضيف لونًا للأماكن التي ستزورها. تعامل معها كرموز ثقافية تثري المهرجانات والمعالم.","history":"لمحة تاريخية عن بوسيتانو وساحل أمالفي تساعدك على رؤية المزيد في كل نزهة: طرق التجارة، والدين، والسياحة الحديثة تتشابك هنا.","language":"القليل من العبارات الإيطالية يجعل السفر أكثر سلاسة. تعلم الأساسيات للحافلات والعبّارات والمقاهي والتعامل المهذب.","gateway":"تُعد مدن ومراكز النقل مثل سورينتو وساليرنو ونابولي بوابات إلى الساحل. استخدمها لوصول أكثر ذكاءً واتصالات أرخص."}}')}}]);