"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[231360],{231360:e=>{e.exports=JSON.parse('{"seo":{"title":"कम बजट में Positano कैसे पहुँचे","description":"Naples हवाई अड्डे से Positano तक शटल + SITA बस के सबसे सस्ते मार्ग, किराए और चरण-दर-चरण दिशा-निर्देश।"},"linkLabel":"कम बजट में Positano कैसे पहुँचें","intro":"Naples Airport → Sorrento शटल, फिर Positano के लिए SITA बस लें—सबसे सस्ता और भरोसेमंद कनेक्शन।","sections":[{"id":"why-route","title":"यह रूट क्यों काम करता है","body":["एक शटल (~€10) + एक SITA बस (~€2.10) कुल खर्च को €13 से कम रखती है, जो फेरी या टैक्सी से काफी सस्ता है।","दोनों लेग्स आपको Chiesa Nuova के पास छोड़ते हैं ताकि सामान के साथ लंबी Positano सीढ़ियों से बचें।"]},{"id":"before-you-go","title":"निकलने से पहले","body":["पीक महीनों में यात्रा कर रहे हों तो Curreri शटल ऑनलाइन प्री‑बुक करें, या आगमन के बाहर डेस्क से खरीदें और लैंडिंग के बाद 15 मिनट का बफर रखें।","Sorrento में SITA टिकट के लिए छोटा कैश रखें; Corso Italia के कियोस्क/ tabacchi बेचते हैं और देर रात बड़े नोट लेने से मना कर सकते हैं।"]},{"id":"timing-backup","title":"समय और बैकअप प्लान","body":["दिन की आखिरी Curreri डिपार्चर नोट करें (गर्मियों में अक्सर ~21:30, ऑफ‑सीज़न में पहले) और उसी के हिसाब से फ्लाइट प्लान करें।","शटल छूट गई? Alibus + Circumvesuviana या Campania Express से Sorrento जाएँ और वही SITA बस रूट जारी रखें।"]}],"mapTitle":"इंटरैक्टिव मैप","stepByStepTitle":"स्टेप‑बाय‑स्टेप","steps":[{"name":"नेपल्स एयरपोर्ट → सोरेंटो (Curreri Viaggi)","text":"आगमन से बाहर निकलें और Curreri शटल से Sorrento स्टेशन जाएँ। टिकट ~€10; अवधि ~1h 15m।"},{"name":"Sorrento → Positano (SITA बस)","text":"SITA CIS टिकट (~€2.10) खरीदें। Amalfi की ओर बस लें और Positano Chiesa Nuova पर उतरें।"},{"name":"Hostel Brikette तक पैदल जाएँ","text":"Chiesa Nuova स्टॉप से हॉस्टल तक ~80m ढलान पर चलें (सीढ़ियाँ नहीं)।"}],"alternativesTitle":"विकल्प","trainTitle":"ट्रेन + बस (थोड़ा महंगा, तेज़ हो सकता है)","train":["NAP → Napoli Centrale (Garibaldi) के लिए Alibus, फिर Circumvesuviana से Sorrento (~€6 कुल)।","Sorrento → Positano के लिए SITA (~€2.10)। समय: कुल ~2–2.5 घंटे।"],"ferryTitle":"फेरी (मौसमी, सुंदर, अधिक खर्च)","ferry":["Alibus → Molo Beverello, फिर Positano के लिए फेरी (केवल गर्मियों में; ऑपरेटर के अनुसार €20–€38)।","लाइव टाइमटेबल के लिए हमारा ferry schedules गाइड देखें।"],"costsTitle":"एक नज़र में खर्च","costs":["शटल (NAP → Sorrento): ~€10","SITA बस (Sorrento → Positano): ~€2.10","कुल बजट रूट: ~€12.10"],"faq":[{"q":"सबसे सस्ता रूट कौन सा है?","a":"Naples Airport → Sorrento शटल, फिर Positano (Chiesa Nuova) के लिए SITA बस।"},{"q":"बस टिकट कहाँ खरीदें?","a":"स्टेशनों के पास tobacconists/kiosks पर; चढ़ने से पहले खरीदें और बस में वैलिडेट करें।"},{"q":"कितना समय लगता है?","a":"अच्छे कनेक्शन के साथ कुल लगभग 2.5 घंटे।"}],"ferryCta":"गर्मियों में फेरी पसंद हैं? पोर्ट टिप्स और एक्सेसिबिलिटी नोट्स के लिए हमारा dedicated ferry गाइड और Arriving by Ferry सहायता लेख देखें।","mapIframeTitle":"Naples Airport से Sorrento होते हुए Positano","mapEmbedUrl":"https://www.google.com/maps/embed?pb=!1m28!1m12!1m3!1d768989.930376533!2d14.43034800958525!3d40.7530661290345!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!4m13!3e3!4m5!1s0x133b089b2b18a3e3%3A0x4bafc3f58a62a2a2!2sNaples%20International%20Airport%20(NAP)!3m2!1d40.8860031!2d14.2907817!4m5!1s0x133b98d2a1b9f60f%3A0x54a8f4e7a3413732!2sHostel%20Brikette%2C%20Via%20Guglielmo%20Marconi%2C%20358%2C%2084017%20Positano%20SA!3m2!1d40.6308766!2d14.487226!5e0!3m2!1sen!2sit!4v1700000000000","mapReferrerPolicy":"no-referrer-when-downgrade","howTo":{"totalTime":"PT2H30M","estimatedCost":{"currency":"EUR","value":"12.10"}},"sectionIds":{"map":"reach-budget-map","steps":"reach-budget-steps","alternatives":"reach-budget-alternatives","costs":"reach-budget-costs","tips":"reach-budget-tips"}}')}}]);