"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[247610],{247610:e=>{e.exports=JSON.parse('{"meta":{"title":"Salerno to Positano by Ferry | Hostel Brikette","description":"Salerno to Positano by ferry: schedules, ticket tips, and how to reach Hostel Brikette from the port using porters or the local bus."},"header":{"eyebrow":"Hostel Brikette Travel Guide","title":"Salerno to Positano — Ferry","description":"Use this guide to travel from Salerno to Positano by ferry. It covers tickets, where to board in Salerno, timetable tips, and how to reach Hostel Brikette from the Positano dock."},"tip":{"label":"Tip","body":"Need other transport options? Open the <GuideLink>How to Get Here overview</GuideLink> for buses, mixed routes, and airport transfers."},"sections":{"overview":{"title":"Journey overview","points":["Seasonal ferries run from Salerno to Positano with scenic coastal views.","Travelmar operates the most frequent services; allow extra time in high season.","Ferries can be cancelled in bad weather — always check live updates before travel."]},"tickets":{"title":"Tickets and schedules","intro":"Check latest times and prices on <TravelmarLink>Travelmar</TravelmarLink> and <PositanoLink>Positano.com – Ferry schedules</PositanoLink>.","points":["Buy tickets online in advance during peak months.","Arrive 15–20 minutes before departure to board calmly.","Keep your ticket handy; staff will scan or tear it before boarding."]},"salernoBoarding":{"title":"Where to board in Salerno","points":["Most services depart Salerno’s Piazza della Concordia (Travelmar).","Some departures use Molo Manfredi — confirm the terminal on your ticket.","Follow signage at the waterfront; staff can direct you to the correct pier."]},"positanoArrival":{"title":"Arriving in Positano","points":["You’ll arrive at Positano’s main ferry dock (Spiaggia Grande).","With luggage, we recommend the porter service or the local Interno bus.","Walking uphill with heavy bags is not advised in summer heat."],"cta":"For a step-by-step route from the dock to our door, read the <DockGuideLink>Ferry dock → Hostel Brikette, with luggage guide</DockGuideLink>."}}}')}}]);