"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[255616],{255616:e=>{e.exports=JSON.parse('{"seo":{"title":"Offbeat Amalfi Coast Villages","description":"Cetara, Minori/Maiori, Atrani, Vietri, Furore — quieter charms."},"linkLabel":"Offbeat Amalfi Coast Villages","intro":["Five quieter Amalfi Coast villages worth a detour, plus how to reach each one."],"tocTitle":"On this page","sections":[{"id":"cetara","title":"Cetara","body":["Anchovy traditions and a quiet beach with working fishing boats.","Reach via SITA bus or seasonal ferries from Amalfi or Salerno."]},{"id":"minori","title":"Minori/Maiori","body":["Flat seaside promenades and beloved pastry shops for a calmer pace.","Arrive by bus or ferry; combine with the lemon trail between the twins."]},{"id":"atrani","title":"Atrani","body":["Charming pocket piazza just east of Amalfi with narrow lanes and beach access.","Walk from Amalfi in minutes or ride one stop on the local bus."]},{"id":"vietri","title":"Vietri sul Mare","body":["Ceramics capital near Salerno where workshops line the streets.","Take the train to Salerno, then a short bus or ferry hop."]},{"id":"furore","title":"Furore","body":["Dramatic fjord with limited beach space — arrive early for calm photos.","Use SITA buses toward Amalfi; summer ferries sometimes stop nearby."]}],"faqs":[{"q":"Can I visit multiple villages in one day?","a":["Yes—link Cetara, Vietri, and Minori via ferry or bus. Start early and keep an eye on return schedules."]},{"q":"Do small villages accept cards?","a":["Carry cash for caf\xe9s and kiosks; many still set €10 minimums or remain cash-only."]}]}')}}]);