"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[262677],{262677:e=>{e.exports=JSON.parse('{"labels":{"onThisPage":"इस पेज पर","before":"निकलने से पहले","steps":"स्टेप‑बाय‑स्टेप रूट","knees":"पैर और सामान","faqs":"अक्सर पूछे जाने वाले सवाल"},"folkloreAmalfi":{"intro":["मत्स्यकन्याएँ, संत और नाविक — ऐसी कहानियाँ जो अमाल्फी कोस्ट को रंग देती हैं और आज भी जिन स्थानों से जुड़ी हैं।"],"sections":[{"title":"मछुआरों की कहानियाँ","body":["रक्षक संतों और अचानक बदलते समुद्र की कथाएँ स्थानीय परंपराओं और त्योहारों का कारण बताती हैं।"]},{"title":"स्थलों के नाम की मिथक","body":["सदियों में स्थानों और निशानों ने अपनी लोककथाएँ कैसे अपनाईं।"]}]},"compareBasesPositanoSorrentoAmalfi":{"intro":[],"toc":[{"href":"#positano","label":"Positano"},{"href":"#sorrento","label":"Sorrento"},{"href":"#amalfi","label":"Amalfi"},{"href":"#who","label":"किसके लिए उपयुक्त"}],"sections":[{"id":"positano","title":"Positano","body":["प्रसिद्ध दृश्य और सीढ़ियाँ — यदि आप उसी नज़ारे में जागना चाहते हैं तो बेहतरीन।","परिवहन की पहले से योजना ज़रूरी है, खासकर सुबह की शुरुआती फेरी और देर रात बसों के लिए।"]},{"id":"sorrento","title":"Sorrento","body":["सबसे अच्छे कनेक्शन और अधिकांश बजट के लिए विस्तृत आवास विकल्प।","दृश्य Positano जितने नाटकीय नहीं, लेकिन व्यवहारिकता बहुत अधिक है।"]},{"id":"amalfi","title":"Amalfi","body":["फेरी और बसों का केंद्रीय हब, जहाँ से Ravello आसानी से पहुँचें।","ठहरने की शैलियाँ विविध हैं और शाम के समय परिवहन विकल्प मध्यम हैं।"]},{"id":"who","title":"किसके लिए कौन सा बेस","body":["Positano: शानदार दृश्य और होस्टल का सामाजिक जीवन।","Sorrento: वैल्यू चाहने वालों के लिए जो तेज़ ट्रांज़िट लिंक चाहते हैं।","Amalfi: कोस्ट के साथ संतुलित एक्सेस के लिए।"]}],"faqs":[],"faqsTitle":"अक्सर पूछे जाने वाले सवाल"},"interrailItalyRailPassAmalfiCoast":{"toc":[{"href":"#routes","label":"मुख्य मार्ग"},{"href":"#supplements","label":"अतिरिक्त शुल्क/आरक्षण"},{"href":"#last-mile","label":"Positano तक अंतिम चरण"},{"href":"#faqs","label":"अक्सर पूछे जाने वाले सवाल"}],"sections":[{"id":"routes","title":"मुख्य मार्ग","body":["Rome → Naples/Salerno हाई-स्पीड; Sorrento तक लोकल ट्रेनें; Positano तक बसें।"]},{"id":"supplements","title":"अतिरिक्त शुल्क/आरक्षण","body":["Frecciarossa/Italo में अक्सर सीट आरक्षण ज़रूरी; क्षेत्रीय ट्रेन में नहीं, लेकिन उनमें भीड़ हो सकती है।"]},{"id":"last-mile","title":"Positano तक अंतिम चरण","body":["Naples से: Circumvesuviana से Sorrento, फिर SITA बस। Salerno से: सीज़न में फेरी/बस।"]}],"faqsTitle":"अक्सर पूछे जाने वाले सवाल","faqs":[{"q":"क्या पास लेना फायदेमंद है?","a":["केवल तब जब आप लंबी दूरी के पर्याप्त सेक्शन ले रहे हों; अन्यथा पॉइंट-टू-पॉइंट टिकट सस्ते हो सकते हैं।"]}]},"photographyGuidePositano":{"toc":[{"href":"#classics","label":"क्लासिक कोण"},{"href":"#crowds","label":"भीड़ से बचना"},{"href":"#etiquette","label":"शिष्टाचार"}],"sections":[{"id":"classics","title":"क्लासिक कोण","body":["Upper Via Pasitea के मोड़, Fornillo का मार्ग और घाट से पीछे मुड़कर दृश्य — सूर्योदय या गोल्डन आवर में सबसे अच्छे।"]},{"id":"crowds","title":"भीड़ से बचना","body":["सुबह जल्दी जाएँ, या सूर्यास्त के बाद ब्लू आवर में शूट करें ताकि दिनभर के पर्यटकों की भीड़ न हो।"]},{"id":"etiquette","title":"शिष्टाचार","body":["जगह साझा करें, संकरी सीढ़ियों पर रास्ता दें, और दुकानों के सामने रास्ता अवरुद्ध न करें।"]}]},"simsAtms":{"intro":["eSIM सुविधाजनक और अक्सर सस्ते होते हैं; यदि आपको भौतिक SIM या ATM चाहिए तो पीक सीज़न में पहले से योजना बनाएँ।"],"faqs":[{"q":"क्या मैं Positano में स्थानीय SIM खरीद सकता हूँ?","a":["दुकानें सीमित हैं; बेहतर दरों के लिए Naples या Sorrento में खरीदें या eSIM पर विचार करें।"]},{"q":"क्या कवरेज भरोसेमंद है?","a":["शहर में सामान्यतः अच्छा है, लेकिन पगडंडियों और चट्टानों के पास कुछ जगहें कमजोर हो सकती हैं।"]},{"q":"क्या ATM उपलब्ध हैं?","a":["ATM हैं, पर शुल्क अलग-अलग हो सकते हैं; बैंक-चालित मशीनों को प्राथमिकता दें और बैकअप कार्ड साथ रखें।"]}]},"ferrySchedules":{"intro":["Positano के लिए फेरियाँ मौसमी होती हैं। गर्मियों में कई ऑपरेटर Naples, Sorrento, Amalfi और Capri को Positano से जोड़ते हैं। मौसम और मांग के अनुसार समय-सारिणी बदलती है, इसलिए हमेशा ऑपरेटर की वेबसाइट पर पुष्टि करें।"],"tipsTitle":"फेरी से जुड़ी स्मार्ट टिप्स","tips":["रवानगी से एक रात पहले अपडेट देखें — तेज़ हवा या ऊंची लहरें बिना सूचना के सेवा रद्द कर सकती हैं.","पीक सीज़न में टिकट ऑनलाइन बुक करें ताकि घाट के काउंटर पर बिकने से बचा जा सके.","घाट से ऊपर कस्बे तक पोर्टर सेवा लेने का सोच रहे हैं तो कुछ यूरो नकद साथ रखें.","जब समुद्र बहुत उग्र हो और फेरी न चले तो बैकअप के लिए SITA बस का टाइमटेबल पास रखें."],"sections":[{"id":"seasonality","title":"मौसमी संचालन","body":["फेरियाँ मुख्यतः अप्रैल–अक्टूबर चलती हैं। सर्दियों में सेवाएँ सीमित या बंद हो सकती हैं; तब बसों का उपयोग करें।"]},{"id":"tips","title":"त्वरित सुझाव","body":["पीक सीज़न: अप्रैल–अक्टूबर। सर्दियों में सेवाएँ कम या न के बराबर।","रवानगी से पहले समय पर पहुँचें ताकि कतार और टिकट मान्य कराने के लिए समय मिले।","पोर्ट से हॉस्टल तक चढ़ाई है; भारी सामान हो तो पोर्टर या \\"Chiesa Nuova\\" तक बस पर विचार करें।","और आगमन सुझावों के लिए हमारी सहायता लेख \\"फेरी से आगमन\\" देखें।"]}],"faqs":[{"q":"क्या फेरियाँ वर्षभर Positano जाती हैं?","a":["फेरियाँ मौसमी (आमतौर पर अप्रैल–अक्टूबर) होती हैं। सर्दियों में बसें/टैक्सियाँ मुख्य विकल्प होती हैं।"]},{"q":"रीयल-टाइम फेरी समय कहाँ देखें?","a":["ऑपरेटर साइट्स (जैसे NLG, Travelmar, Alilauro) या गर्मियों में DirectFerries जैसे एग्रीगेटर्स देखें।"]},{"q":"Positano में किस घाट का उपयोग करना चाहिए?","a":["मुख्य घाट Spiaggia Grande पर है। व्यस्त दिनों में अपने टिकट पर बताए गए बोर्डिंग लेन की जाँच करें।"]},{"q":"प्रस्थान से कितनी देर पहले पहुँचना चाहिए?","a":["20–30 मिनट पहले पहुँचें; जून–सितंबर में कतार आम है।"]},{"q":"पोर्ट से Hostel Brikette तक पैदल रास्ता कठिन है?","a":["ऊphill सीढ़ियाँ हैं। भारी सामान हो तो पोर्टर सेवा या Chiesa Nuova तक बस पर विचार करें।"]}],"gallery":[{"alt":"Positano पियर क्षेत्र","caption":"Spiaggia Grande पियर"},{"alt":"Amalfi की ओर आती फेरी","caption":"तट के साथ चलती फेरियाँ"}],"faqsTitle":"पोसिटानो फेरी से जुड़े सामान्य प्रश्न","galleryTitle":"फेरी की प्रमुख तस्वीरें"},"drivingAmalfi":{"intro":["SS163 पर ड्राइव करना सुंदर लेकिन चुनौतीपूर्ण है। पहले फेरी और बस विकल्प सोचें; यदि ड्राइव करें तो संकरी लेन, सीमित पार्किंग और मौसमी प्रतिबंधों की योजना बनाएँ।"]},"ecoFriendlyAmalfi":{"intro":["तट का आनंद लेते हुए अपना पर्यावरणीय प्रभाव कम रखें। सीढ़ी मार्गों पर चलें, सीज़न में कार की जगह फेरी चुनें, पानी रीफिल करें और पगडंडियों पर कचरा लौटाएँ।"],"toc":[{"href":"#getting-around","label":"आवागमन"},{"href":"#refills","label":"रीफिल और कचरा"},{"href":"#trails","label":"ट्रेल शिष्टाचार"}],"sections":[{"id":"getting-around","title":"आवागमन","body":["निजी कार की जगह फेरी और बस को प्राथमिकता दें; पास-पास के गाँवों के बीच सुरक्षित हो तो पैदल चलें।","ऑफ-पीक समय चुनें ताकि संचालन पर दबाव कम हो और तट पर भीड़ घटे।"]},{"id":"refills","title":"रीफिल और कचरा","body":["सार्वजनिक फव्वारों का उपयोग करें या कैफे से बोतल भरने के लिए कहें; पुन: उपयोग योग्य बोतल साथ रखें।","जहाँ संभव हो कचरा अलग करें और एकबार इस्तेमाल होने वाले बीच गियर से बचें।"]},{"id":"trails","title":"ट्रेल शिष्टाचार","body":["संकरी जगहों पर रास्ता दें, सारा कचरा वापस साथ लाएँ और निजी संपत्ति का सम्मान करें।","गर्मी और पगडंडी की भीड़ से बचने के लिए जल्दी शुरू करें।"]}],"faqs":[],"faqsTitle":"अक्सर पूछे जाने वाले सवाल"},"itinerariesPillar":{"intro":"अमाल्फी कोस्ट का आनंद लेने के लिए आपको कार की ज़रूरत नहीं। दर्शनीय यात्रा के लिए फेरी, पहाड़ी कस्बों के लिए बसें और भीड़ से बचने के लिए थोड़ी टाइमिंग का उपयोग करें। इन टेम्पलेट से शुरुआत करें और फिर अपनी योजना बनाएँ।","toc":{"twoDays":"2 दिन: Positano केंद्रित","threeDays":"3 दिन: Capri या Amalfi+Ravello जोड़ें","sevenDays":"7 दिन: पूरा लूप","tips":"समय और परिवहन टिप्स","faqs":"अक्सर पूछे जाने वाले सवाल"},"twoDaysHeading":"2 दिन: Positano केंद्रित","twoDaysItems":["दिन 1: Positano पुराना शहर, तटीय पाथ से Fornillo, Via Pasitea के मोड़ों से सूर्यास्त।","दिन 2: <pathOfTheGodsLink>Path of the Gods</pathOfTheGodsLink> Nocelle से; बाद में बीच पर आराम।"],"threeDaysHeading":"3 दिन: Capri या Ravello जोड़ें","threeDaysIntro":"एक चुनें:","threeDaysOptions":["Capri डे ट्रिप: सुबह की फेरी → Marina Grande → Anacapri चेयरलिफ्ट → Marina Piccola लूप।","Amalfi + Ravello: Amalfi तक बस, फिर Ravello के बागों और व्यूज़ के लिए जाएँ; बस/फेरी से लौटें।"],"sevenDaysHeading":"7 दिन: पूरा लूप","sevenDaysItems":["Positano बेस (2–3 रातें)","Amalfi/Ravello (2 रातें)","Capri (1 रात) या Sorrento (1–2 रातें) ताकि ट्रांसफ़र आसान हों"],"tipsHeading":"समय और परिवहन टिप्स","tips":["पीक दिनों में 10:00–16:00 के बीच फेरी कम लें; इससे पहले या बाद में यात्रा करें।","बस टिकट पहले से खरीदें; चढ़ते समय मान्य करें।","समुद्र उफान पर हो तो बस योजना पर स्विच करें; ऑपरेटर के अपडेट देखें।"],"faqHeading":"अक्सर पूछे जाने वाले सवाल","faqs":[{"q":"क्या कार लेना उचित है?","a":["पार्किंग की कमी, ZTL और वैकल्पिक नंबर प्लेट नियम साल के अधिकतर समय बिना कार यात्रा को आसान बनाते हैं।"]}]},"amalfiTownGuide":{"headings":{"highlights":"मुख्य आकर्षण","gettingThere":"कैसे पहुँचे","tips":"टिप्स"},"highlights":["Duomo di Amalfi और Chiostro del Paradiso (क्लॉइस्टर)।","पेपर म्यूज़ियम (Museo della Carta) — अमाल्फी के इतिहास का अनूठा हिस्सा।","समुद्र तट के किनारे सैर और सीढ़ियों पर जेलाटो।"],"gettingThere":"Positano से, सीज़न में फेरी सुंदर और आरामदायक हैं। अन्यथा SITA बस लें; पीक समय में कतार की उम्मीद रखें। टिकट पहले से खरीदें।","tips":["जल्दी पहुँचे; पूरा दिन बनाने के लिए Amalfi से बस लेकर Ravello के बाग देखें।","सूर्य से बचाव का सामान साथ रखें; पियर पर छाया सीमित है।"]},"sevenDayNoCar":{"intro":"ट्रांसफ़र सरल रखने के लिए Positano में 3 रातें, Amalfi/Ravello में 2 और Capri या Sorrento में 1–2 रातें रखें।","toc":{"onThisPage":"इस पेज पर","overview":"सारांश","dayByDay":"दिनवार","tips":"परिवहन और समय टिप्स","faqs":"अक्सर पूछे जाने वाले सवाल"},"days":[{"id":"day1","label":"दिन 1","items":["Positano पहुँचना; पुराना शहर और Fornillo"]},{"id":"day2","label":"दिन 2","items":["Nocelle से Path of the Gods"]},{"id":"day3","label":"दिन 3","items":["Capri डे ट्रिप या बीच + बोट टूर"]},{"id":"day4","label":"दिन 4","items":["Amalfi के लिए ट्रांसफ़र; Duomo; पियर पर सूर्यास्त"]},{"id":"day5","label":"दिन 5","items":["Ravello गार्डन्स (Villa Rufolo/Villa Cimbrone)"]},{"id":"day6","label":"दिन 6","items":["Capri में रात या Sorrento की ओर"]},{"id":"day7","label":"दिन 7","items":["प्रस्थान"]}],"tips":["फेरियाँ मौसमी हैं; समुद्र उफान पर हो तो बस बैकअप रखें।","कतारों से बचने के लिए 10:00 से पहले या 16:00 के बाद यात्रा करें।"],"faq":[{"q":"क्या मुझे बेस बदलना चाहिए?","a":["दो बेस रखने से सामान कम शिफ्ट करना पड़ता है और ट्रांसफ़र भी कुशल रहता है।"]}]},"chiesaNuovaDepartures":{"intro":["अमाल्फी या सोरेंटो जाने वाली बस पकड़ने के लिए हॉस्टल से निकल रहे हैं? यह सूक्ष्म मार्ग आपको हॉस्टल ब्रिकेट से चिएसा नूओवा/बार इंटरनाज़ियोनाले बस स्टॉप तक का सबसे तेज़ रास्ता और कुछ सुझाव दिखाता है।"],"sections":[{"title":"यह रूट क्यों काम करता है","body":["हॉस्टल के प्रवेश द्वार से स्टॉप तक मुख्य सड़क पर लगभग 120 मीटर (400 फ़ुट) की पैदल दूरी है।","अगर आप अमाल्फी की ओर जा रहे हैं तो स्टॉप बार इंटरनाज़ियोनाले के सामने है। सड़क के समुद्र की ओर वाले हिस्से में कतार में लगें। अगर आप सोरेंटो की ओर जा रहे हैं तो आपको सीधे बार के बाहर इंतज़ार करना होगा।"]},{"title":"टिकट और समय","body":["SITA का टिकट तंबाकू की दुकान या बार के अंदर से खरीदें। बिना टिकट बस में चढ़ने की कोशिश न करें, क्योंकि बस में टिकट नहीं मिलेंगे। बस में चढ़ते समय अपना टिकट ड्राइवर को दिखाएं। अगर आप पहली बस पकड़ रहे हैं तो टिकट एक दिन पहले खरीदें और चेक-आउट की व्यवस्था के लिए रिसेप्शन पर हमें मिलें।","गर्मी में बसें अक्सर भर जाती हैं। खासकर सबसे व्यस्त समय में उसी अनुसार योजना बनाएं।"]}],"beforeList":[],"stepsList":["Hostel Brikette से निकलें, छोटी सीढ़ियाँ लेकर Viale Pasitea पर जाएँ, और बाएँ मुड़ें।","मुख्य सड़क के साथ लगभग 120 मीटर (400 फ़ुट) तक फुटपाथ पर चलते हुए बार इंटरनाज़ियोनाले तक पहुंचें।"],"stepsMapEmbedUrl":"https://www.google.com/maps/embed?origin=mfe&mode=walking&pb=!1m5!4m4!4m1!2sHostel+Brikette!4m1!2sBar+Internazionale","kneesList":[],"kneesDockPrefix":"फेरी डॉक पर सामान के साथ शुरू कर रहे हैं? हमारा उपयोग करें","kneesDockLinkLabel":"फेरी डॉक सामान रूट","kneesPorterPrefix":"पोर्टर कीमत या बुकिंग मदद चाहिए? देखें","kneesPorterLinkLabel":"पोर्टर सेवा गाइड","faqs":[{"q":"क्या बसें हॉस्टल के ठीक बाहर रुकती हैं?","a":"वे Bar Internazionale पर रुकती हैं—प्रवेश से लगभग 120 मीटर (400 फीट) नीचे।"},{"q":"क्या मैं आखिरी समय में टिकट खरीद सकता/सकती हूँ?","a":"हाँ, स्टॉप के पास tabacchi SITA टिकट बेचता है, लेकिन पीक समय में कतारें होती हैं।"}],"howtoSteps":["Hostel Brikette से निकलें और Viale Pasitea पर नीचे की ओर चलें","Bar Internazionale पर कतार में लगें और बोर्डिंग पर वैलिडेट करें"],"faqsTitle":"अक्सर पूछे जाने वाले सवाल"}}')}}]);