"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[269154],{269154:n=>{n.exports=JSON.parse('{"meta":{"title":"Từ Positano đến Salerno bằng ph\xe0 | Hostel Brikette","description":"Từ cửa hostel tới cảng Positano, đi ph\xe0 đến Salerno, k\xe8m mẹo v\xe9 v\xe0 ghi ch\xfa khi đến nơi."},"header":{"eyebrow":"Cẩm nang di chuyển của Hostel Brikette","title":"Positano đến Salerno – Ph\xe0","description":"L\xe0m theo c\xe1c bước n\xe0y để đi bộ từ Hostel Brikette tới cảng Positano, mua v\xe9 ph\xe0 v\xe0 l\xean t\xe0u đi Salerno. B\xe0i viết cung cấp mẹo căn thời gian, lựa chọn h\xe3ng cũng như th\xf4ng tin khi cập cảng Salerno v\xe0 nối chuyến tới ga t\xe0u."},"tip":{"label":"Mẹo","copy":"Biển động cần phương \xe1n dự ph\xf2ng? Xem <link>Tổng quan C\xe1ch đến đ\xe2y</link> để chọn tuyến thay thế."},"journey":{"title":"Tổng quan h\xe0nh tr\xecnh","paragraphs":["Ph\xe0 giữa Positano v\xe0 Salerno chạy nhiều nhất từ m\xf9a xu\xe2n đến đầu m\xf9a thu v\xe0 c\xf3 thể bị hủy khi biển động. Thời gian di chuyển thường khoảng 60–80 ph\xfat t\xf9y h\xe3ng v\xe0 loại t\xe0u.","So s\xe1nh giờ chạy v\xe0 đặt v\xe9: <positanoLink>Positano.com (lịch tổng hợp)</positanoLink> \xb7 <travelmarLink>Travelmar – Salerno ↔ Positano</travelmarLink>."]},"walk":{"title":"Đi bộ tới cảng Positano","steps":["Ra khỏi hostel bằng cửa ch\xednh.","Đi xuống cầu thang tới mặt đường, rẽ tr\xe1i v\xe0 đi hơn 100 m.","Ở ng\xe3 rẽ đầu ti\xean (điểm 1), rẽ phải cạnh cửa h\xe0ng Tabacchi (t\xf2a nh\xe0 đầu ti\xean b\xean phải).","Tiếp tục xuống dốc; tại kh\xfac cua gấp đầu ti\xean (điểm 2), rời đường v\xe0 đi thẳng xuống cầu thang.","Xuống hết cầu thang gặp đường (điểm 3); băng qua v\xe0 đi tiếp xuống d\xe3y cầu thang kế tiếp.","Khi cầu thang đ\xf3 kết th\xfac ở mặt đường, băng qua, rẽ tr\xe1i một đoạn ngắn rồi xuống cầu thang (điểm 4).","Xuống tới cuối, rẽ phải tới điểm 5 rồi đi tiếp qua lối v\xf2m b\xean phải.","Cảng (cầu t\xe0u Spiaggia Grande) nằm c\xe1ch khoảng 50 m ph\xeda tay phải b\xe3i biển."]},"tickets":{"title":"Mua v\xe9 v\xe0 l\xean t\xe0u","steps":["Tại cảng, mỗi h\xe3ng ph\xe0 c\xf3 quầy ri\xeang.","Biển b\xean cạnh quầy hiển thị chuyến khởi h\xe0nh tiếp theo của h\xe3ng đ\xf3.","Mua v\xe9 trực tiếp từ h\xe3ng. Đến sớm 20–30 ph\xfat để đổi phiếu (voucher) nếu cần v\xe0 xếp h\xe0ng.","Sau khi mua v\xe9, đi ra cuối cầu t\xe0u v\xe0 chờ tới lượt l\xean t\xe0u."]},"arrival":{"title":"Khi đến Salerno","paragraphs":["Xuống t\xe0u tại khu cảng Concordia/Manfredi của Salerno t\xf9y hướng dẫn từ h\xe3ng. Nếu cần bắt t\xe0u, đi bộ tới ga ch\xednh Salerno (Stazione FS); đường đi dễ d\xe0ng v\xe0 hầu như bằng phẳng.","Bản đồ: <mapLink>Từ cảng Concordia → ga t\xe0u Salerno</mapLink>.","Nếu ph\xe0 bị hủy v\xec thời tiết, h\xe3y ho\xe3n chuyến hoặc c\xe2n nhắc đi qua Sorrento v\xe0o ng\xe0y h\xf4m sau."]}}')}}]);