"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[271102],{271102:n=>{n.exports=JSON.parse('{"seo":{"title":"C\xe1ch tr\xe1nh đ\xf4ng ở Positano (\xedt người biết)","description":"Canh thời gian, tuyến đường y\xean tĩnh, thị trấn thay thế v\xe0 g\xf3c chụp th\xf4ng minh để tận hưởng Positano kh\xf4ng chen lấn."},"linkLabel":"C\xe1ch tr\xe1nh đ\xf4ng ở Positano (\xedt người biết)","intro":["Tr\xe1nh đ\xe1m đ\xf4ng ở Positano nghĩa l\xe0 canh giờ hợp l\xfd, len qua những lối \xedt người đi v\xe0 biết khi n\xe0o n\xean gh\xe9 c\xe1c thị trấn y\xean b\xecnh hơn."],"sections":[{"id":"timing","title":"Canh thời gian","body":["Đến Spiaggia Grande hoặc c\xe1c viewpoint ch\xednh v\xe0o l\xfac b\xecnh minh hay giờ v\xe0ng; ăn trưa muộn (sau 14:30) cũng tr\xe1nh được cao điểm b\xe0n trống.","Nghỉ trưa trong b\xf3ng r\xe2m hoặc chợp mắt — kh\xe1ch đi trong ng\xe0y thường lấp đầy c\xe1c con hẻm từ 10:00 đến 16:00."]},{"id":"routes","title":"Tuyến đường & g\xf3c y\xean tĩnh","body":["D\xf9ng lối đi ph\xeda tr\xean giữa Chiesa Nuova v\xe0 Sponda để c\xf3 tầm nh\xecn ban c\xf4ng m\xe0 kh\xf4ng bị tắc ở Via dei Mulini.","Ra b\xe3i Fornillo hoặc điểm nh\xecn Via Positanesi d’America để tắm biển v\xe0 chụp ảnh trong kh\xf4ng kh\xed trầm lắng hơn."]},{"id":"alternatives","title":"Điểm ở thay thế","body":["Praiano v\xe0 Cetara mang lại buổi tối chậm r\xe3i, điểm ngắm ho\xe0ng h\xf4n dễ tiếp cận v\xe0 bữa ăn rẻ hơn m\xe0 vẫn nh\xecn ra biển.","Vườn ở Ravello hoặc c\xe1c l\xe0ng đồi của Agerola l\xe0 nơi tr\xfa ẩn tuyệt vời khi Positano qu\xe1 đ\xf4ng."]}],"faqs":[{"q":"L\xfac n\xe0o Spiaggia Grande y\xean tĩnh?","a":["Trước 09:00 hoặc sau 18:30 sẽ c\xf3 chỗ ngồi v\xe0 \xe1nh s\xe1ng mềm để chụp ảnh — mang khăn v\xe0 đến sớm."]},{"q":"Chụp ảnh ở đ\xe2u kh\xf4ng bị chen lấn?","a":["Đi bộ l\xean Via Cristoforo Colombo để c\xf3 s\xe2n thượng nh\xecn từ tr\xean cao hoặc đi xe bu\xfdt đến Nocelle ngắm to\xe0n cảnh Con Đường của C\xe1c Vị Thần."]},{"q":"Ở ngo\xe0i Positano c\xf3 y\xean tĩnh hơn kh\xf4ng?","a":["C\xf3 nếu bạn muốn buổi tối \xeam đềm — Praiano, Minori hoặc Agerola gi\xe1 mềm hơn v\xe0 bạn vẫn c\xf3 thể đi xe bu\xfdt hay ph\xe0 v\xe0o Positano ban ng\xe0y."]}]}')}}]);