"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[283336],{283336:n=>{n.exports=JSON.parse('{"seo":{"title":"Đường M\xf2n Thần Th\xe1nh qua ph\xe0 Amalfi","description":"Đi ph\xe0 từ Positano tới Amalfi, xe bu\xfdt l\xean Bomerano v\xe0 đi bộ Sentiero degli Dei về Nocelle với gợi \xfd v\xe9 v\xe0 thời gian."},"linkLabel":"Path of the Gods qua Amalfi (ph\xe0 + leo n\xfai)","intro":["Lịch tr\xecnh n\xe0y kết hợp chuyến ph\xe0 ngắm cảnh tới Amalfi với chặng đi bộ kinh điển Bomerano → Nocelle tr\xean Path of the Gods. Đ\xe2y l\xe0 trải nghiệm đầy đủ nhất xuất ph\xe1t từ Hostel Brikette, cho bạn ngắm v\xe1ch đ\xe1 từ mặt biển trước khi băng ngang ch\xfang.","H\xe3y d\xe0nh trọn buổi s\xe1ng hoặc đầu giờ chiều. V\xe9 ph\xe0 rất nhanh hết v\xe0o cao điểm, n\xean mua ngay khi tới bến v\xe0 lu\xf4n c\xf3 lịch xe bu\xfdt dự ph\xf2ng nếu biển động."],"sections":[{"id":"prep","title":"Chuẩn bị trước khi rời hostel","body":["Đem theo nước, đồ ăn nhẹ, kem chống nắng v\xe0 \xe1o kho\xe1c mỏng. Kh\xf4ng c\xf3 dịch vụ n\xe0o giữa Bomerano v\xe0 Nocelle sau khi rời khỏi l\xe0ng.","Kiểm tra lịch ph\xe0 mới nhất (Travelmar hoặc Lucibello) v\xe0 giờ xe bu\xfdt SITA. Nhắm tới chuyến s\xe1ng để tr\xe1nh nắng trưa tr\xean sống n\xfai trống trải.","Mang theo ba v\xe9 SITA mỗi người: Amalfi → Bomerano, Nocelle → Positano v\xe0 một v\xe9 dự ph\xf2ng ph\xf2ng khi trễ."]},{"id":"walk-to-port","title":"Đi bộ xuống cảng Positano v\xe0 mua v\xe9 xe bu\xfdt","body":["Rời hostel, rẽ tr\xe1i v\xe0 đi khoảng 100 m\xe9t tới Bar Internazionale. Mua v\xe9 SITA tại quầy tabacchi trước khi tiếp tục xuống dốc.","Theo lối tắt bậc thang xuống Spiaggia Grande. Qu\xe3ng đi mất khoảng 20–30 ph\xfat; chừa th\xeam thời gian v\xe0o ng\xe0y đ\xf4ng kh\xe1ch.","Tới b\xe3i biển, rẽ phải để tới khu cảng nhỏ nơi c\xe1c h\xe3ng ph\xe0 đặt quầy v\xe9 cạnh nhau."]},{"id":"buy-ferry-ticket","title":"Đảm bảo v\xe9 ph\xe0 tới Amalfi","body":["Xem bảng lịch ngo\xe0i quầy Lucibello v\xe0 Travelmar để biết chuyến kế tiếp. V\xe0o m\xf9a cao điểm, ph\xe0 buổi s\xe1ng chạy 30–60 ph\xfat một chuyến.","Mua v\xe9 một chiều tới Amalfi v\xe0 giữ sẵn khi l\xean t\xe0u; từng h\xe3ng sẽ gọi kh\xe1ch v\xe0i ph\xfat trước giờ khởi h\xe0nh.","H\xe0ng xếp rất nhanh, v\xec vậy h\xe3y c\xf3 mặt \xedt nhất 15 ph\xfat trước giờ ph\xe0 bạn chọn để chắc chắn c\xf3 chỗ tr\xean boong."]},{"id":"sail-to-amalfi","title":"L\xean ph\xe0 v\xe0 tận hưởng Amalfi","body":["Chuyến đi mất khoảng 25 ph\xfat. Đứng ph\xeda mạn phải để ngắm Positano v\xe0 Praiano l\xf9i lại ph\xeda sau.","Khi t\xe0u cập bến, xuống nhanh v\xe0 đi thẳng tới bến xe bu\xfdt nằm b\xean tr\xe1i quảng trường cảng.","Nếu c\xf2n thời gian trước chuyến xe, h\xe3y dạo một v\xf2ng quảng trường nh\xe0 thờ hoặc uống espresso — nhưng nhớ quan s\xe1t bến xe Agerola."]},{"id":"bus-to-bomerano","title":"Bắt xe bu\xfdt tới Bomerano","body":["T\xecm xe bu\xfdt SITA ghi “Agerola/Bomerano”. X\xe1c nhận v\xe9 khi l\xean v\xe0 ngồi ph\xeda trước để \xedt bị say cua.","Sau khoảng 45 ph\xfat, xuống tại điểm Bomerano cạnh Ristorante Jerla. Băng qua đường v\xe0 men theo con dốc nhỏ xuống quảng trường.","Rẽ tr\xe1i băng qua quảng trường, rồi theo biển chỉ “Sentiero degli Dei” tới bảng th\xf4ng tin đường m\xf2n."]},{"id":"hike","title":"Đi bộ từ Bomerano tới Nocelle","body":["Đường m\xf2n l\xean xuống khoảng 6 km. Chuẩn bị bước tr\xean bậc đ\xe1, lối hẹp v\xe0 những ban c\xf4ng ngoạn mục nh\xecn xuống bờ biển.","Nghỉ tại c\xe1c điểm ngắm cảnh được đ\xe1nh dấu v\xe0 mang r\xe1c về. T\xf4n trọng n\xf4ng d\xe2n địa phương v\xe0 đi đ\xfang lối c\xf3 biển b\xe1o.","Khi đường đất gặp lối b\xea t\xf4ng dẫn v\xe0o Nocelle, bạn đ\xe3 ho\xe0n th\xe0nh h\xe0nh tr\xecnh."]},{"id":"nocelle","title":"Đi qua Nocelle","body":["Đi theo lối b\xea t\xf4ng v\xe0o thị trấn, giữ b\xean phải tại ng\xe3 rẽ đầu ti\xean để tr\xe1nh cầu thang 1.700 bậc xuống Positano.","Đi khoảng 175 m\xe9t tới điểm dừng xe bu\xfdt gần b\xe3i đỗ nhỏ. Ki-ốt cạnh nh\xe0 thờ b\xe1n đồ uống m\xe1t v\xe0o m\xf9a cao điểm.","C\xf3 mặt trước \xedt nhất 10 ph\xfat; khi b\xe3i đỗ chật, xe bu\xfdt đ\xf4i khi dừng trước nh\xe0 chờ một ch\xfat."]},{"id":"return","title":"Trở lại Positano","body":["L\xean xe bu\xfdt Montepertuso/Nocelle về Positano v\xe0 xuống tại Bar Internazionale hoặc Chiesa Nuova để đi bộ ngắn nhất về hostel.","Nếu muốn đi bộ xuống đường, h\xe3y d\xe0nh 45–60 ph\xfat v\xe0 ch\xfa \xfd xe cộ của d\xe2n địa phương ở c\xe1c kh\xfac cua.","Khi trở lại thị trấn, tự thưởng một ly granita hoặc bữa trưa muộn — phần kh\xf3 nhất trong ng\xe0y đ\xe3 xong."]},{"id":"journey-planner","title":"Gợi \xfd thời gian","body":["06:30 rời hostel → 07:10 ph\xe0 → 08:40 tới Bomerano → leo 3 giờ → 12:30 xe bu\xfdt về → 13:00 tới Positano.","08:00 xuất ph\xe1t vẫn kịp trước bữa trưa: 08:00 rời hostel, 08:40 ph\xe0, 10:15 tới đường m\xf2n, 14:30 về thị trấn.","C\xe1c chuyến muộn ph\xf9 hợp v\xe0o m\xf9a thấp điểm, nhưng lu\xf4n kiểm tra chuyến xe Nocelle cuối c\xf9ng trước khi chọn giờ chiều."]}],"essentialsTitle":"Vật dụng cần thiết","essentials":["V\xe9 ph\xe0 Positano ⇄ Amalfi v\xe0 ba v\xe9 xe bu\xfdt SITA mỗi người (Amalfi → Bomerano, Nocelle → Positano, v\xe9 dự ph\xf2ng).","1,5–2 l\xedt nước, đồ ăn nhẹ hoặc panino mang theo, kem chống nắng v\xe0 mũ.","Gi\xe0y chắc ch\xe2n c\xf3 độ b\xe1m cho bậc đ\xe1 v\xe0 lối m\xf2n gồ ghề."],"typicalCostsTitle":"Chi ph\xed tham khảo","typicalCosts":["Ph\xe0 Positano → Amalfi: €12–€16 một chiều t\xf9y h\xe3ng v\xe0 m\xf9a.","Xe bu\xfdt SITA Amalfi → Bomerano v\xe0 Nocelle → Positano: €2–€4 mỗi chặng nếu mua trước.","Đồ ăn nhẹ hoặc panini từ mini-market dọc đường: €4–€8."],"tipsTitle":"Mẹo h\xe0nh tr\xecnh","tips":["Khởi h\xe0nh sớm để tr\xe1nh nắng n\xf3ng v\xe0 đảm bảo chỗ tr\xean ph\xe0 — chuyến đầu ng\xe0y l\xe0 y\xean tĩnh nhất.","Mua panino mang đi tại cửa h\xe0ng tạp h\xf3a thứ hai tr\xean Via Pasitea để c\xf3 bữa trưa gi\xe1 mềm tr\xean đường m\xf2n.","Giữ v\xe9 ph\xe0 v\xe0 xe bu\xfdt trong t\xfai chống nước để xuất tr\xecnh nhanh khi l\xean phương tiện."],"warningsTitle":"Cảnh b\xe1o","warnings":["Đừng đi theo cầu thang từ Nocelle xuống Positano trừ khi bạn muốn thử th\xe1ch 1.700 bậc v\xe0 kết th\xfac ở xa hostel.","Biển động c\xf3 thể hủy ph\xe0 m\xe0 b\xe1o trước rất \xedt — h\xe3y kiểm tra t\xecnh trạng tại cảng v\xe0 chuyển sang phương \xe1n xe bu\xfdt nếu cần.","Buổi chiều m\xf9a h\xe8 nắng gắt v\xe0 trống trải; nếu lỡ chuyến s\xe1ng, h\xe3y c\xe2n nhắc khởi h\xe0nh cuối buổi chiều thay v\xec đi giữa trưa."],"steps":[{"name":"Đi bộ tới cảng","text":"Rời Hostel Brikette, mua v\xe9 xe bu\xfdt tại Bar Internazionale v\xe0 xuống Spiaggia Grande."},{"name":"Mua v\xe9 ph\xe0","text":"Chọn chuyến Travelmar hoặc Lucibello kế tiếp tới Amalfi v\xe0 xếp h\xe0ng trước 15 ph\xfat."},{"name":"Đi ph\xe0 tới Amalfi","text":"Tận hưởng h\xe0nh tr\xecnh 25 ph\xfat v\xe0 đi thẳng tới bến xe cạnh cảng."},{"name":"Xe bu\xfdt tới Bomerano","text":"Đi xe SITA ghi Agerola/Bomerano v\xe0 men theo con phố phụ tới điểm đầu đường m\xf2n."},{"name":"Leo tới Nocelle","text":"Đi theo biển b\xe1o Sentiero degli Dei tr\xean sống n\xfai cho tới khi gặp lối b\xea t\xf4ng."},{"name":"Quay về Positano","text":"Bắt xe bu\xfdt Nocelle về Bar Internazionale hoặc đi bộ theo đường nếu xe đầy."}],"faqsTitle":"C\xe2u hỏi thường gặp","faqs":[{"q":"T\xf4i n\xean mua v\xe9 ở đ\xe2u?","a":["Mua v\xe9 xe bu\xfdt SITA tại Bar Internazionale trước khi xuống dốc, v\xe0 mua v\xe9 ph\xe0 trực tiếp tại quầy Lucibello hoặc Travelmar ở cảng."]},{"q":"T\xf4i cần chừa bao nhi\xeau thời gian ở Amalfi?","a":["H\xe3y d\xe0nh 20–30 ph\xfat để chuyển từ ph\xe0 sang xe bu\xfdt Agerola. C\xf3 thể tranh thủ uống c\xe0 ph\xea nhanh, nhưng lu\xf4n ở trong tầm mắt của bến xe để kh\xf4ng lỡ chuyến."]},{"q":"Nếu ph\xe0 bị hủy th\xec sao?","a":["Kiểm tra t\xecnh trạng tại cảng. Nếu t\xe0u ngừng chạy, chuyển sang tuyến xe bu\xfdt qua Amalfi — h\xe0nh tr\xecnh đi bộ vẫn giống nhau khi tới Bomerano."]}],"toc":{"onThisPage":"Nội dung ch\xednh","essentials":"Vật dụng cần thiết","costs":"Chi ph\xed tham khảo","tips":"Mẹo h\xe0nh tr\xecnh","warnings":"Cảnh b\xe1o","faqs":"C\xe2u hỏi thường gặp"}}')}}]);