"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[287022],{287022:e=>{e.exports=JSON.parse('{"seo":{"title":"पाथ ऑफ द गॉड्स (अमाल्फी फेरी मार्ग)","description":"पोसीटानो से अमाल्फी तक फेरी लें, बोमेरानो के लिए बस पकड़ें और टिकट सलाह व समय-सारिणी के साथ सेंटिएरो देग्ली देई को नॉचेल तक ट्रेक करें।"},"linkLabel":"पाथ ऑफ द गॉड्स अमाल्फी (फेरी + हाइक) से","intro":["यह यात्रा योजना एक दर्शनीय फेरी राइड को बोमेरानो → नॉचेल के क्लासिक पाथ ऑफ द गॉड्स ट्रैवर्स के साथ जोड़ती है। हॉस्टल ब्रिकेत्ते से यह सबसे पूर्ण अनुभव है—समुद्र तल से चट्टानों को निहारने के बाद आप उन्हीं चट्टानों पर पैदल चलते हैं।","एक पूरा सुबह या शुरुआती दोपहर का समय रखें। पीक सीज़न में फेरी जल्दी भर जाती हैं, इसलिए बंदरगाह पहुँचते ही टिकट खरीद लें और समुद्र खुरदरे हो जाएँ तो बैकअप बस समय-सारणी पास रखें।"],"sections":[{"id":"prep","title":"हॉस्टल छोड़ने से पहले","body":["पानी, स्नैक्स, सनस्क्रीन और परतें पैक करें। बोमेरानो और नॉचेल के बीच गाँवों से बाहर निकलते ही कोई सेवा उपलब्ध नहीं होती।","ताज़ा फेरी समय-सारिणी (Travelmar या Lucibello) और SITA बस टाइमटेबल जांचें। दोपहर की धूप से बचने के लिए सुबह की फेरी चुनें।","प्रति व्यक्ति तीन SITA बस टिकट साथ रखें: अमाल्फी → बोमेरानो, नॉचेल → पोसीटानो और देरी की स्थिति में एक अतिरिक्त।"]},{"id":"walk-to-port","title":"पोसीटानो पोर्ट तक पैदल जाएं और बस टिकट खरीदें","body":["हॉस्टल से निकलें, बाईं ओर मुड़ें और लगभग 100 मीटर चलकर बार इंटरनाज़ियोनाले पहुँचें। आगे बढ़ने से पहले तबाक्की काउंटर से SITA टिकट खरीदें।","स्पियाज्जा ग्रांदे की ओर जाने वाली शॉर्टकट सीढ़ियों का अनुसरण करें। पैदल यात्रा लगभग 20–30 मिनट की है; व्यस्त दिनों में अतिरिक्त समय रखें।","बीच पर पहुँचकर दाईं ओर मुड़ें और छोटे बंदरगाह तक जाएँ जहाँ फेरी ऑपरेटरों के टिकट बूथ एक-दूसरे के पास स्थित हैं।"]},{"id":"buy-ferry-ticket","title":"अमाल्फी के लिए फेरी टिकट सुरक्षित करें","body":["Lucibello और Travelmar के बोर्ड देखें ताकि अगली नाव का समय पता चल सके। सीज़न में सुबह की नावें आमतौर पर हर 30–60 मिनट में चलती हैं।","अमाल्फी के लिए एकतरफा टिकट खरीदें और बोर्डिंग के लिए हाथ में रखें; हर ऑपरेटर प्रस्थान से कुछ मिनट पहले यात्रियों को बुलाता है।","कतारें जल्दी लग जाती हैं, इसलिए अपनी चुनी हुई फेरी से कम से कम 15 मिनट पहले पहुँचें ताकि डेक पर जगह सुनिश्चित हो सके।"]},{"id":"sail-to-amalfi","title":"फेरी का सफर और अमाल्फी का आनंद लें","body":["क्रॉसिंग लगभग 25 मिनट की होती है। पोसीटानो और प्राइआनो के दृश्य देखने के लिए स्टारबोर्ड (दाईं) तरफ खड़े हों।","डॉक करते ही तेजी से उतरें और सीधे उस बस टर्मिनल की ओर जाएँ जो बंदरगाह पियाज़ा के बाईं ओर स्थित है।","अगर बस से पहले समय हो तो अमाल्फी के कैथेड्रल स्क्वायर में घूम लें या एक एस्प्रेसो लें—बस बे पर नज़र बनाए रखें ताकि एजे़रोला वाली बस ना छूटे।"]},{"id":"bus-to-bomerano","title":"बोमेरानो जाने वाली बस पकड़ें","body":["“Agerola/Bomerano” लिखी SITA बस को देखें। बस में चढ़ते समय टिकट वैलिडेट करें और कम मोड़ों के लिए आगे की सीटें चुनें।","करीब 45 मिनट बाद, रिस्तोरांते जेरला के पास बोमेरानो स्टॉप पर उतरें। सड़क पार करें और साइड स्ट्रीट से नीचे पियाज़ा की ओर बढ़ें।","चौक के पार बाईं ओर बढ़ें और “Sentiero degli Dei” के संकेतों का पालन करते हुए ट्रेलहेड सूचना पट्ट तक पहुँचें।"]},{"id":"hike","title":"बोमेरानो से नॉचेल तक ट्रेक करें","body":["ट्रेल लगभग 6 किलोमीटर तक ऊपर-नीचे होता है। पत्थर की सीढ़ियाँ, सँकरी धारियाँ और तट के रोमांचक दृश्य पॉइंट्स की उम्मीद करें।","चिह्नित व्यूपॉइंट्स पर विराम लें और कचरा साथ लेकर लौटें। स्थानीय किसानों का सम्मान करें और चिह्नित पथ से बाहर न जाएँ।","जब कच्चा रास्ता नॉचेल की कंक्रीट वॉकवे से मिलता है, समझ लें कि यात्रा पूरी हो गई है।"]},{"id":"nocelle","title":"नॉचेल में दिशा-निर्देश","body":["पक्के रास्ते का अनुसरण करते हुए गाँव में प्रवेश करें और पहली मोड़ पर दाईं ओर बने रहें ताकि पोसीटानो तक जाने वाली 1,700 सीढ़ियों से बच सकें।","करीब 175 मीटर चलकर छोटे कार पार्क के पास स्थित बस स्टॉप तक पहुँचें। मौसम के अनुसार चर्च के पास का कियोस्क ठंडे पेय बेचता है।","कम से कम 10 मिनट पहले पहुँचें; पार्किंग क्षेत्र व्यस्त होने पर बसें शेल्टर से थोड़ा पहले भी रुक सकती हैं।"]},{"id":"return","title":"पोसीटानो वापस लौटें","body":["मोंटेपरटूसो/नॉचेल बस पकड़कर पोसीटानो लौटें और सबसे कम पैदल दूरी के लिए बार इंटरनाज़ियोनाले या चिएसा नुओवा पर उतरें।","यदि आप पैदल नीचे उतरना चाहें तो 45–60 मिनट का समय रखें और मोड़ों पर स्थानीय यातायात से सावधान रहें।","शहर में वापस आने के बाद खुद को ग्रानीटा या देर से लंच से पुरस्कृत करें—दिन का सबसे कठिन हिस्सा पूरा हो चुका है।"]},{"id":"journey-planner","title":"उदाहरण समय-सारिणी","body":["06:30 हॉस्टल से प्रस्थान → 07:10 फेरी → 08:40 बोमेरानो आगमन → 3 घंटे की हाइक → 12:30 बस वापसी → 13:00 पोसीटानो वापसी।","08:00 प्रस्थान भी लंच से पहले फिट बैठता है: 08:00 हॉस्टल से निकलें, 08:40 फेरी, 10:15 ट्रेल पर पहुँचें, 14:30 तक शहर लौटें।","शोल्डर सीज़न में देर से नौकाएँ संभव हैं, लेकिन दोपहर की शुरुआत चुनने से पहले अंतिम नॉचेल बस की पुष्टि ज़रूर करें।"]}],"essentialsTitle":"जरूरी सामान","essentials":["पोसीटानो ⇄ अमाल्फी फेरी टिकट और प्रति व्यक्ति तीन SITA बस टिकट (अमाल्फी → बोमेरानो, नॉचेल → पोसीटानो, अतिरिक्त)।","1.5–2 लीटर पानी, स्नैक्स या पैक किया हुआ पनिनी, सनस्क्रीन और टोपी।","असमान सीढ़ियों और चट्टानी धारियों के लिए ग्रिप वाले आरामदायक जूते।"],"typicalCostsTitle":"सामान्य लागत","typicalCosts":["पोसीटानो → अमाल्फी फेरी: ऑपरेटर और सीज़न के आधार पर एकतरफा €12–€16।","SITA बस अमाल्फी → बोमेरानो और नॉचेल → पोसीटानो: अग्रिम खरीद पर प्रति सवारी €2–€4।","रास्ते के मिनी-मार्केट से स्नैक्स या पनिनी: €4–€8।"],"tipsTitle":"रूट टिप्स","tips":["सुबह जल्दी शुरुआत करें ताकि गर्मी से बचें और फेरी सीटें सुनिश्चित हों—पहली नावें सबसे शांत होती हैं।","विया पासितेआ के दूसरे मिनी-मार्केट से पैक किया हुआ पनिनी लें ताकि ट्रेल लंच सस्ता पड़े।","फेरी और बस टिकटों को वाटरप्रूफ पाउच में साथ रखें ताकि बोर्डिंग के समय तुरंत दिखा सकें।"],"warningsTitle":"सावधानियाँ","warnings":["नॉचेल से पोसीटानो की ओर संकेतित सीढ़ियों पर तभी उतरें जब आप जानबूझकर 1,700 सीढ़ियों का लंबा अवरोह करना चाहें—यह हॉस्टल से काफी दूर खत्म होता है।","तेज़ लहरों की स्थिति में फेरी कम सूचना पर रद्द हो सकती हैं—बंदरगाह पर स्थिति देखें और आवश्यकता पड़ने पर बस मार्ग अपनाएँ।","गर्मियों की दोपहरें खुली धूप में बहुत गर्म होती हैं; सुबह की फेरी छूट जाए तो दोपहर के बजाय देर दोपहर का विकल्प चुनें।"],"steps":[{"name":"बंदरगाह तक पैदल जाएं","text":"हॉस्टल ब्रिकेत्ते से निकलें, बार इंटरनाज़ियोनाले पर बस टिकट खरीदें और स्पियाज्जा ग्रांदे की ओर बढ़ें।"},{"name":"फेरी टिकट खरीदें","text":"अगली Travelmar या Lucibello फेरी चुनें और 15 मिनट पहले कतार में लगें।"},{"name":"अमाल्फी तक नौकायन","text":"25 मिनट की सवारी का आनंद लें और बंदरगाह के पास ही स्थित बस स्टैंड तक सीधे जाएँ।"},{"name":"बोमेरानो के लिए बस","text":"Agerola/Bomerano लिखी SITA बस लें और साइड स्ट्रीट से ट्रेलहेड तक पहुँचें।"},{"name":"नॉचेल तक हाइक","text":"सेंटिएरो देग्ली देई के वेमार्क का पालन करें और जहाँ कंक्रीट पथ शुरू हो वहीं समाप्त करें।"},{"name":"पोसीटानो लौटें","text":"नॉचेल बस से बार इंटरनाज़ियोनाले वापस आएँ या बसें भरी हों तो सड़क से पैदल उतरें।"}],"faqsTitle":"अक्सर पूछे जाने वाले प्रश्न","faqs":[{"q":"मुझे परिवहन टिकट कहाँ से खरीदने चाहिए?","a":["वियाले पासितेआ पर बार इंटरनाज़ियोनाले से SITA बस टिकट खरीदें, और बंदरगाह कियोस्क पर Lucibello या Travelmar से फेरी टिकट सीधे लें।"]},{"q":"मुझे अमाल्फी में कितना समय रखना चाहिए?","a":["फेरी से एजे़रोला बस में बदलने के लिए 20–30 मिनट का बफर रखें। इस समय का उपयोग त्वरित कॉफी के लिए कर सकते हैं, लेकिन बस बे की नज़रों से दूर मत जाएँ ताकि बोर्डिंग न छूटे।"]},{"q":"अगर फेरी रद्द हो जाए तो क्या करें?","a":["बंदरगाह पर स्थितियों की जाँच करें। यदि नावें चलना बंद कर दें तो अमाल्फी मार्ग वाली बस ले लें—एक बार बोमेरानो पहुँचने पर ट्रेल वही रहता है।"]}],"toc":{"onThisPage":"इस पृष्ठ पर","essentials":"जरूरी सामान","costs":"सामान्य लागत","tips":"रूट टिप्स","warnings":"सावधानियाँ","faqs":"प्रश्नोत्तर"}}')}}]);