"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[307486],{307486:n=>{n.exports=JSON.parse('{"seo":{"title":"Đến Chiesa Nuova/Bar Internazionale: đường tới Hostel Brikette","description":"Bản dịch đang được chuẩn bị."},"linkLabel":"Đến Chiesa Nuova/Bar Internazionale: đường tới Hostel Brikette","intro":"Đến bằng xe bu\xfdt? “Chiesa Nuova” hoặc “Bar Internazionale” l\xe0 điểm dừng gần nhất với hostel. Lộ tr\xecnh ngắn n\xe0y giảm tối đa số bậc thang cho đầu gối v\xe0 h\xe0nh l\xfd của bạn.","image":{"alt":"Điểm dừng SITA Chiesa Nuova ở Positano ph\xeda trước Bar Internazionale","caption":"Điểm dừng Positano (Chiesa Nuova) — h\xe3y đợi b\xean ngo\xe0i Bar Internazionale; v\xe0o m\xf9a h\xe8 n\xean đến sớm một ch\xfat v\xec xe bu\xfdt nhanh ch\xf3ng đầy chỗ."},"toc":{"onThisPage":"Nội dung trang","before":"Trước khi l\xean đường","steps":"Từng bước","knees":"Đầu gối & h\xe0nh l\xfd","faqs":"C\xe2u hỏi thường gặp"},"sections":[{"id":"why-stop","title":"V\xec sao n\xean xuống ở đ\xe2y","body":["Điểm dừng xe bu\xfdt trước qu\xe1n c\xe0 ph\xea/qu\xe1n bar Bar Internazionale c\xe1ch Hostel Brikette khoảng 140 m\xe9t."]},{"id":"bus-basics","title":"Giờ xe bu\xfdt v\xe0 v\xe9","body":["Mua v\xe9 SITA tại cửa h\xe0ng tabacchi s\xe1t điểm dừng hoặc đ\xf3ng dấu v\xe9 đ\xe3 mua trước ngay khi l\xean xe; giữ th\xeam một v\xe9 dự ph\xf2ng cho chuyến về muộn.","Sau 22:00 c\xe1c chuyến xe bu\xfdt thưa dần — h\xe3y kiểm tra lịch tr\xecnh trước v\xe0 cố gắng bắt chuyến \xe1p ch\xf3t để tr\xe1nh phải đi taxi."]}],"beforeList":["Xem trước lịch chạy của SITA để kh\xf4ng phải đứng b\xean đường chờ chuyến cuối c\xf9ng.","Đ\xf3ng dấu v\xe9 ngay khi bước l\xean xe — thanh tra c\xf3 thể phạt v\xe9 chưa x\xe1c thực d\xf9 chỉ đi qu\xe3ng ngắn.","Nếu bạn đến sau nửa đ\xeam, h\xe3y nhắn cho hostel để nh\xe2n vi\xean trực đ\xeam chuẩn bị sẵn m\xe3 cửa."],"stepsList":["Từ điểm dừng trước Bar Internazionale, đi bộ l\xean dốc hơn 100 m\xe9t (330 feet) dọc theo Viale Pasitea — l\xe0m theo lộ tr\xecnh tr\xean bản đồ b\xean dưới để được hướng dẫn từng bước.","Đến kh\xfac cua, đi qua biển hiệu Hostel Brikette ở tầng s\xe2n thượng rồi rẽ l\xean cầu thang b\xean phải con đường tới cửa ch\xednh."],"stepsMapEmbedUrl":"https://www.google.com/maps/embed?origin=mfe&mode=walking&pb=!1m5!4m4!4m1!2sBar+Internazionale!4m1!2sHostel+Brikette","kneesList":["To\xe0n bộ đường đi đ\xe3 được l\xe1t, chỉ c\xf3 một đoạn cầu thang ngắn ở lối v\xe0o.","Nếu đoạn dốc khiến đầu gối mỏi, h\xe3y nghỉ ngắn — xe cộ cũng tự động giảm tốc ở kh\xfac cua."],"kneesDockPrefix":"Bạn đến bằng ph\xe0 trước?","kneesDockLinkLabel":"Bến t\xe0u → đường mang h\xe0nh l\xfd l\xean hostel","kneesPorterPrefix":"Cần hỗ trợ với h\xe0nh l\xfd nặng?","kneesPorterLinkLabel":"Hướng dẫn dịch vụ khu\xe2n v\xe1c","faqs":[{"q":"C\xf3 vỉa h\xe8 suốt qu\xe3ng đường kh\xf4ng?","a":"C\xf3 — tuyến đường đi tr\xean vỉa h\xe8 của trục đường ch\xednh n\xean tr\xe1nh được c\xe1c cầu thang d\xe0i."},{"q":"Ban đ\xeam c\xf3 đủ s\xe1ng kh\xf4ng?","a":"C\xf3 đ\xe8n đường; bạn n\xean bật đ\xe8n pin điện thoại khi đi trong hẻm để nh\xecn r\xf5 hơn."}],"howtoSteps":["Từ điểm dừng “Chiesa Nuova”, đi bộ 2–5 ph\xfat l\xe0 tới hostel","Chọn c\xe1c hẻm c\xf3 \xedt bậc thang nhất; tr\xe1nh những cầu thang d\xe0i","Mang nhiều h\xe0nh l\xfd? H\xe3y c\xe2n nhắc đặt dịch vụ khu\xe2n v\xe1c đ\xf3n tại điểm dừng"]}')}}]);