"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[309098],{309098:i=>{i.exports=JSON.parse('{"seo":{"title":"Пляжный хоппинг на Амальфи","description":"Какие пляжи сочетать за день, варианты доступа и расходы."},"linkLabel":"Пляжный хоппинг на Амальфитанском побережье","intro":["Составьте маршрут по пляжам, сочетая поездки на пароме, виды со скал и неспешный отдых на берегу."],"sections":[{"id":"morning","title":"Утро: Позитано → Форнилло","body":["Начните день на Spiaggia Grande с короткого заплыва, затем по прибрежной тропе дойдите до Форнилло к 09:30, пока море спокойное.","Если нужна тень, забронируйте лежаки заранее; иначе возьмите с собой лёгкий зонтик."]},{"id":"midday","title":"Полдень: Амальфи или Атрани","body":["Сядьте на поздний утренний паром в Амальфи, пообедайте на пирсе и искупайтесь на городском пляже или в бухточке Атрани.","Оставьте сумки в камере хранения Амальфи, чтобы гулять налегке."]},{"id":"evening","title":"Вечер: купание на закате","body":["Завершите день на Лаурито или Аренцо ради видов в \xabзолотой час\xbb: оба пляжа смотрят на запад и после 17:00 становятся тише.","Успейте на последний паром или заранее закажите обратное такси, чтобы не подниматься в темноте."]}],"tips":["Берите быстросохнущее полотенце и смену одежды, чтобы чувствовать себя комфортно на паромах.","Запаситесь перекусами и минимум литром воды — киоски на пляже быстро опустошаются в загруженные дни."],"faqs":[{"q":"Нужно ли бронировать лежаки заранее?","a":["Да, в июле–августе. Свяжитесь с пляжным клубом в WhatsApp накануне, чтобы закрепить места в первых рядах."]},{"q":"Можно ли приносить свою еду?","a":["На бесплатных участках разрешены пикники. В пляжных клубах обычно терпимо относятся к лёгким закускам, но напитки предпочитают продавать сами."]}]}')}}]);