"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[315991],{315991:i=>{i.exports=JSON.parse('{"meta":{"title":"बस से रावेल्लो से पोसिटानो | हॉस्टल ब्रिकेट","description":"रावेल्लो से पोसिटानो तक अमाल्फी के रास्ते SITA बसों द्वारा चरण-दर-चरण दिशा-निर्देश, जिनमें समय-सारिणी सुझाव, स्टॉप विवरण और हॉस्टल ब्रिकेट तक अंतिम पैदल मार्ग शामिल है।"},"header":{"eyebrow":"हॉस्टल ब्रिकेट यात्रा मार्गदर्शिका","title":"रावेल्लो से पोसिटानो – अमाल्फी के रास्ते बस से","description":"रावेल्लो से पोसिटानो तक अमाल्फी में एक सरल बदलाव के साथ यात्रा करें। यह मार्गदर्शिका बताती है कि SITA टिकट कहाँ खरीदें और वैलिडेट करें, समय-सारिणी कैसे पढ़ें, अमाल्फी में बदलाव करते समय क्या अपेक्षा करें, और पोसिटानो के Chiesa Nuova स्टॉप से हॉस्टल ब्रिकेट तक छोटा पैदल मार्ग कैसे तय करें।"},"tip":{"eyebrow":"सुझाव","body":"सभी परिवहन विकल्प चाहिए? फ़ेरी, टैक्सी और मिश्रित यात्रा कार्यक्रमों के लिए <GuideLink>{{guideLabel}}</GuideLink> खोलें।"},"journeyOverview":{"title":"यात्रा का अवलोकन","description":"कनेक्शनों पर निर्भर करते हुए लगभग 1.5–2.5 घंटे का समय रखें। पहले रावेल्लो से अमाल्फी तक SITA बस लें, फिर पोसिटानो/सोरेंतो जाने वाली दूसरी SITA बस में बदलें। ऊँचे मौसम में बसें अक्सर चलती हैं और भीड़भाड़ हो सकती है — कतार के लिए थोड़ा पहले पहुँचें।"},"sitaTickets":{"title":"SITA बसें और टिकट","points":["रावेल्लो और अमाल्फी के बीच बसें SITA (SITA Sud Srl) संचालित करती है।","पहले आपको अमाल्फी तक जाना होगा, फिर पोसिटानो की ओर जाने वाली दूसरी बस में बदलना होगा।","रावेल्लो → अमाल्फी के लिए दो विकल्प चलते हैं: 5110 और 5120। 5110 आमतौर पर ~10 मिनट तेज़ है।","टिकट अख़बार स्टैंड या तंबाकू दुकानों (तबाक्की) से खरीदें और बोर्डिंग के समय वैलिडेट करने के लिए तैयार रखें।"]},"timetable":{"title":"समय-सारिणी","description":"ताज़ा समय-सारिणी <TimetableLink>{{timetableLabel}}</TimetableLink> पर देखें।","notesTitle":"समय-सारिणी कैसे पढ़ें","points":["पहले कॉलम की पंक्तियाँ मार्ग के प्रत्येक स्टॉप को सूचीबद्ध करती हैं।","कॉलम अक्षरों से चिह्नित निर्धारित यात्राओं का प्रतिनिधित्व करते हैं।","सोमवार से शनिवार यात्रा कर रहे हैं? ‘F’ और ‘G’ कॉलम देखें।","रविवार या सार्वजनिक अवकाश पर यात्रा कर रहे हैं? ‘G’ और ‘H’ कॉलम का उपयोग करें।"],"linkLabel":"Positano.com (SITA समय-सारिणी अवलोकन)"},"ravelloToAmalfi":{"title":"रावेल्लो → अमाल्फी","points":["रावेल्लो से अमाल्फी तक SITA 5110 या 5120 लें (5110 आमतौर पर तेज़ है)।","बोर्डिंग के समय अपना टिकट वैलिडेट करें और गंतव्य तक सुरक्षित रखें।","पोसिटानो के लिए कनेक्शन पकड़ने हेतु अमाल्फी बस स्टेशन पर उतरें।"]},"waitingInAmalfi":{"title":"अमाल्फी में प्रतीक्षा","points":["अमाल्फी पहुँचने पर इंतज़ार करते समय शहर के केंद्र का दौरा कर सकते हैं।","पोसिटानो जाने वाली बस पकड़ने के लिए उसी बस स्टेशन क्षेत्र में लौटें जहाँ आप उतरे थे।","अमाल्फी कोस्ट मार्ग पर पोसिटानो/सोरेंतो के लिए चिह्नित किसी भी SITA सेवा में सवार हों।"]},"arrivingPositano":{"title":"पोसिटानो में आगमन (Chiesa Nuova)","points":["सबसे नज़दीकी SITA स्टॉप पोसिटानो (Chiesa Nuova) है, जो वाया जी. मारकोनी पर स्थित है।","यह हॉस्टल के प्रवेश द्वार से लगभग 140 मीटर (490 फीट) दूर है।","आप ‘Bar Internazionale’ नामक कैफ़े/बार के बाहर प्रतीक्षा करेंगे।"],"figure":{"alt":"बार इंटरनाज़ियोनाले के बाहर पोसिटानो का Chiesa Nuova SITA स्टॉप","caption":"ग्रीष्मकाल में बसें जल्दी भर सकती हैं, इसलिए सीट सुनिश्चित करने के लिए कुछ मिनट पहले पहुँचे।"}},"walking":{"title":"बस स्टॉप से हॉस्टल तक पैदल चलें","points":["Chiesa Nuova बस स्टॉप से लगभग 100 मीटर (330 फीट) आगे बढ़ते रहें।","दाईं ओर मुड़ें और सीढ़ियों का पालन करते हुए हॉस्टल के मुख्य दरवाज़े तक पहुँचें।","हमारा साइन टैरेस के पास दिखाई देगा; प्रवेश ठीक मोड़ के ऊपर है।"]},"links":{"guideOverview":"यहाँ कैसे पहुँचे अवलोकन"}}')}}]);