"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[320794],{320794:n=>{n.exports=JSON.parse('{"seo":{"title":"Tạp ho\xe1 v\xe0 hiệu thuốc ở Positano","description":"Nơi mua nhu yếu phẩm tại Positano: si\xeau thị nhỏ, giờ mở cửa muộn v\xe0 th\xf4ng tin hiệu thuốc."},"linkLabel":"Tạp ho\xe1 v\xe0 hiệu thuốc ở Positano","intro":["Positano kh\xf4ng c\xf3 si\xeau thị lớn, v\xec vậy h\xe3y chuẩn bị mua sắm ở cửa h\xe0ng nhỏ, chợ hằng tuần v\xe0 v\xe0i hiệu thuốc uy t\xedn cho nhu yếu phẩm."],"sections":[{"id":"mini-markets","title":"Cửa h\xe0ng tạp h\xf3a & deli","body":["C\xe1c cửa h\xe0ng tr\xean Via Pasitea v\xe0 Via dei Mulini b\xe1n b\xe1nh m\xec mới, ph\xf4 mai v\xe0 salad chế biến sẵn; diện t\xedch hạn chế nhưng nhập h\xe0ng mỗi ng\xe0y.","Giờ mở cửa theo nhịp nghỉ trưa — h\xe3y mua trước 13:00 hoặc sau 16:30 để tr\xe1nh cửa đ\xf3ng."]},{"id":"produce","title":"Rau củ & chợ","body":["Quầy rau củ b\xe0y ngay kh\xfac cua Sponda v\xe0 trạm Chiesa Nuova mỗi s\xe1ng; mang tiền lẻ để thanh to\xe1n nhanh.","Thứ Tư hằng tuần, Montepertuso (xe bu\xfdt 13) c\xf3 chợ nhỏ b\xe1n rau theo m\xf9a, sữa v\xe0 thịt nguội."]},{"id":"pharmacies","title":"Hiệu thuốc & chăm s\xf3c sức khỏe","body":["Farmacia Positano (Via Pasitea) c\xf3 thuốc cơ bản, kem chống nắng v\xe0 thuốc say t\xe0u xe; mang theo toa nếu cần thuốc đặc trị.","Dịch vụ ngo\xe0i giờ lu\xe2n phi\xean — xem bảng th\xf4ng b\xe1o tr\xean cửa hiệu để biết số trực nếu cần thuốc muộn."]},{"id":"late-hours","title":"Nhu yếu phẩm buổi tối","body":["Một số alimentari mở tới 22:00 v\xe0o m\xf9a cao điểm; ngo\xe0i ra c\xf3 m\xe1y b\xe1n h\xe0ng 24/7 gần trạm xe bu\xfdt.","Nếu cần gấp, Sorrento (45–60 ph\xfat đi xe) c\xf3 si\xeau thị lớn v\xe0 ph\xf2ng cấp cứu bệnh viện."]}],"gallery":[{"alt":"Cửa h\xe0ng dọc đường ch\xednh Positano","caption":"Cửa tiệm ven trục đường"},{"alt":"Giỏ h\xe0ng với tr\xe1i c\xe2y","caption":"Tr\xe1i c\xe2y v\xe0 nhu yếu phẩm"}],"tips":["Mang t\xfai tote nhẹ hoặc balo để x\xe1ch đồ l\xean cầu thang m\xe0 kh\xf4ng dồn lực v\xe0o một tay.","Giữ chai nước t\xe1i sử dụng: đa số cửa h\xe0ng b\xe1n nước 1,5 L gi\xe1 rẻ v\xe0 s\xe2n thượng hostel c\xf3 v\xf2i lọc."],"faqs":[{"q":"T\xf4i c\xf3 mua được đồ kh\xf4ng gluten ở đ\xe2y kh\xf4ng?","a":["C\xf3 — t\xecm kệ “senza glutine” trong c\xe1c cửa h\xe0ng nhỏ hoặc hỏi hiệu thuốc; lượng h\xe0ng \xedt n\xean thấy l\xe0 mua ngay."]},{"q":"Hiệu thuốc c\xf3 mở cửa Chủ nhật kh\xf4ng?","a":["Lu\xf4n c\xf3 \xedt nhất một hiệu mở với giờ r\xfat gọn; xem bảng ph\xe2n c\xf4ng v\xe0 gọi trước nếu cần thuốc k\xea toa."]}],"fallback":"Nhu yếu phẩm c\xf3 mặt khắp Positano — h\xe3y mua sớm trong ng\xe0y v\xe0 ghi ch\xfa số hiệu thuốc địa phương."}')}}]);