"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[321069],{321069:e=>{e.exports=JSON.parse('{"seo":{"title":"포시타노 가성비 맛집","description":"테이크아웃, 캐주얼 식당, 피크닉 아이디어로 식비를 줄이세요."},"linkLabel":"포시타노 가성비 맛집","heroAlt":"포시타노의 가성비 테이크아웃 음식","intro":["테이크아웃 클래식, 델리 피크닉, 동네 베이커리를 조합하면 포시타노에서도 합리적인 비용으로 맛있게 먹을 수 있습니다."],"sections":[{"id":"grab-and-go","title":"테이크아웃 기본","body":["비아 데이 물리니 일대 바에서 정오 전에 피자 알 타그리오, 파니니, 아란치니를 구입하면 줄을 피할 수 있습니다.","에스프레소는 테이블이 아닌 바 카운터(“알 반코”)에서 주문하면 현지 요금인 1.2유로 정도로 즐길 수 있습니다."]},{"id":"casual-dining","title":"캐주얼 식당","body":["해변에서 한 단계 위 언덕에 있는 트라토리아를 찾아보세요. 코페르토가 적고 하우스 와인을 카라프로 주문할 수 있습니다.","에피타이저를 나눠 먹고 디저트는 생략하며, 수돗물(“아쿠아 델 루비네토”)을 요청하면 식비를 줄일 수 있습니다."]},{"id":"picnics","title":"전망과 함께하는 피크닉","body":["미니마켓에서 치즈, 포카치아, 과일을 구입해 호스텔 테라스나 포르닐로 공용 구역에서 피크닉을 즐기세요.","재사용 가능한 식기와 냅킨을 챙기면 일회용품을 줄이고 추가 구매를 피할 수 있습니다."]},{"id":"sweet-breaks","title":"달콤한 휴식","body":["길거리 카트의 레몬 그라니타는 약 3유로로 시원한 대안입니다. 바에서 칵테일을 주문하는 것보다 훨씬 경제적입니다.","델리치아 알 리모네나 티라미수 한 조각을 베이커리에서 나눠 먹으면 부담 없는 디저트가 됩니다."]}],"tips":["메누 델 조르노(점심 세트)를 물어보세요. 파스타+물+커피를 18유로 이하로 제공하는 곳이 많습니다.","접이식 용기를 챙기면 남은 음식을 포장해 호스텔에서 다시 먹을 수 있습니다."],"recommendationsTitle":"추천 맛집","recommendations":[{"name":"콜리나 베이커리","note":"파니니, 피자 알 타그리오, 샐러드, 페이스트리를 빠르게 테이크아웃할 수 있습니다."},{"name":"동네 델리 카운터","note":"델리 플레이트와 포카치아, 피크닉 용품을 해안가보다 저렴한 가격으로 구입하세요."},{"name":"미니마켓 & 과일 가판","note":"과일, 대용량 생수, 스낵을 준비해 테라스 피크닉을 즐기세요."}],"gallery":{"title":"포토 갤러리","items":[{"alt":"동네 델리의 파니니","caption":"파니니 & 델리"},{"alt":"포시타노 전망과 피크닉","caption":"전망 좋은 피크닉"}]},"faqs":[{"q":"무료로 물을 다시 채울 수 있나요?","a":["네. 호스텔이나 키에사 누오바 근처 공공 급수대에서 물병을 채우세요. 최소 750ml는 항상 휴대하는 것이 좋습니다."]},{"q":"채식 메뉴도 있나요?","a":["물론입니다. 카프레제 파니니, 가지 파르미지아나 슬라이스, 파스타 알 포모도로 등을 캐주얼 트라토리아에서 주문하세요."]}],"fallbackIntro":["어디를 찾아야 하는지만 알면 포지타노에서도 가성비 좋은 먹거리를 충분히 즐길 수 있습니다."],"fallbackRecommendations":[{"name":"콜리나 베이커리","note":"파니니, 피자 알 타그리오, 샐러드, 페이스트리를 빠르게 테이크아웃할 수 있습니다."},{"name":"동네 델리 카운터","note":"해안가보다 저렴한 가격으로 델리 플레이트, 포카치아, 피크닉 용품을 준비하세요."},{"name":"미니마켓 & 과일 가판","note":"과일, 대용량 생수, 스낵을 챙겨 테라스 피크닉을 즐겨 보세요."}],"fallbackGalleryItems":[{"alt":"동네 델리에서 산 파니니","caption":"파니니 & 델리"},{"alt":"포지타노 전망과 함께하는 피크닉","caption":"피크닉 전망 명소"}],"fallbackFaqs":[{"q":"카드 결제가 가능한가요?","a":["대부분 가능하지만 키오스크나 최소 결제 금액에 대비해 소액 현금을 준비해 두면 좋습니다."]},{"q":"테이크아웃 포장비가 흔한가요?","a":["일부 매장은 소액의 포장비를 받으니 재사용 용기를 가져가면 비용도 아끼고 쓰레기도 줄일 수 있습니다."]}]}')}}]);