"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[364734],{364734:e=>{e.exports=JSON.parse('{"seo":{"title":"Eco‑friendly travel on the Amalfi Coast","description":"Lower your footprint: refills, walking routes, ferries over cars, and mindful beach days."},"linkLabel":"Eco‑friendly travel on the Amalfi Coast","intro":["Eco-friendly travel on the Amalfi Coast covers transport, waste, and daily energy habits.","Keep your footprint light without giving up the coastline views."],"sections":[{"id":"transport","title":"Greener transport","body":["Choose buses or ferries instead of private taxis whenever possible.","Walk short distances in town rather than calling a car."]},{"id":"waste","title":"Waste & plastic","body":["Carry a reusable bottle/bag and refill at fountains or caf\xe9s.","Sort rubbish where bins exist and pack out what you bring onto trails."]},{"id":"energy","title":"Energy & water","body":["Switch off AC and lights when leaving your room; close shutters to keep interiors cool.","Keep showers short and reuse towels to save water."]}],"tips":["Support small local businesses that use regional produce and low-waste practices.","Order seasonal dishes — lemons, anchovies, and vegetables shine in their peak months."],"faqs":[{"q":"Can I rent e-bikes on the coast?","a":["Yes in Amalfi, Sorrento, and some villages — reserve early because fleets are small."]}]}')}}]);