"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[367263],{367263:n=>{n.exports=JSON.parse('{"meta":{"title":"Ch\xednh s\xe1ch quyền ri\xeang tư | Hostel Brikette","description":"C\xe1ch Hostel Brikette thu thập, sử dụng, lưu trữ v\xe0 chia sẻ dữ liệu c\xe1 nh\xe2n khi bạn truy cập website hoặc đặt ph\xf2ng."},"headings":{"pageTitle":"Ch\xednh s\xe1ch quyền ri\xeang tư","subtitle":"C\xe1ch ch\xfang t\xf4i xử l\xfd dữ liệu c\xe1 nh\xe2n của bạn"},"lastUpdated":{"label":"Cập nhật lần cuối","date":"2026-01-09"},"toc":{"title":"Trong trang n\xe0y","aria":"Mục lục ch\xednh s\xe1ch quyền ri\xeang tư"},"sections":{"intro":{"title":"Tổng quan","body":["Ch\xednh s\xe1ch quyền ri\xeang tư n\xe0y giải th\xedch dữ liệu c\xe1 nh\xe2n ch\xfang t\xf4i thu thập, l\xfd do thu thập v\xe0 c\xe1ch sử dụng khi bạn truy cập website, li\xean hệ với ch\xfang t\xf4i hoặc đặt ph\xf2ng.","Nếu bạn đang t\xecm c\xe1c quy tắc v\xe0 điều khoản thương mại của việc đặt ph\xf2ng (thanh to\xe1n, thời hạn hủy, check‑in/out v\xe0 nghĩa vụ của kh\xe1ch), vui l\xf2ng đọc Điều khoản v\xe0 Điều kiện cho việc đặt ph\xf2ng."]},"controller":{"title":"Ai chịu tr\xe1ch nhiệm về dữ liệu của bạn","body":["B\xean kiểm so\xe1t dữ liệu l\xe0 Skylar SRL, hoạt động với t\xean Hostel Brikette (\\"ch\xfang t\xf4i\\").","Nếu bạn c\xf3 c\xe2u hỏi về ch\xednh s\xe1ch n\xe0y hoặc muốn thực hiện quyền ri\xeang tư, h\xe3y li\xean hệ với ch\xfang t\xf4i tại {{email}}."]},"dataWeCollect":{"title":"Dữ liệu c\xe1 nh\xe2n ch\xfang t\xf4i thu thập","body":["Khi bạn li\xean hệ hoặc y\xeau cầu đặt ph\xf2ng, bạn c\xf3 thể cung cấp th\xf4ng tin như t\xean, địa chỉ email, số điện thoại, quốc tịch, ng\xe0y lưu tr\xfa v\xe0 mọi tin nhắn bạn gửi cho ch\xfang t\xf4i.","Nếu bạn ho\xe0n tất đặt ph\xf2ng th\xf4ng qua Đại l\xfd Du lịch Trực tuyến (OTA) hoặc c\xf4ng cụ đặt ph\xf2ng b\xean thứ ba, nh\xe0 cung cấp đ\xf3 sẽ xử l\xfd th\xf4ng tin thanh to\xe1n của bạn theo ch\xednh s\xe1ch quyền ri\xeang tư của họ. Ch\xfang t\xf4i thường nhận th\xf4ng tin đặt ph\xf2ng cần thiết để đ\xf3n tiếp bạn (v\xed dụ: t\xean kh\xe1ch, th\xf4ng tin li\xean hệ, ng\xe0y th\xe1ng v\xe0 m\xe3 đặt ph\xf2ng).","Khi bạn truy cập website, m\xe1y chủ của ch\xfang t\xf4i c\xf3 thể xử l\xfd dữ liệu kỹ thuật cơ bản (như địa chỉ IP, th\xf4ng tin thiết bị v\xe0 tr\xecnh duyệt, v\xe0 c\xe1c trang được y\xeau cầu) cho mục đ\xedch bảo mật, gỡ lỗi v\xe0 hiệu năng."]},"howWeUse":{"title":"C\xe1ch ch\xfang t\xf4i sử dụng dữ liệu của bạn","body":["Để phản hồi y\xeau cầu v\xe0 cung cấp hỗ trợ kh\xe1ch h\xe0ng.","Để xử l\xfd v\xe0 quản l\xfd đặt ph\xf2ng, check‑in v\xe0 c\xe1c li\xean lạc li\xean quan đến lưu tr\xfa.","Để tu\xe2n thủ nghĩa vụ ph\xe1p l\xfd (bao gồm c\xe1c nghĩa vụ li\xean quan đến dịch vụ lưu tr\xfa v\xe0 an to\xe0n c\xf4ng cộng).","Để bảo vệ v\xe0 duy tr\xec website của ch\xfang t\xf4i (ph\xf2ng chống gian lận, gi\xe1m s\xe1t lạm dụng v\xe0 độ tin cậy)."]},"legalBases":{"title":"Cơ sở ph\xe1p l\xfd (v\xec sao ch\xfang t\xf4i được ph\xe9p xử l\xfd dữ liệu)","body":["Hợp đồng: để thực hiện c\xe1c bước theo y\xeau cầu của bạn trước khi đặt ph\xf2ng v\xe0 thực hiện hợp đồng đặt ph\xf2ng.","Nghĩa vụ ph\xe1p l\xfd: để đ\xe1p ứng c\xe1c y\xeau cầu của ph\xe1p luật \xe1p dụng (bao gồm c\xe1c quy định của \xdd về lưu tr\xfa).","Lợi \xedch hợp ph\xe1p: để vận h\xe0nh, bảo vệ v\xe0 cải thiện website v\xe0 li\xean lạc của ch\xfang t\xf4i, miễn l\xe0 c\xe1c lợi \xedch đ\xf3 kh\xf4ng vượt qu\xe1 quyền của bạn."]},"sharing":{"title":"Ch\xfang t\xf4i chia sẻ dữ liệu với ai","body":["Ch\xfang t\xf4i chỉ chia sẻ dữ liệu c\xe1 nh\xe2n khi cần thiết để cung cấp dịch vụ hoặc tu\xe2n thủ ph\xe1p luật. Điều n\xe0y c\xf3 thể bao gồm c\xe1c nền tảng đặt ph\xf2ng bạn chọn sử dụng, nh\xe0 cung cấp thanh to\xe1n của c\xe1c nền tảng đ\xf3, nh\xe0 cung cấp email/li\xean lạc v\xe0 nh\xe0 cung cấp kỹ thuật lưu trữ hoặc bảo vệ website của ch\xfang t\xf4i.","Ch\xfang t\xf4i kh\xf4ng b\xe1n dữ liệu c\xe1 nh\xe2n."]},"retention":{"title":"Ch\xfang t\xf4i lưu giữ dữ liệu trong bao l\xe2u","body":["Ch\xfang t\xf4i chỉ giữ dữ liệu c\xe1 nh\xe2n trong thời gian cần thiết cho c\xe1c mục đ\xedch n\xeau tr\xean v\xe0 để đ\xe1p ứng c\xe1c y\xeau cầu ph\xe1p l\xfd, kế to\xe1n hoặc b\xe1o c\xe1o.","Thời gian lưu giữ c\xf3 thể kh\xe1c nhau t\xf9y theo loại hồ sơ v\xe0 nghĩa vụ ph\xe1p l\xfd \xe1p dụng."]},"security":{"title":"C\xe1ch ch\xfang t\xf4i bảo vệ dữ liệu của bạn","body":["Ch\xfang t\xf4i \xe1p dụng c\xe1c biện ph\xe1p kỹ thuật v\xe0 tổ chức hợp l\xfd để bảo vệ dữ liệu c\xe1 nh\xe2n khỏi mất m\xe1t, lạm dụng v\xe0 truy cập tr\xe1i ph\xe9p.","Kh\xf4ng hệ thống n\xe0o c\xf3 thể đảm bảo an to\xe0n 100%, nhưng ch\xfang t\xf4i li\xean tục cải thiện bảo mật."]},"yourRights":{"title":"Quyền của bạn","body":["T\xf9y theo nơi bạn ở v\xe0 luật \xe1p dụng, bạn c\xf3 thể c\xf3 quyền truy cập, chỉnh sửa, x\xf3a hoặc hạn chế việc xử l\xfd dữ liệu c\xe1 nh\xe2n của m\xecnh, v\xe0 phản đối một số hoạt động xử l\xfd.","Để thực hiện quyền của bạn, h\xe3y li\xean hệ với ch\xfang t\xf4i tại {{email}}. Ch\xfang t\xf4i c\xf3 thể cần x\xe1c minh danh t\xednh của bạn."]},"international":{"title":"Chuyển dữ liệu quốc tế","body":["Một số nh\xe0 cung cấp dịch vụ c\xf3 thể xử l\xfd dữ liệu ngo\xe0i quốc gia của bạn. Khi cần, ch\xfang t\xf4i sử dụng c\xe1c biện ph\xe1p bảo vệ ph\xf9 hợp cho việc chuyển dữ liệu quốc tế."]},"changes":{"title":"Thay đổi đối với ch\xednh s\xe1ch n\xe0y","body":["Ch\xfang t\xf4i c\xf3 thể cập nhật Ch\xednh s\xe1ch quyền ri\xeang tư n\xe0y theo thời gian. Phi\xean bản mới nhất l\xe0 phi\xean bản được đăng tr\xean trang n\xe0y."]}},"footer":{"backToTop":"L\xean đầu trang"}}')}}]);