"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[374395],{374395:i=>{i.exports=JSON.parse('{"seo":{"title":"Marina di Praia समुद्र तट गाइड","description":"कहाँ नाव/बस से उतरें, किन सुविधाओं की उम्मीद रखें और कम भीड़ कब मिलती है।"},"linkLabel":"Marina di Praia और शांत बीच","intro":["पोसितानो के मुख्य बीच से अलग अनुभव के लिए मरीना दी प्राइआ और आसपास की खाड़ियों की खोज करें।"],"sections":[{"id":"marina","title":"मरीना दी प्राइआ की ज़रूरी बातें","body":["चट्टानों के बीच बसी यह कंकरीली बीच सनबेड, कायक किराए और चट्टान में बसे समुद्री भोजन रेस्तराँ प्रदान करती है।","फ्रंट-रो लाउंजर पाने के लिए 10:30 से पहले पहुँचें; 16:00 के बाद दोपहर की छाया किनारे तक पहुँचने लगती है।"]},{"id":"coves","title":"पास की खाड़ियाँ","body":["ग्रोट्टा दी सुप्राइआनो तक कायक से जाएँ या गावीतेला और लाउरीतो जैसी एकांत जगहों तक छोटी नाव ले लें।","रीफ़-फ्रेंडली सनस्क्रीन और वाटर शूज़ साथ रखें—पथरीला समुद्री तल स्नॉर्कलिंग के लिए बेहतरीन दृश्यता देता है।"]},{"id":"logistics","title":"आवागमन","body":["पोसितानो से अमाल्फी दिशा की SITA बस पकड़ें; ड्राइवर से मरीना दी प्राइआ स्टॉप के लिए कहें और रैंप से पाँच मिनट में नीचे उतरें।","स्कूटर खाड़ी के ऊपर पार्क किए जा सकते हैं—मीटर वाली पार्किंग के लिए सिक्के साथ रखें।"]}],"tips":["इल पिराटा या ला प्राइआ में दोपहर का भोजन अग्रिम बुक करें ताकि तैराकी के बीच चट्टान किनारे भोजन मिल सके।","कीमती सामान के लिए ड्राई बैग रखें; बीच का स्थान छोटा है और सब कुछ साथ रहने पर अधिक व्यवस्थित रहता है।"],"faqs":[{"q":"क्या बीच मुफ़्त है?","a":["यहाँ एक मुफ़्त क्षेत्र है, लेकिन जगह सीमित है। पीक सीज़न में बीच क्लबों पर दो सनबेड और एक छतरी के लिए लगभग €20–€25 का बजट रखें।"]},{"q":"क्या मैं पोसितानो से पैदल जा सकता हूँ?","a":["सड़क से पैदल जाने में लगभग 50 मिनट लगते हैं और कंधे संकरे हैं। विशेषकर भीड़ भरे मौसम में बस या स्कूटर अधिक सुरक्षित विकल्प है।"]}],"toc":[{"href":"#why-go","label":"क्यों जाएँ"},{"href":"#getting-there","label":"कैसे पहुँचे"},{"href":"#layout","label":"लेआउट और सेवाएँ"},{"href":"#evening","label":"डिनर के लिए रुकें"}],"essentialsTitle":"क्या उम्मीद करें","essentials":["पॉसिटानो से 20 मिनट की एसआईटीए बस की सवारी; मरीना डि प्रिया को रुकने के लिए कहें और ढलान पर दो मिनट पैदल चलें।","एक मुख्य लिडो और बीच में एक छोटा सा मुक्त खंड वाला संकीर्ण कंकड़ वाला समुद्र तट।","रेस्तरां और स्नैक कियोस्क वॉकवे पर हैं - घर जाने से पहले सूर्यास्त रात्रिभोज के लिए आदर्श।"],"typicalCostsTitle":"सामान्य लागत","typicalCosts":["मरीना डि प्रिया बीच क्लब में लाउंजर + छाता: दिन के लिए €25-€35 (दो व्यक्तियों के सेट उपलब्ध)।","कयाक किराया: दो लोगों के लिए लगभग €20 प्रति घंटा।","दा आर्मंडिनो या इल पिराटा में मुख्य रात्रिभोज: €16-€28, कैच और पास्ता विशेष पर निर्भर करता है।"],"tipsTitle":"झटपट टिप्स","warningsTitle":"जानने योग्य बातें","warnings":["स्थान सीमित है; मुक्त क्षेत्र पहले भरता है और लीडो लाउंजर सप्ताहांत पर बिक जाते हैं।","दोपहर के मध्य तक चट्टानें सीधी धूप को रोक लेती हैं - ठंडक के लिए बढ़िया है लेकिन हवा वाले दिनों में ठंड महसूस हो सकती है।","मोबाइल रिसेप्शन कण्ठ के अंदर डूब जाता है; बस समय सारिणी या रात्रिभोज बुकिंग पुष्टिकरण पहले से डाउनलोड करें।"],"faqsTitle":"Marina di Praia प्रश्नोत्तर"}')}}]);