"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[377012],{377012:n=>{n.exports=JSON.parse('{"seo":{"title":"Trải nghiệm lớp nấu ăn tr\xean Amalfi Coast","description":"Mức gi\xe1, nội dung lớp v\xe0 c\xe1ch đặt chỗ cho buổi học nấu ăn local."},"linkLabel":"Trải nghiệm lớp nấu ăn tr\xean Amalfi Coast","intro":["Lớp nấu ăn Amalfi Coast mang tới chanh sfusato, pasta tươi v\xe0 c\xf4ng thức gia đ\xecnh trong căn bếp nhỏ nh\xecn ra biển.","D\xf9ng cẩm nang n\xe0y để chọn lớp ph\xf9 hợp ng\xe2n s\xe1ch v\xe0 lịch tr\xecnh, đồng thời x\xe1c nhận chi tiết trước khi đặt cọc."],"sections":[{"id":"flow","title":"Bản dịch đang được chuẩn bị.","body":["Chủ lớp ch\xe0o bạn bằng c\xe0 ph\xea hoặc prosecco rồi giới thiệu nguy\xean liệu: chanh b\xe0o vỏ thơm, rau củ theo m\xf9a v\xe0 khối bột pasta bạn sẽ tạo h\xecnh.","Thường nấu 2–3 m\xf3n — pasta tươi, sốt ăn k\xe8m v\xe0 m\xf3n tr\xe1ng miệng vị chanh — sau đ\xf3 c\xf9ng ngồi thưởng thức; tổng thời lượng 2,5–4 giờ t\xednh cả bữa ăn."]},{"id":"choose","title":"Chọn lớp ph\xf9 hợp","body":["Kiểm tra quy m\xf4 nh\xf3m v\xe0 vị tr\xed bếp: trong Positano, tr\xean n\xf4ng trại v\xf9ng cao hay ở thị trấn l\xe2n cận; một số lớp bao gồm đưa đ\xf3n, số kh\xe1c t\xednh ri\xeang.","X\xe1c minh những g\xec đ\xe3 bao gồm trong gi\xe1: nguy\xean liệu, rượu vang, c\xf4ng thức in sẵn, tạp dề hay đồ ăn mang về."]},{"id":"logistics","title":"Hậu cần & thời gian","body":["M\xf9a cao điểm lớp k\xedn chỗ v\xe0i tuần trước; đặt slot trước khi tới, nhất l\xe0 buổi tối sau c\xe1c chuyến đi trong ng\xe0y.","Nếu lớp ở ngo\xe0i Positano, x\xe1c nhận điểm đ\xf3n hoặc dự tr\xf9 tiền taxi; đường dốc n\xean d\xf9 đi shuttle vẫn cần gi\xe0y thoải m\xe1i."]},{"id":"dietary","title":"Chế độ ăn & ứng xử","body":["B\xe1o trước dị ứng, nhu cầu chay/thuần chay hoặc kh\xf4ng gluten khi đặt; nhiều lớp c\xf3 thể điều chỉnh nếu biết sớm.","Đi gi\xe0y k\xedn mũi, buộc gọn t\xf3c d\xe0i v\xe0 hạn chế nước hoa — bếp nhỏ, bạn sẽ l\xe0m việc gần nguy\xean liệu tươi."]}],"faqs":[{"q":"Người mới bắt đầu c\xf3 hợp kh\xf4ng?","a":["C\xf3. Giảng vi\xean minh hoạ từng bước, chỉnh tay ngay khi cần v\xe0 chia việc để mọi người đều được tham gia m\xe0 kh\xf4ng vội v\xe0ng."]},{"q":"C\xf3 cần n\xf3i tiếng \xdd kh\xf4ng?","a":["Kh\xf4ng. Đa số lớp dạy bằng tiếng Anh hoặc song ngữ, cộng với phần minh hoạ trực quan rất dễ theo d\xf5i."]},{"q":"T\xf4i n\xean mang g\xec?","a":["Mang sổ nhỏ nếu muốn ghi ch\xfa, sạc đầy điện thoại để chụp ảnh v\xe0 đem theo tiền mặt trả phần c\xf2n lại hay chi ph\xed di chuyển."]}],"fallback":{"intro":["Tự tay l\xe0m pasta, sốt v\xe0 tr\xe1ng miệng chanh; nh\xf3m nhỏ, bếp view biển trong m\xf9a cao điểm."],"toc":[{"href":"#what","label":"Mong đợi g\xec"},{"href":"#choose","label":"C\xe1ch chọn lớp"},{"href":"#tips","label":"Mẹo"}],"sections":[{"id":"what","title":"Mong đợi g\xec","body":["Tự tay l\xe0m pasta, sốt v\xe0 tr\xe1ng miệng chanh; nh\xf3m nhỏ, bếp view biển trong m\xf9a cao điểm."]},{"id":"choose","title":"C\xe1ch chọn lớp","body":["Kiểm tra số người, thời lượng, nội dung bao gồm v\xe0 phương \xe1n di chuyển."]},{"id":"tips","title":"Mẹo","body":["Đặt sớm v\xe0o th\xe1ng cao điểm; b\xe1o trước nhu cầu ăn ki\xeang cho chủ lớp."]}]}}')}}]);