"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[381556],{381556:n=>{n.exports=JSON.parse('{"seo":{"title":"Bến ph\xe0 → Hostel Brikette, k\xe8m h\xe0nh l\xfd","description":"C\xe1c lựa chọn từ bến: khu\xe2n v\xe1c hoặc xe bu\xfdt SITA tới điểm dừng gần hơn; mẹo thực tế v\xe0 c\xe2u hỏi thường gặp."},"linkLabel":"Bến ph\xe0 → Hostel Brikette, k\xe8m h\xe0nh l\xfd","intro":["Đến Positano bằng ph\xe0 v\xe0 mang theo h\xe0nh l\xfd? H\xe3y kết hợp xe bu\xfdt Interno Positano với dịch vụ khu\xe2n v\xe1c ở cảng để tới Hostel Brikette m\xe0 kh\xf4ng phải k\xe9o vali l\xean những bậc thang dốc."],"beforeList":["Kiểm tra lịch tr\xecnh Interno Positano mới nhất — đ\xe2y l\xe0 [lịch v\xe0 gi\xe1 v\xe9 hiện tại](https://forum.amalfi.com/t/mobility-amalfi-coast-bus-positano-nocelle-praiano/26).","Mua v\xe9 Interno Positano tại bất kỳ cửa h\xe0ng tabacchi/quầy b\xe1o n\xe0o trước khi l\xean xe.","C\xe2n nhắc giao h\xe0nh l\xfd nặng cho đội khu\xe2n v\xe1c ngay khi họ đ\xf3n ph\xe0 — h\xe3y n\xf3i rằng bạn lưu tr\xfa tại Hostel Brikette."],"sections":[{"id":"dock-to-mulini","title":"Từ bến đến Piazza dei Mulini","body":["Từ b\xe3i biển ch\xednh đi bộ l\xean Piazza dei Mulini theo lối đi dọc bờ biển c\xf3 biển chỉ dẫn — sử dụng [hướng dẫn n\xe0y](https://www.google.com/maps/dir/Positano+Spiaggia/2+Viale+Pasitea+Positano,+Campania/@40.6289096,14.4857482,18z/data=!4m14!4m13!1m5!1m1!1s0x133b976916d1a44d:0xf1e08d61658b4291!2m2!1d14.4878892!2d40.6276549!1m5!1m1!1s0x133b976f39841bc9:0x5425cf574fd9d282!2m2!1d14.4857716!2d40.6301562!3e2).","Nếu mang nhiều h\xe0nh l\xfd, h\xe3y thu\xea dịch vụ khu\xe2n v\xe1c của Positano tại bến để họ vận chuyển tất cả l\xean dốc gi\xfap bạn — [hướng dẫn dịch vụ khu\xe2n v\xe1c](guide:porterServices) của ch\xfang t\xf4i n\xeau chi tiết gi\xe1 v\xe0 c\xe1ch đặt."]},{"id":"mulini-to-bus","title":"Piazza dei Mulini tới điểm đ\xf3n Interno","body":["Khi tới Piazza dei Mulini, mua v\xe9 interno tại một cửa h\xe0ng g\xf3c phố hoặc tabacchi. Sau đ\xf3 đi tới điểm dừng nằm ở r\xeca quảng trường — ngay nơi quảng trường giao với đường ch\xednh.","Xe bu\xfdt Interno chạy khoảng mỗi 30 ph\xfat từ s\xe1ng tới nửa đ\xeam v\xe0 đi theo [lộ tr\xecnh l\xean dốc n\xe0y](https://www.google.com/maps/dir/2+Viale+Pasitea+Positano,+Campania/Viale+Pasitea,+323,+84017+Positano+SA/@40.6309188,14.4819138,16z/data=!3m1!4b1!4m14!4m13!1m5!1m1!1s0x133b976f39841bc9:0x5425cf574fd9d282!2m2!1d14.4857716!2d40.6301562!1m5!1m1!1s0x133b97667312711d:0xa2b15bca59b377fa!2m2!1d14.4824389!2d40.6293416!3e0) về ph\xeda hostel."]},{"id":"identify-bus","title":"Nhận diện xe bu\xfdt Interno Positano","body":["Xe c\xf3 thể kh\xe1c nhau, nhưng bảng điện tử ph\xeda trước lu\xf4n ghi \\"interno positano\\".","Đ\xe2y l\xe0 xe bu\xfdt đ\xf4 thị cỡ nhỏ c\xf3 chỗ đứng; l\xean xe từ lề đường v\xe0 x\xe1c nhận hoặc đưa v\xe9 cho t\xe0i xế."]},{"id":"bus-stop-to-hostel","title":"Từ điểm dừng tới Hostel Brikette","body":["Xuống xe tại cửa h\xe0ng tabacchi ở điểm dừng ‘Chiesa Nuova’. Bước xuống, ra đường ch\xednh rồi rẽ tr\xe1i v\xe0o Viale Pasitea.","Đi bộ hơn 100 m\xe9t; bạn sẽ thấy hostel b\xean tay phải. T\xecm biển hiệu tr\xean s\xe2n thượng tầng hai, đi tiếp qua kh\xfac cua v\xe0 leo cầu thang b\xean phải để tới lối v\xe0o.","Cần xem đường đi trực quan? L\xe0m theo [chỉ dẫn cuối c\xf9ng n\xe0y](https://www.google.com/maps/dir/Brikette+Hostel,+Via+G.+Marconi,+358,+84017+Positano+SA/Tabacchi+Cafiero+Emanuele,+Viale+Pasitea,+325a,+84017+Positano+SA/@40.6292434,14.4813669,19z/data=!4m14!4m13!1m5!1m1!1s0x133b97669dd0003b:0xf11f22a59b1b00da!2m2!1d14.480818!2d40.629634!1m5!1m1!1s0x133b97660ef186c1:0xc9eb9ab54d880307!2m2!1d14.4823321!2d40.6293865!3e2)."]}],"stepsList":["Giao h\xe0nh l\xfd cho đội khu\xe2n v\xe1c tại bến v\xe0 mua v\xe9 xe bu\xfdt Interno Positano ở tabacchi.","Đi bộ từ b\xe3i biển ch\xednh tới Piazza dei Mulini, sau đ\xf3 tới điểm dừng Interno b\xean cạnh tabacchi tr\xean Viale Pasitea.","L\xean xe bu\xfdt Interno Positano (t\xecm bảng \\"interno positano\\") v\xe0 đi l\xean dốc về ph\xeda Viale Pasitea 325.","Xuống xe trước cửa h\xe0ng tabacchi, rẽ tr\xe1i đi dọc Viale Pasitea hơn 100 m rồi leo cầu thang b\xean phải để tới Hostel Brikette."],"kneesList":["Đi xe bu\xfdt Interno Positano gi\xfap bạn tr\xe1nh h\xe0ng trăm bậc thang dốc.","Để đội khu\xe2n v\xe1c mang h\xe0nh l\xfd nặng ri\xeang, bạn c\xf3 thể rảnh tay tr\xean xe v\xe0 đoạn đường đi bộ ngắn."],"kneesPorterPrefix":"Cần th\xeam th\xf4ng tin về gi\xe1 khu\xe2n v\xe1c v\xe0 c\xe1ch đặt? H\xe3y đọc","kneesPorterLinkLabel":"hướng dẫn dịch vụ khu\xe2n v\xe1c","faqs":[{"q":"T\xf4i mua v\xe9 xe bu\xfdt Interno Positano ở đ\xe2u?","a":["Mua v\xe9 tại bất kỳ tabacchi/quầy b\xe1o n\xe0o trong thị trấn, bao gồm ki-ốt ngay cạnh điểm dừng. T\xe0i xế kh\xf4ng b\xe1n v\xe9 tr\xean xe."]},{"q":"Xe bu\xfdt Interno Positano chạy bao l\xe2u một chuyến?","a":["Tuyến hoạt động khoảng mỗi 30 ph\xfat từ 08:00 tới nửa đ\xeam, v\xe0 c\xf3 th\xeam chuyến v\xe0o cao điểm — lu\xf4n kiểm tra lịch trước khi l\xean ph\xe0."]},{"q":"Dịch vụ khu\xe2n v\xe1c ở bến hoạt động ra sao?","a":["Đội khu\xe2n v\xe1c chờ sẵn mỗi chuyến ph\xe0, bốc h\xe0nh l\xfd v\xe0 đưa l\xean dốc. H\xe3y n\xf3i rằng bạn ở Hostel Brikette, thanh to\xe1n mức gi\xe1 đ\xe3 b\xe1o v\xe0 giữ vật dụng qu\xfd gi\xe1 hoặc thuốc men b\xean m\xecnh."]}],"howtoSteps":["Mua v\xe9 Interno v\xe0 giao h\xe0nh l\xfd cho đội khu\xe2n v\xe1c tại bến","Đi bộ từ b\xe3i biển tới Piazza dei Mulini v\xe0 điểm dừng Interno","Đi xe bu\xfdt Interno Positano l\xean dốc (t\xecm bảng \\"interno positano\\")","Xuống tại tabacchi, đi bộ 100 m v\xe0 leo cầu thang v\xe0o Hostel Brikette"]}')}}]);