"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[402378],{402378:n=>{n.exports=JSON.parse('{"seo":{"title":"Con Đường của C\xe1c Vị Thần từ Hostel Brikette","description":"Lộ tr\xecnh cụ thể từ hostel tới c\xe1c điểm v\xe0o Sentiero degli Dei, k\xe8m phương tiện v\xe0 mẹo an to\xe0n."},"linkLabel":"Con Đường của C\xe1c Vị Thần từ Brikette","intro":["Sentiero degli Dei (Con Đường của C\xe1c Vị Thần) kết nối những s\xe2n hi\xean cheo leo, rừng dẻ v\xe0 tầm nh\xecn to\xe0n cảnh Amalfi Coast. Từ Hostel Brikette, bạn c\xf3 thể kết hợp ph\xe0 địa phương v\xe0 xe bu\xfdt SITA để tới đường m\xf2n m\xe0 kh\xf4ng cần tour đắt đỏ.","C\xf3 ba c\xe1ch khởi h\xe0nh ch\xednh từ hostel: đi bộ xuống bến để đi ph\xe0 tới Amalfi rồi bắt xe bu\xfdt l\xean Bomerano, ngồi xe bu\xfdt suốt h\xe0nh tr\xecnh, hoặc bỏ qua c\xe1c chặng trung chuyển v\xe0 đi tuyến khứ hồi ngắn hơn từ Nocelle. H\xe3y d\xf9ng phần tổng quan n\xe0y để chọn tuyến chi tiết ph\xf9 hợp với quỹ thời gian, ng\xe2n s\xe1ch v\xe0 mức độ thoải m\xe1i với độ cao của bạn."],"sections":[{"id":"highlights","title":"Điểm nhấn nhanh","body":["Những ban c\xf4ng tự nhi\xean lơ lửng nh\xecn xuống Positano, Praiano v\xe0 Capri, n\xean ngay cả đoạn ngắn cũng c\xf3 điểm dừng sống ảo tuyệt đẹp.","Độ dốc đều chứ kh\xf4ng qu\xe1 kỹ thuật; chỉ cần đ\xf4i gi\xe0y b\xe1m tốt v\xe0 đủ nước, bạn c\xf3 thể y\xean t\xe2m chinh phục.","Sử dụng phương tiện c\xf4ng cộng gi\xfap chi ph\xed thấp hơn nhiều so với thu\xea t\xe0i xế ri\xeang hay tour hướng dẫn, để bạn d\xe0nh ng\xe2n s\xe1ch cho phần thưởng sau chuyến đi."]},{"id":"considerations","title":"Trước khi l\xean đường","body":["H\xe3y chuẩn bị tinh thần cho c\xe1c gờ đ\xe1 trống trải, cầu thang kh\xf4ng tay vịn v\xe0 vực s\xe2u. Nếu bạn sợ độ cao, h\xe3y chọn tuyến Nocelle đi-về dễ chịu hơn.","T\xednh cả di chuyển, chuyến đi mất khoảng 3–5 giờ t\xf9y tuyến. M\xf9a h\xe8 n\xf3ng bức n\xean cần xuất ph\xe1t sớm hoặc đi v\xe0o cuối buổi chiều."]},{"id":"route-options","title":"C\xe1c tuyến đi từ Hostel Brikette","body":["Đi ph\xe0 qua Amalfi: đi bộ xuống Spiaggia Grande, đi ph\xe0 25 ph\xfat tới Amalfi, bắt xe bu\xfdt l\xean Bomerano, đi bộ tới Nocelle rồi đi shuttle về lại. Đ\xe2y l\xe0 ng\xe0y d\xe0i nhất nhưng kết hợp cảnh biển với thời gian kh\xe1m ph\xe1 Amalfi.","Đi xe bu\xfdt qua Amalfi: mua v\xe9 tại Bar Internazionale, l\xean xe bu\xfdt SITA thẳng tới Amalfi, đổi sang tuyến Agerola rồi theo h\xe0nh tr\xecnh kinh điển Bomerano → Nocelle. Ph\xf9 hợp khi biển động hoặc ph\xe0 hết chỗ.","Tuyến Nocelle đi-về: bắt xe minibus Montepertuso–Nocelle, đi bộ đoạn đường ngắm cảnh tới gần Colle Serra rồi quay lại bằng c\xf9ng chuyến xe. L\xfd tưởng cho ng\xe0y nhẹ nh\xe0ng hoặc khi kh\xf4ng muốn lệ thuộc v\xe0o lịch trung chuyển d\xe0y đặc."]},{"id":"trail-basics","title":"Th\xf4ng tin cơ bản","body":["Sentiero degli Dei nối Bomerano v\xe0 Nocelle với qu\xe3ng đường khoảng 6 km l\xean xuống nhẹ, tổng độ ch\xeanh cao khoảng 200 m.","Biển chỉ đường l\xe0 sọc đỏ-trắng của CAI c\xf9ng c\xe1c mũi t\xean gỗ \\"Sentiero\\". Đường đi xen kẽ bậc đ\xe1, đất n\xe9n v\xe0 gờ đ\xe1 — nhớ mang gi\xe0y b\xe1m tốt v\xe0 để tay rảnh."]},{"id":"planning","title":"Mẹo chuẩn bị","body":["Kiểm tra dự b\xe1o thời tiết từ tối h\xf4m trước; trời mưa l\xe0m bậc đ\xe1 trơn v\xe0 gi\xf3 lớn c\xf3 thể khiến ph\xe0 dừng chạy.","Mỗi người n\xean mang \xedt nhất 1,5 l\xedt nước. Điểm tiếp nước chỉ c\xf3 tại qu\xe1n c\xe0 ph\xea ở Bomerano, kiosk tại Nocelle v\xe0 thỉnh thoảng l\xe0 quầy của n\xf4ng trại.","So\xe1t v\xe9 SITA trước khi l\xean xe v\xe0 lu\xf4n c\xf3 một v\xe9 dự ph\xf2ng trong balo ph\xf2ng trường hợp phải đổi hướng."]}],"essentialsTitle":"Vật dụng cần c\xf3","essentials":["Gi\xe0y thoải m\xe1i, b\xe1m tốt c\xf9ng đồ chống nắng v\xe0 \xe1o kho\xe1c nhẹ cho c\xe1c đoạn gi\xf3 lộng.","V\xe9 xe bu\xfdt SITA mua sẵn (tại quầy tabacchi hoặc Bar Internazionale) cho mọi chặng bạn dự định đi.","Bản đồ ngoại tuyến hoặc lộ tr\xecnh lưu sẵn ph\xf2ng khi mất s\xf3ng trong hẻm n\xfai."],"typicalCostsTitle":"Chi ph\xed tham khảo","typicalCosts":["Xe bu\xfdt SITA Positano ⇄ Amalfi/Bomerano/Nocelle: 2–4 € mỗi chặng t\xf9y loại v\xe9.","Ph\xe0 Travelmar hoặc Lucibello Positano ⇄ Amalfi: khoảng 12–16 € mỗi lượt v\xe0o m\xf9a cao điểm.","Đồ ăn vặt v\xe0 nước tại c\xe1c cửa h\xe0ng l\xe0ng: 3–8 €; đi đường m\xf2n miễn ph\xed."],"tipsTitle":"Thời điểm l\xfd tưởng","tips":["Th\xe1ng 7–8 n\xean khởi h\xe0nh trước 09:00 hoặc chọn giờ v\xe0ng m\xf9a xu\xe2n, thu để c\xf3 nhiệt độ m\xe1t v\xe0 \xe1nh s\xe1ng dịu.","Đừng xuống 1.700 bậc về Positano nếu đầu gối đ\xe3 mỏi — bậc Montepertuso sau một ng\xe0y d\xe0i sẽ rất gắt.","Mang balo nhẹ v\xe0 chỉ bung gậy trekking khi gặp đoạn cầu thang dốc."],"steps":[{"name":"Chọn tuyến ph\xf9 hợp","text":"C\xe2n nhắc ph\xe0 qua Amalfi, xe bu\xfdt qua Amalfi hoặc tuyến Nocelle trực tiếp t\xf9y thời tiết, lịch ph\xe0 v\xe0 sức của bạn."},{"name":"Chuẩn bị v\xe9 trước","text":"Mua v\xe9 xe bu\xfdt SITA tại Bar Internazionale v\xe0 v\xe9 ph\xe0 ở bến nếu bạn định đi thuyền."},{"name":"Di chuyển tới điểm bắt đầu","text":"Đi tới Bomerano nếu muốn đi trọn tuyến, hoặc tới Nocelle cho h\xe0nh tr\xecnh ngắn hơn."},{"name":"Bắt đầu chinh phục","text":"Lần theo c\xe1c k\xfd hiệu CAI giữa những s\xe2n hi\xean ngắm cảnh, đi chậm để dừng chụp ảnh v\xe0 ăn nhẹ."},{"name":"Quay lại Positano","text":"Bắt xe bu\xfdt về Chiesa Nuova hoặc chỉ đi bộ xuống nếu bạn thật sự muốn thử th\xe1ch bậc thang d\xe0i."}],"faqsTitle":"C\xe2u hỏi thường gặp","faq":[{"q":"To\xe0n bộ chuyến đi mất bao l\xe2u?","a":"Dự tr\xf9 3–5 giờ cả di chuyển. Tuyến đi ph\xe0 l\xe0 d\xe0i nhất; tuyến Nocelle đi-về g\xf3i gọn trong nửa ng\xe0y."},{"q":"Đường m\xf2n c\xf3 ph\xf9 hợp cho người mới kh\xf4ng?","a":"C\xf3, miễn bạn đi vững v\xe0 kh\xf4ng qu\xe1 sợ độ cao. Kh\xf4ng c\xf3 đoạn leo kỹ thuật, chỉ l\xe0 những đoạn l\xean xuống đều."},{"q":"Nếu ph\xe0 bị hủy th\xec sao?","a":"Chuyển sang đi xe bu\xfdt qua Amalfi hoặc đi thẳng l\xean Nocelle. Lu\xf4n giữ một v\xe9 xe bu\xfdt dự ph\xf2ng để linh hoạt khi thời tiết xấu."}],"faqs":[{"q":"To\xe0n bộ chuyến đi mất bao l\xe2u?","a":["Dự tr\xf9 3–5 giờ cả di chuyển. Tuyến đi ph\xe0 l\xe0 d\xe0i nhất; tuyến Nocelle đi-về g\xf3i gọn trong nửa ng\xe0y."]},{"q":"Đường m\xf2n c\xf3 ph\xf9 hợp cho người mới kh\xf4ng?","a":["C\xf3, miễn bạn đi vững v\xe0 kh\xf4ng qu\xe1 sợ độ cao. Kh\xf4ng c\xf3 đoạn leo kỹ thuật, chỉ l\xe0 những đoạn l\xean xuống đều."]},{"q":"Nếu ph\xe0 bị hủy th\xec sao?","a":["Chuyển sang đi xe bu\xfdt qua Amalfi hoặc đi thẳng l\xean Nocelle. Lu\xf4n giữ một v\xe9 xe bu\xfdt dự ph\xf2ng để linh hoạt khi thời tiết xấu."]}],"galleryTitle":"Bộ ảnh","gallery":{"items":[{"alt":"Khung cảnh dọc Con Đường của C\xe1c Vị Thần","caption":"Dọc đường m\xf2n"},{"alt":"Khung cảnh l\xe0ng Nocelle","caption":"Nocelle nh\xecn từ tr\xean cao"}]},"toc":{"onThisPage":"Nội dung ch\xednh","essentials":"Vật dụng cần c\xf3","costs":"Chi ph\xed tham khảo","tips":"Thời điểm l\xfd tưởng","faqs":"C\xe2u hỏi thường gặp","gallery":"Bộ ảnh"}}')}}]);