"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[417604],{417604:e=>{e.exports=JSON.parse('{"seo":{"title":"Exploring Praiano: Laid‑Back Neighbor","description":"Why visit Praiano: sunsets, Marina di Praia, San Gennaro."},"linkLabel":"Exploring Praiano: Laid‑Back Neighbor","intro":["Praiano is Positano\'s laid-back neighbour with sunset views, manageable prices, and quiet beaches."],"sections":[{"id":"why","title":"Why visit","body":["Enjoy panoramic sunsets from Piazza San Gennaro without elbow-to-elbow crowds.","Accommodation runs €20–€40 cheaper per night than Positano while keeping bus access."]},{"id":"beaches","title":"Beaches & swimming","body":["Marina di Praia offers a dramatic cove with beach clubs and kayak rentals; arrive before 11:00 for front-row loungers.","Gavitella Beach faces west for golden-hour swims; count 400 steps down, so plan snacks and water."]},{"id":"dining","title":"Food & nightlife","body":["Book Il Pirata or Kasai for seafood dinners with terrace views; reserve sunset tables a few days ahead in summer.","Praiano\'s beach bars host live music on weekends—check posters near the SITA stop for set times."]}],"tips":["Use the local internal bus to skip the steepest sections between upper and lower Praiano.","Carry a headlamp for late-night stair climbs back to accommodation."],"faqs":[{"q":"How long is the bus ride from Positano?","a":["Around 15 minutes on the SITA bus, longer at peak times. Services run roughly every 30 minutes in summer."]},{"q":"Can I stay in Praiano and visit Positano daily?","a":["Yes. Many travellers commute by bus or scooter, enjoying quieter nights while keeping Positano day access."]}]}')}}]);