"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[423882],{423882:e=>{e.exports=JSON.parse('{"seo":{"title":"Лучшие места для заката в Позитано","description":"Главные точки для встречи заката и как до них добраться пешком или на автобусе."},"linkLabel":"Лучшие места для заката в Позитано","intro":["Закаты в Позитано впечатляют — важно выбрать правильную точку и вовремя занять место.","Пользуйтесь этими проверенными ракурсами и временем прибытия, чтобы избежать толп."],"itemList":[{"name":"Терраса Chiesa Nuova","note":"Простой доступ рядом с остановкой SITA и широкий обзор бухты."},{"name":"Смотровая Via Positanesi d’America","note":"Балкон над Форнилло; приходите за 30 минут до заката ради тёплого света."},{"name":"Ночелле (старт Тропы богов)","note":"Вид на острова Ли Галли — возьмите тёплый слой на обратную дорогу."}],"sections":[{"id":"classics","title":"Классические точки","body":["Терраса Chiesa Nuova открывает панораму бухты прямо у остановки SITA.","Via Positanesi d’America — балкон над Форнилло; приходите за полчаса до заката ради мягкого света."]},{"id":"alternatives","title":"Более тихие альтернативы","body":["Ночелле у начала Тропы богов смотрит на острова Ли Галли; возьмите тёплую одежду на обратный путь.","Пляж Форнилло вне пиковых недель остаётся спокойным; солнце уходит за скалу, поэтому занимайте место заранее."]},{"id":"sunset","title":"Как выбрать время","body":["Приходите минимум за 20 минут до начала \xabзолотого часа\xbb, чтобы спокойно выбрать место и настроить технику.","Летние закаты около 20:30 — проверьте расписание автобусов, чтобы не застрять после темноты."]},{"id":"etiquette","title":"Этикет","body":["Не перекрывайте лестницы и частные входы во время съёмки — сделайте кадр и отойдите.","Забирайте мусор с собой и переходите в бесшумный режим во время вечерних концертов и мероприятий."]}],"tips":["Приходите за 20–30 минут до заката, чтобы спокойно занять место и поставить штатив.","Лёгкий тревел-штатив или зажим пригодятся для съёмок в \xabсиний час\xbb, когда света мало."],"faqs":[{"q":"Нужна ли бронь?","a":["Бары и террасы на крышах летом часто требуют бронирования; открытые смотровые работают по принципу \xabкто раньше занял\xbb."]},{"q":"Хватит ли освещения на обратном пути?","a":["Уличное освещение разное — возьмите фонарик в телефоне или маленький налобный свет для позднего спуска."]}],"gallery":{"title":"Фотогалерея","items":[{"caption":"Терраса Chiesa Nuova","alt":"Закат с террасы Chiesa Nuova"},{"caption":"Nocelle","alt":"Закат из Ночелле"}]}}')}}]);