"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[42392],{42392:h=>{h.exports=JSON.parse('{"seo":{"title":"Lịch ph\xe0 đến Positano","description":"Ph\xe0 chạy khi n\xe0o, nơi kiểm tra lịch thời gian thực, m\xf9a vụ, nh\xe0 khai th\xe1c v\xe0 mẹo l\xe0m thủ tục l\xean ph\xe0."},"linkLabel":"Lịch ph\xe0 đến Positano","intro":["Lịch ph\xe0 đi/đến Positano: kiểm tra chuyến trực tiếp ở đ\xe2u, ph\xe0 chạy m\xf9a n\xe0o v\xe0 c\xe1ch chuẩn bị phương \xe1n dự ph\xf2ng."],"sections":[{"id":"seasonality","title":"Thời gian ph\xe0 hoạt động","body":["M\xf9a ch\xednh k\xe9o d\xe0i khoảng th\xe1ng 4 đến th\xe1ng 10 với tần suất d\xe0y nhất từ giữa th\xe1ng 5 tới th\xe1ng 9; m\xf9a đ\xf4ng hiếm chuyến v\xe0 phụ thuộc thời tiết.","C\xe1c th\xe1ng giao m\xf9a thường cắt bớt chuyến đầu v\xe0 cuối ng\xe0y — h\xe3y x\xe1c nhận lịch v\xe0o tối h\xf4m trước."]},{"id":"live-updates","title":"Theo d\xf5i cập nhật trực tiếp","body":["Sử dụng website/ứng dụng của h\xe3ng (Travelmar, NLG, Alilauro, Positano Jet) c\xf9ng bảng điện tử ở bến để theo d\xf5i trễ chuyến v\xe0 đổi cổng.","Chụp lại lịch v\xe0 bật th\xf4ng b\xe1o email/SMS để nhận tin cảnh b\xe1o thời tiết ngay cả khi kh\xf4ng c\xf3 SIM \xdd."]},{"id":"tickets","title":"V\xe9 & thời gian đệm","body":["M\xf9a cao điểm v\xe9 c\xf3 thể hết trước nhiều giờ — h\xe3y mua online hoặc từ h\xf4m trước v\xe0 tới bến sớm 30 ph\xfat, chuẩn bị CMND/hộ chiếu c\xf9ng h\xe0nh l\xfd để d\xe1n thẻ.","Giữ thời gian đệm trước chuyến cuối trong ng\xe0y để c\xf3 thể chuyển sang xe bu\xfdt hoặc taxi nếu biển bất ngờ động."]}],"tips":["Theo d\xf5i dự b\xe1o h\xe0ng hải (gi\xf3 tr\xean 20 hải l\xfd/giờ hoặc s\xf3ng vượt 1,5 m thường khiến ph\xe0 hủy chuyến).","Nếu phải đổi bến, kiểm tra giờ xe bu\xfdt địa phương trước để h\xe0nh tr\xecnh kh\xf4ng bị \xe1p lực."],"faqs":[{"q":"Khi n\xe0o c\xf3 lịch ph\xe0 m\xf9a h\xe8?","a":["Đa số h\xe3ng c\xf4ng bố lịch m\xf9a cao điểm v\xe0o khoảng th\xe1ng 2–3; lịch m\xf9a thu thường được cập nhật cuối th\xe1ng 8."]},{"q":"L\xe0m sao x\xe1c nhận ph\xe0 bị hủy trong ng\xe0y?","a":["Tải lại website/k\xeanh mạng x\xe3 hội của h\xe3ng v\xe0 xem th\xf4ng b\xe1o d\xe1n tại bến; nh\xe2n vi\xean cũng ph\xe1t loa khi c\xf3 thay đổi.","Lu\xf4n mang theo lịch xe bu\xfdt SITA hoặc số taxi để chuyển hướng ngay nếu cần."]},{"q":"T\xf4i c\xf3 thể mang h\xe0nh l\xfd lớn kh\xf4ng?","a":["C\xf3, nhưng h\xe0nh l\xfd qu\xe1 khổ c\xf3 thể thu th\xeam phụ ph\xed; h\xe3y đến sớm để nh\xe2n vi\xean gắn thẻ, sắp xếp an to\xe0n v\xe0 bạn vẫn giữ đồ qu\xfd b\xean m\xecnh."]}]}')}}]);