"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[428421],{428421:a=>{a.exports=JSON.parse('{"seo":{"title":"Da Salerno a Positano: come arrivare","description":"Traghetti stagionali, bus SITA e transfer privati per collegare Salerno a Positano in ogni periodo dell\'anno."},"linkLabel":"Da Salerno a Positano: come arrivare","intro":["Salerno → Positano: il traghetto \xe8 la scelta pi\xf9 comoda in stagione (soprattutto con bagagli); l’autobus via Amalfi funziona tutto l’anno come alternativa economica."],"sections":[{"id":"ferry","title":"Traghetto (consigliato con bagagli)","body":["Imbarcati al Molo Manfredi/Concordia a seconda dell’operatore e prevedi circa un’ora di navigazione, condizioni del mare permettendo.","Controlla meteo e orari aggiornati: nei weekend i posti possono esaurirsi — arriva con anticipo."]},{"id":"bus","title":"Autobus via Amalfi (tutto l’anno)","body":["Treno fino ad Amalfi (SITA) → cambio con la linea per Positano; pi\xf9 lungo ma disponibile fuori stagione.","Tieni contanti spicci, convalida il biglietto e prevedi margine al cambio di Amalfi mentre controlli il tabellone delle partenze."]}],"faqs":[{"q":"Il traghetto \xe8 affidabile in primavera?","a":["S\xec, ma le mareggiate di inizio stagione possono cancellare le corse. Controlla sempre il giorno stesso."]},{"q":"Dove lascio i bagagli?","a":["Usa i portabagagli a bordo dei traghetti; tieni con te gli oggetti di valore."]}]}')}}]);