"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[451217],{451217:n=>{n.exports=JSON.parse('{"seo":{"title":"Lộ tr\xecnh quay về: B\xe3i biển Fornillo → Hostel Brikette","description":"Đi dọc lối đi ven biển tới Spiaggia Grande, gh\xe9 c\xe1c cửa h\xe0ng rồi l\xean xe bu\xfdt interno để trở lại hostel."},"linkLabel":"Lộ tr\xecnh quay về: B\xe3i biển Fornillo → Hostel Brikette","intro":["Quay trở lại Hostel Brikette từ B\xe3i biển Fornillo l\xe0 c\xe1ch dễ d\xe0ng nhất nếu bạn đi theo bờ s\xf4ng đến Spiaggia Grande v\xe0 đi xe bu\xfdt nội địa từ trung t\xe2m thị trấn. Tuyến đường n\xe0y ho\xe1n đổi hầu hết c\xe1c cầu thang l\xean dốc để c\xf3 lối đi dạo bằng phẳng v\xe0 leo l\xean một đoạn ngắn b\xean cạnh nh\xe0 trọ."],"sections":[{"id":"plan-your-return","title":"Lập kế hoạch trở lại của bạn","body":["D\xe0nh th\xeam thời gian để dừng chụp ảnh hoặc dạo qua c\xe1c cửa h\xe0ng xung quanh Spiaggia Grande trước khi l\xean xe bu\xfdt.","Con đường trở về n\xe0y d\xe0i hơn việc leo l\xean cầu thang tắt nhưng m\xe1t mẻ hơn v\xe0o buổi chiều v\xe0 mang lại cho bạn cảm gi\xe1c nhẹ nh\xe0ng hơn sau một ng\xe0y đi biển."]},{"id":"route-steps","title":"Lộ tr\xecnh từ B\xe3i biển Fornillo tới Hostel Brikette","body":["1. Đi bộ về ph\xeda b\xean tr\xe1i của B\xe3i biển Fornillo (b\xean tr\xe1i khi bạn quay mặt ra biển).","2. Đi theo con đường ven biển về ph\xeda Spiaggia Grande cho đến khi bạn đến b\xe3i biển ch\xednh v\xe0 khu vực cảng.","3. Đi qua cổng bến cảng, rời cầu cảng v\xe0 tiếp tục đi thẳng dọc theo lối đi dạo ven s\xf4ng.","4. Đi theo con đường ngay ph\xeda trước b\xe3i biển v\xe0 tiếp tục v\xe0o đất liền.","5. Rẽ phải ở con hẻm đầu ti\xean giữa c\xe1c cửa h\xe0ng.","6. Leo l\xean cầu thang dẫn l\xean Chiesa di Santa Maria Assunta.","7. Ở đầu l\xe0n đường, rẽ tr\xe1i v\xe0 đi l\xean dốc.","8. Khi l\xe0n đường gặp đường một chiều tại ng\xe3 ba, bạn đ\xe3 đến trạm xe bu\xfdt nội địa đi Chiesa Nuova. Đi l\xean một điểm dừng để đến Hostel Brikette."]},{"id":"bus-connection","title":"Bắt xe bu\xfdt nội địa","body":["Từ trạm dừng Chiesa Nuova, xe bu\xfdt nội địa Positano m\xe0u cam sẽ đưa bạn đi bộ một đoạn ngắn đến Hostel Brikette—ho\xe0n hảo nếu bạn muốn tr\xe1nh phải leo l\xean to\xe0n bộ cầu thang để quay về.","V\xe9 được b\xe1n tại c\xe1c ki-ốt tabacchi/sạp b\xe1o khắp thị trấn, v\xec vậy h\xe3y mua v\xe9 trước khi bạn xuống hoặc kiểm tra với lễ t\xe2n xem c\xf2n chỗ hay kh\xf4ng."]}],"tips":["Mua v\xe9 xe bu\xfdt nội địa tại bất kỳ quầy b\xe1n b\xe1o/b\xe1o n\xe0o trước khi bạn đi biển.","H\xe3y d\xe0nh lối tắt nặng nề cho buổi s\xe1ng; chuyến trở về ngắm cảnh n\xe0y y\xean b\xecnh hơn sau một chuyến bơi d\xe0i.","Li\xean kết n\xf3 với hướng dẫn B\xe3i biển Fornillo của ch\xfang t\xf4i để biết chi tiết về c\xe2u lạc bộ b\xe3i biển v\xe0 c\xe1c điểm y\xean tĩnh.","Xem lại tổng quan về c\xe1c b\xe3i biển Positano để l\xean kế hoạch bơi lội ở những nơi kh\xe1c trong thời gian lưu tr\xfa của bạn."],"faqsTitle":"C\xe2u hỏi thường gặp về lộ tr\xecnh","faqs":[{"q":"Thời gian ho\xe0n trả mất bao l\xe2u?","a":["Đi bộ trở lại qua Spiaggia Grande sẽ mất th\xeam khoảng 10 ph\xfat so với đi bộ bằng cầu thang bộ, nhưng bạn tr\xe1nh được phần lớn việc phải leo dốc trước khi l\xean xe bu\xfdt."]},{"q":"T\xf4i c\xf3 thể bắt xe bu\xfdt ở đ\xe2u?","a":["Đi xe bu\xfdt nội địa Positano m\xe0u cam từ trạm dừng Chiesa Nuova ở đầu l\xe0n đường. N\xf3 đưa bạn đến gần k\xfd t\xfac x\xe1 v\xe0 v\xe9 đến từ c\xe1c ki-ốt tabacchi/sạp b\xe1o."]}]}')}}]);