"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[453372],{453372:h=>{h.exports=JSON.parse('{"meta":{"title":"Nội quy | Hostel Brikette","description":"Nội quy Hostel Brikette về ứng xử, kh\xe1ch thăm, giờ y\xean tĩnh, an ninh, thẻ kh\xf3a v\xe0 quy tr\xecnh tại chỗ."},"headings":{"pageTitle":"Hostel Brikette — Nội quy","pageSubtitle":"Nội quy"},"toc":{"title":"Mục lục","aria":"Mục lục"},"footer":{"end":"Hết t\xe0i liệu.","backToTop":"L\xean đầu trang"},"sections":{"s1":{"title":"1. Ứng xử t\xf4n trọng v\xe0 an to\xe0n","body":["\xc1p dụng cho: mọi kh\xe1ch v\xe0 kh\xe1ch thăm tại cơ sở.","Nội quy l\xe0 một phần của hợp đồng lưu tr\xfa. Vi phạm c\xf3 thể dẫn đến y\xeau cầu rời khỏi cơ sở v\xe0/hoặc chấm dứt do vi phạm theo Điều khoản đặt chỗ.","1.1 T\xf4n trọng nh\xe2n vi\xean v\xe0 kh\xe1ch kh\xe1c. Cấm quấy rối, đe dọa, bạo lực hoặc ph\xe2n biệt đối xử.","1.2 Tu\xe2n theo hướng dẫn của nh\xe2n vi\xean về an ninh v\xe0 vận h\xe0nh.","1.3 Kh\xf4ng l\xe0m hư hỏng t\xe0i sản hoặc can thiệp thiết bị an to\xe0n."]},"s2":{"title":"2. Giờ y\xean tĩnh","body":["2.1 Giờ y\xean tĩnh: 23:45–08:00. Trong thời gian n\xe0y, hạn chế tiếng ồn, kh\xf4ng mở nhạc lớn, gọi điện ngo\xe0i khu ngủ.","2.2 Nếu g\xe2y ồn lặp lại, ch\xfang t\xf4i c\xf3 thể y\xeau cầu bạn rời đi theo Điều khoản đặt chỗ."]},"s3":{"title":"3. Kh\xe1ch thăm (chỉ khu vực chung)","body":["3.1 Kh\xe1ch thăm chỉ được ph\xe9p theo quyết định của ch\xfang t\xf4i v\xe0 chỉ ở khu vực chung; kh\xf4ng v\xe0o ph\xf2ng/k\xfd t\xfac.","3.2 Thời gian kh\xe1ch thăm (nếu cho ph\xe9p): 10:30–23:00, t\xf9y vận h\xe0nh.","3.3 Kh\xe1ch thăm phải xuất tr\xecnh giấy tờ gốc v\xe0 tu\xe2n thủ biện ph\xe1p an ninh (v\xf2ng tay nếu c\xf3).","3.4 Kh\xf4ng đưa kh\xe1ch thăm v\xe0o ph\xf2ng; đ\xe2y l\xe0 vi phạm nghi\xeam trọng."]},"s4":{"title":"4. Người chưa đăng k\xfd","body":["4.1 Chỉ kh\xe1ch đ\xe3 đăng k\xfd mới được qua đ\xeam.","4.2 Nếu ph\xe1t hiện người chưa đăng k\xfd trong ph\xf2ng hoặc qua đ\xeam, ch\xfang t\xf4i c\xf3 thể y\xeau cầu đăng k\xfd ngay hoặc rời đi v\xe0 \xe1p dụng ph\xed theo Điều khoản đặt chỗ."]},"s5":{"title":"5. Nhận ph\xf2ng v\xe0 đến sớm","body":["5.1 Khung giờ nhận ph\xf2ng: 15:00–22:00.","5.2 Đến trước 10:30 c\xf3 thể kh\xf4ng v\xe0o được.","5.3 Trong khoảng mở cửa đến giờ nhận ph\xf2ng, c\xf3 thể cho ph\xe9p chờ, v\xe0o khu vực chung v\xe0/hoặc gửi h\xe0nh l\xfd."]},"s6":{"title":"6. Y\xeau cầu ID","body":["6.1 Kh\xe1ch v\xe0 kh\xe1ch thăm phải xuất tr\xecnh ID gốc khi được y\xeau cầu.","6.2 Kh\xf4ng xuất tr\xecnh ID c\xf3 thể bị y\xeau cầu rời đi."]},"s7":{"title":"7. Thẻ kh\xf3a/v\xf2ng tay","body":["7.1 Thẻ kh\xf3a/m\xe3 v\xe0o cửa l\xe0 c\xe1 nh\xe2n, kh\xf4ng được chia sẻ.","7.2 Nếu d\xf9ng v\xf2ng tay an ninh, phải đeo v\xe0 xuất tr\xecnh khi y\xeau cầu.","7.3 Mất/hỏng thẻ hoặc v\xf2ng tay phải b\xe1o ngay; c\xf3 thể t\xednh ph\xed thay thế."]},"s8":{"title":"8. Chăn ga v\xe0 đặt cọc","body":["8.1 Chăn ga được cung cấp cho mỗi kh\xe1ch.","8.2 Nếu c\xf3 đặt cọc (v\xed dụ €10), sẽ ho\xe0n theo quy tr\xecnh trả ph\xf2ng khi trả đủ vật dụng."]},"s9":{"title":"9. Quy tắc ph\xf2ng k\xfd t\xfac","body":["9.1 Giường chọn theo thứ tự đến.","9.2 Chuyển giường phải c\xf3 ph\xe9p của nh\xe2n vi\xean.","9.3 Giữ khu vực ngủ gọn g\xe0ng."]},"s10":{"title":"10. Ch\xednh s\xe1ch rượu","body":["10.1 C\xf3 thể cấm rượu mua ngo\xe0i mang v\xe0o; ph\xe1t hiện c\xf3 thể y\xeau cầu mang ra hoặc giữ đến khi trả ph\xf2ng.","10.2 Vi phạm nghi\xeam trọng c\xf3 thể dẫn đến chấm dứt lưu tr\xfa."]},"s11":{"title":"11. Vật phẩm tr\xe1i ph\xe9p/nguy hiểm","body":["11.1 Cấm ma t\xfay, vũ kh\xed v\xe0 vật phẩm nguy hiểm.","11.2 C\xf3 thể tịch thu v\xe0/hoặc b\xe1o cơ quan chức năng theo luật."]},"s12":{"title":"12. Rệp","body":["12.1 Nghi ngờ rệp phải b\xe1o ngay; kh\xf4ng tự \xfd đổi ph\xf2ng hoặc di chuyển chăn ga.","12.2 C\xf3 thể y\xeau cầu thực hiện c\xe1c biện ph\xe1p c\xe1ch ly."]},"s13":{"title":"13. Trả ph\xf2ng v\xe0 đồ đạc","body":["13.1 Giờ trả ph\xf2ng: 07:30–10:30.","13.2 Trả ph\xf2ng muộn đến 12:00 nếu được x\xe1c nhận, ph\xed EUR 40.","13.3 Kh\xf4ng trả ph\xf2ng đ\xfang giờ c\xf3 thể dẫn đến lưu giữ đồ v\xe0 thu ph\xed theo Điều khoản đặt chỗ.","13.4 Đồ bỏ qu\xean được giữ 30 ng\xe0y rồi xử l\xfd theo luật."]},"s14":{"title":"14. Thực thi","body":["14.1 Vi phạm nhẹ c\xf3 thể bị cảnh c\xe1o.","14.2 Vi phạm nghi\xeam trọng/lặp lại c\xf3 thể bị y\xeau cầu rời đi.","14.3 C\xe1c khoản ph\xed xử l\xfd theo Điều khoản đặt chỗ."]}}}')}}]);