"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[460198],{460198:n=>{n.exports=JSON.parse('{"seo":{"title":"Cẩm nang b\xe3i biển Fiordo di Furore","description":"C\xe1ch đi xe bu\xfdt từ Positano tới Fiordo di Furore, n\xean mang g\xec v\xe0 mong đợi điều g\xec tại eo vịnh v\xe1ch đ\xe1."},"linkLabel":"Cẩm nang b\xe3i biển Fiordo di Furore","intro":["Fiordo di Furore l\xe0 hẻm n\xfai biểu tượng của Amalfi Coast — lối v\xe0o hẹp dưới c\xe2y cầu v\xf2m, nước xanh ngọc v\xe0 v\xe1ch đ\xe1 dựng đứng. Đ\xe2y l\xe0 eo biển nhỏ, gần như hoang sơ, kh\xf4ng c\xf3 beach club hay nh\xe0 h\xe0ng, v\xec vậy h\xe3y chuẩn bị cho một lần tắm biển, v\xe0i bức ảnh v\xe0 m\xe0n nhảy v\xe1ch kịch t\xednh.","H\xe3y d\xf9ng hướng dẫn n\xe0y để di chuyển bằng xe bu\xfdt, hiểu r\xf5 sự hạn chế về dịch vụ v\xe0 l\xean kế hoạch quay về để kh\xf4ng bị kẹt khi chuyến SITA cuối c\xf9ng k\xedn chỗ."],"toc":[{"href":"#getting-there","label":"Di chuyển"},{"href":"#returning","label":"Quay về"},{"href":"#on-site","label":"C\xf3 g\xec tại chỗ"}],"sections":[{"id":"getting-there","title":"Tự tin đi xe bu\xfdt SITA","body":["Từ Hostel Brikette, đi bộ xuống Via Pasitea tới điểm dừng Sponda v\xe0 l\xean xe bu\xfdt SITA hướng Amalfi. N\xf3i với t\xe0i xế bạn cần xuống \\"Fiordo di Furore\\" để họ nhớ tấp v\xe0o — điểm dừng nằm giữa Praiano v\xe0 Furore n\xean rất dễ bỏ qua v\xe0o ng\xe0y đ\xf4ng.","Sau khoảng 25 ph\xfat, xuống tại điểm dừng ngay trước c\xe2y cầu. Theo c\xe1c bậc thang khắc v\xe0o v\xe1ch đ\xe1 v\xe0i ph\xfat l\xe0 tới b\xe3i sỏi nhỏ nằm dưới v\xf2m cầu."]},{"id":"returning","title":"L\xean kế hoạch quay về trước khi xuống nước","body":["Dưới eo vịnh kh\xf4ng c\xf3 quầy v\xe9 hay m\xe1y b\xe1n v\xe9. H\xe3y mua v\xe9 khứ hồi ở Positano (tabacchi gần Chiesa Nuova hoặc quầy b\xe1o Sponda) v\xe0 đ\xf3ng dấu khi l\xean xe.","M\xf9a h\xe8 xe bu\xfdt c\xf3 thể đầy kh\xe1ch ngay từ khi rời Amalfi. H\xe3y chọn chuyến sớm hơn bạn nghĩ, v\xe0 b\xe1o với những người chờ tại điểm dừng rằng bạn đang đợi để t\xe0i xế ch\xfa \xfd. Nếu hai chuyến li\xean tiếp kh\xf4ng dừng, h\xe3y c\xe2n nhắc chung taxi từ đường ph\xeda tr\xean."]},{"id":"on-site","title":"Chờ đợi vẻ đẹp nguy\xean sơ, kh\xf4ng phải dịch vụ","body":["Fiordo di Furore kh\xf4ng c\xf3 beach club, kh\xf4ng nh\xe0 vệ sinh v\xe0 chỉ thỉnh thoảng mới c\xf3 xe đẩy đồ ăn vặt v\xe0o cao điểm. H\xe3y mang nước, đồ ăn nhẹ v\xe0 thứ g\xec đ\xf3 ngồi cho đỡ đau — sỏi to v\xe0 diện t\xedch hạn chế.","Người địa phương thường nhảy từ tảng đ\xe1 cạnh cầu, nhưng lu\xf4n kiểm tra độ s\xe2u trước v\xe0 chỉ nhảy khi đ\xe3 thấy người bản địa dưới nước. T\xf4n trọng người đang tắm ph\xeda dưới v\xe0 ch\xfa \xfd thủy triều; eo vịnh r\xe2m m\xe1t từ cuối chiều."]}],"essentialsTitle":"Mong đợi điều g\xec","essentials":["Điểm dừng xe bu\xfdt SITA giữa Praiano v\xe0 Furore — nhờ t\xe0i xế th\xf4ng b\xe1o khi tới fiord.","Cầu thang dốc xuống b\xe3i sỏi nhỏ dưới ch\xe2n cầu.","Kh\xf4ng c\xf3 dịch vụ tại chỗ: mang nước, đồ ăn, kem chống nắng v\xe0 tiền mặt ph\xf2ng trường hợp phải đi taxi."],"tipsTitle":"Mẹo nhanh","tips":["Xem %LINK:positanoBeaches|tổng quan c\xe1c b\xe3i biển% để kết hợp Fiordo di Furore với điểm tắm kh\xe1c trong c\xf9ng ng\xe0y.","Đến sớm nếu muốn chụp ảnh dưới cầu m\xe0 kh\xf4ng bị chen ch\xfac.","Mang kem chống nắng th\xe2n thiện với biển v\xe0 gi\xe0y đi nước — đ\xe1 trơn khi ướt."],"warningsTitle":"Lưu \xfd","warnings":["Mua v\xe9 xe bu\xfdt khứ hồi tại Positano; ở fiord kh\xf4ng c\xf3 nơi b\xe1n hay đ\xf3ng dấu v\xe9.","Xe bu\xfdt đ\xf4i khi bỏ qua điểm dừng khi đ\xe3 đầy trong m\xf9a cao điểm — h\xe3y chừa th\xeam thời gian để về nh\xe0.","B\xe3i nhỏ v\xe0 b\xf3ng r\xe2m tới sớm v\xe0o buổi chiều."],"faqsTitle":"C\xe2u hỏi thường gặp về Fiordo di Furore","faqs":[]}')}}]);