"use strict";(self.webpackChunk_N_E=self.webpackChunk_N_E||[]).push([[466550],{466550:t=>{t.exports=JSON.parse('{"seo":{"title":"تجارب دروس الطبخ على ساحل أمالفي","description":"أماكن حجز الدروس، ما تتوقع تعلمه، ونصائح للأشخاص النباتيين والذين لديهم حساسية."},"linkLabel":"تجارب دروس الطبخ على ساحل أمالفي","intro":["صفوف الطهي العملية على ساحل أمالفي تجمع بين ليمون سفوزاتو والمعكرونة الطازجة ووصفات العائلة في مطابخ صغيرة ذات إطلالات.","استخدم هذا الدليل لتختار صفًا يناسب ميزانيتك وجدولك ولتؤكد التفاصيل قبل دفع أي عربون."],"sections":[{"id":"flow","title":"كيف تسير الحصة النموذجية","body":["يستقبلك المضيفون بقهوة أو بروسيكو قبل استعراض المكونات: قشر ليمون طازج، خضار موسمية، وعجين المعكرونة الذي ستشكّله أنت.","توقع تحضير طبقين أو ثلاثة — غالبًا معكرونة طازجة، صلصة، وحلوى بنكهة الليمون — ثم الجلوس لتناول الطعام معًا؛ تدوم الجلسات من ساعتين ونصف إلى أربع ساعات بما في ذلك الوجبة المشتركة."]},{"id":"choose","title":"اختيار الصف المناسب","body":["تحقق من حجم المجموعة وما إذا كان المطبخ في بوسيتانو، أو في مزرعة على التلال، أو في بلدة مجاورة؛ قد تكون التنقلات مشمولة أو برسوم منفصلة.","وضّح ما يشمله السعر: المكونات، تذوق الخمور، الوصفات المطبوعة، وهل تحتفظ بالمآزر أو ما تبقى من الطعام."]},{"id":"logistics","title":"اللوجستيات والتوقيت","body":["تُحجز صفوف موسم الذروة قبل أسابيع؛ احجز مقعدك قبل الوصول، خاصة للجلسات المسائية بعد الرحلات اليومية.","إذا كان الموقع خارج بوسيتانو، أكد نقاط الالتقاء أو ضع في الحسبان تكلفة سيارة أجرة؛ الأزقة الحادة تعني أنك بحاجة إلى أحذية مريحة حتى مع وجود شاتل."]},{"id":"dietary","title":"ملاحظات غذائية وإتيكيت","body":["أخبر المضيفين بالحساسية أو التفضيلات النباتية/النباتية الصرفة أو عدم تحمل الغلوتين عند الحجز؛ يستطيع معظمهم تعديل القوائم إذا عرفوا مسبقًا.","ارتدِ أحذية مغلقة، اربط الشعر الطويل، وتجنب العطر القوي — المطابخ صغيرة وستنحني فوق مكونات طازجة."]}],"faqs":[{"q":"هل تناسب دروس الطهي المبتدئين؟","a":["نعم. يشرح المدرسون كل خطوة، ويصححون التقنية أثناء العمل، ويقسمون المهام بحيث يحصل الجميع على دور من دون إحساس بالاستعجال."]},{"q":"هل أحتاج إلى التحدث بالإيطالية؟","a":["لا. معظم الصفوف تُقدَّم بالإنجليزية أو بصيغة ثنائية اللغة، والعروض البصرية تجعل المتابعة سهلة."]},{"q":"ماذا يجب أن أحضر؟","a":["أحضر دفترًا صغيرًا لكتابة الملاحظات إن رغبت، واحرص على شحن هاتفك للصور، واحمل نقودًا لسداد المتبقي أو المواصلات."]}],"fallback":{"intro":["معكرونة وصلصات وحلويات بالليمون مع مجموعات صغيرة ومطابخ بإطلالة في موسم الذروة."],"toc":[{"href":"#what","label":"ما الذي تتوقعه"},{"href":"#choose","label":"كيفية الاختيار"},{"href":"#tips","label":"نصائح"}],"sections":[{"id":"what","title":"ما الذي تتوقعه","body":["عمل يدوي على المعكرونة والصلصات والحلويات بالليمون؛ مجموعات صغيرة ومطابخ ذات مناظر خلابة في الموسم."]},{"id":"choose","title":"كيفية الاختيار","body":["تحقق من حجم الصف ومدته وما يتضمنه وخيارات النقل."]},{"id":"tips","title":"نصائح","body":["احجز مبكرًا في أشهر الذروة؛ وأبلغ المضيفين باحتياجاتك الغذائية."]}]}}')}}]);